// =========== METHOD 3: Professional Web Audio API ===========
// Most control and features - for advanced games

class GameAudioEngine {
    constructor() {
        // Create audio context
        this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
        this.sounds = new Map();
        this.music = null;
        this.masterGain = this.audioContext.createGain();
        this.masterGain.connect(this.audioContext.destination);
        this.masterGain.gain.value = 1.0;  // Full volume
        
        // Mute state
        this.isMuted = false;
        
        // Check if audio is suspended (browser policy)
        if (this.audioContext.state === 'suspended') {
            console.log("Audio needs user interaction to start");
        }
    }
    
    // Load sound from URL
    async loadSound(name, url) {
        try {
            const response = await fetch(url);
            const arrayBuffer = await response.arrayBuffer();
            const audioBuffer = await this.audioContext.decodeAudioData(arrayBuffer);
            
            this.sounds.set(name, audioBuffer);
            console.log(\`Sound loaded: \${name}\`);
        } catch (error) {
            console.error(\`Failed to load sound \${name}:\`, error);
        }
    }
    
    // Play a sound effect
    playSound(name, options = {}) {
        if (this.isMuted || !this.sounds.has(name)) return;
        
        const audioBuffer = this.sounds.get(name);
        const source = this.audioContext.createBufferSource();
        const gainNode = this.audioContext.createGain();
        
        // Configure source
        source.buffer = audioBuffer;
        
        // Configure volume
        gainNode.gain.value = options.volume || 1.0;
        
        // Connect nodes: source â†’ gain â†’ master â†’ speakers
        source.connect(gainNode);
        gainNode.connect(this.masterGain);
        
        // Optional fade out
        if (options.fadeOut) {
            const fadeTime = options.fadeOut;
            gainNode.gain.exponentialRampToValueAtTime(
                0.001,  // Can't go to 0 with exponential
                this.audioContext.currentTime + fadeTime
            );
        }
        
        // Start playing
        source.start();
        
        // Clean up when done
        source.onended = () => {
            source.disconnect();
            gainNode.disconnect();
        };
        
        return { source, gainNode };
    }
    
    // Play background music with looping
    playMusic(name, options = {}) {
        if (!this.sounds.has(name)) return;
        
        // Stop current music if playing
        if (this.music) {
            this.music.source.stop();
        }
        
        const audioBuffer = this.sounds.get(name);
        const source = this.audioContext.createBufferSource();
        const gainNode = this.audioContext.createGain();
        
        // Configure music
        source.buffer = audioBuffer;
        source.loop = true;
        
        // Volume with fade in
        gainNode.gain.value = 0;
        gainNode.connect(this.masterGain);
        
        // Fade in
        gainNode.gain.linearRampToValueAtTime(
            options.volume || 0.5,
            this.audioContext.currentTime + (options.fadeIn || 1)
        );
        
        source.connect(gainNode);
        source.start();
        
        // Store for control
        this.music = { source, gainNode, name };
    }
    
    // Stop music with optional fade out
    stopMusic(fadeOut = 1.0) {
        if (!this.music) return;
        
        const { gainNode, source } = this.music;
        
        // Fade out
        gainNode.gain.exponentialRampToValueAtTime(
            0.001,
            this.audioContext.currentTime + fadeOut
        );
        
        // Stop after fade
        setTimeout(() => {
            source.stop();
            source.disconnect();
            gainNode.disconnect();
            this.music = null;
        }, fadeOut * 1000);
    }
    
    // Create sound effects with code (no files needed!)
    createBeep(frequency = 440, duration = 0.5) {
        const oscillator = this.audioContext.createOscillator();
        const gainNode = this.audioContext.createGain();
        
        oscillator.frequency.value = frequency;
        oscillator.type = 'sine';  // 'sine', 'square', 'sawtooth', 'triangle'
        
        gainNode.gain.value = 0.3;
        
        // Envelope: fade in and out
        gainNode.gain.setValueAtTime(0, this.audioContext.currentTime);
        gainNode.gain.linearRampToValueAtTime(0.3, this.audioContext.currentTime + 0.01);
        gainNode.gain.exponentialRampToValueAtTime(0.001, this.audioContext.currentTime + duration);
        
        oscillator.connect(gainNode);
        gainNode.connect(this.masterGain);
        
        oscillator.start();
        oscillator.stop(this.audioContext.currentTime + duration);
    }
    
    // Set master volume
    setMasterVolume(volume) {
        this.masterGain.gain.value = volume;
    }
    
    // Toggle mute
    toggleMute() {
        this.isMuted = !this.isMuted;
        this.masterGain.gain.value = this.isMuted ? 0 : 1.0;
    }
}

// Initialize and use:
