// =========== COMPLETE GAME SOUND MANAGER ===========
class GameSoundManager {
    constructor() {
        this.audio = new GameAudioEngine();
        this.soundBank = {};
        this.musicVolume = 0.6;
        this.sfxVolume = 1.0;
        this.soundsLoaded = 0;
        this.totalSounds = 0;
    }
    
    // Define all game sounds
    defineSounds() {
        return {
            // Player sounds
            player: {
                jump: { file: 'jump.wav', volume: 0.8 },
                land: { file: 'land.wav', volume: 0.6 },
                hurt: { file: 'hurt.wav', volume: 1.0 },
                collect: { file: 'coin.wav', volume: 0.7 }
            },
            
            // Enemy sounds
            enemy: {
                spawn: { file: 'enemy_spawn.wav', volume: 0.9 },
                death: { file: 'explosion.wav', volume: 1.0 },
                attack: { file: 'enemy_attack.wav', volume: 0.8 }
            },
            
            // UI sounds
            ui: {
                click: { file: 'click.wav', volume: 0.5 },
                select: { file: 'select.wav', volume: 0.6 },
                error: { file: 'error.wav', volume: 0.7 }
            },
            
            // Music tracks
            music: {
                mainMenu: { file: 'menu.mp3', volume: this.musicVolume },
                level1: { file: 'level1.mp3', volume: this.musicVolume },
                boss: { file: 'boss.mp3', volume: this.musicVolume },
                victory: { file: 'victory.mp3', volume: this.musicVolume }
            }
        };
    }
    
    // Load all sounds
    async loadAllSounds(basePath = 'audio/') {
        const soundDefinitions = this.defineSounds();
        const loadPromises = [];
        
        // Count total sounds
        let total = 0;
        for (const category in soundDefinitions) {
            for (const soundName in soundDefinitions[category]) {
                total++;
            }
        }
        this.totalSounds = total;
        
        // Load each sound
        for (const category in soundDefinitions) {
            for (const soundName in soundDefinitions[category]) {
                const sound = soundDefinitions[category][soundName];
                const fullPath = basePath + category + '/' + sound.file;
                const fullName = category + '_' + soundName;
                
                // Store sound info
                this.soundBank[fullName] = {
                    ...sound,
                    category,
                    name: soundName
                };
                
                // Load the audio
                loadPromises.push(
                    this.audio.loadSound(fullName, fullPath).then(() => {
                        this.soundsLoaded++;
                        console.log(\`Loaded: \${fullName} (\${this.soundsLoaded}/\${this.totalSounds})\`);
                    })
                );
            }
        }
        
        // Wait for all to load
        await Promise.all(loadPromises);
        console.log('All sounds loaded!');
    }
    
    // Play a sound by category and name
    play(category, name, options = {}) {
        const soundKey = category + '_' + name;
        const soundDef = this.soundBank[soundKey];
        
        if (!soundDef) {
            console.warn(\`Sound not found: \${category}.\${name}\`);
            return;
        }
        
        // Merge default volume with options
        const volume = (options.volume || soundDef.volume) * this.sfxVolume;
        
        // Play the sound
        return this.audio.playSound(soundKey, { ...options, volume });
    }
    
    // Play music
    playMusic(trackName, options = {}) {
        const soundKey = 'music_' + trackName;
        const soundDef = this.soundBank[soundKey];
        
        if (!soundDef) {
            console.warn(\`Music not found: \${trackName}\`);
            return;
        }
        
        // Use music volume setting
        const volume = (options.volume || soundDef.volume) * this.musicVolume;
        
        this.audio.playMusic(soundKey, { ...options, volume });
    }
    
    // Update volume settings
    setMusicVolume(volume) {
        this.musicVolume = Math.max(0, Math.min(1, volume));
        if (this.audio.music) {
            this.audio.music.gainNode.gain.value = volume;
        }
    }
    
    setSfxVolume(volume) {
        this.sfxVolume = Math.max(0, Math.min(1, volume));
    }
    
    // Create loading screen
    getLoadingProgress() {
        if (this.totalSounds === 0) return 0;
        return (this.soundsLoaded / this.totalSounds) * 100;
    }
}

// =========== USAGE IN GAME ===========
