// =========== MAIN GAME ENGINE ===========
class ArcadeGame {
    constructor(canvasId) {
        // Canvas setup
        this.canvas = document.getElementById(canvasId);
        this.ctx = this.canvas.getContext('2d');
        this.width = this.canvas.width;
        this.height = this.canvas.height;
        
        // Game state
        this.state = 'MENU'; // MENU, PLAYING, PAUSED, GAME_OVER, WIN
        this.score = 0;
        this.level = 1;
        this.lives = 3;
        this.gameTime = 0;
        
        // Game objects
        this.player = null;
        this.enemies = [];
        this.bullets = [];
        this.particles = [];
        this.powerups = [];
        
        // Input system
        this.keys = {};
        this.setupInput();
        
        // Systems
        this.audio = new GameSoundManager();
        this.physics = new PhysicsEngine();
        this.ui = new UIManager(this);
        
        // Timing
        this.lastTime = 0;
        this.deltaTime = 0;
        this.frameCount = 0;
        
        // Initialize
        this.init();
    }
    
    init() {
        // Load assets
        this.loadAssets().then(() => {
            console.log('All assets loaded!');
            this.showTitleScreen();
        });
        
        // Start game loop
        this.gameLoop();
    }
    
    async loadAssets() {
        // Load sprite sheets
        await this.loadSpriteSheets();
        
        // Load sounds
        await this.audio.loadAllSounds('assets/audio/');
        
        // Load level data
        await this.loadLevel(this.level);
    }
    
    setupInput() {
        // Keyboard controls
        window.addEventListener('keydown', (e) => {
            this.keys[e.key] = true;
            
            // Game state shortcuts
            if (e.key === 'Escape') this.togglePause();
            if (e.key === 'Enter' && this.state === 'MENU') this.startGame();
            if (e.key === 'r' && this.state === 'GAME_OVER') this.resetGame();
        });
        
        window.addEventListener('keyup', (e) => {
            this.keys[e.key] = false;
        });
        
        // Mouse/touch controls
        this.canvas.addEventListener('click', (e) => this.handleClick(e));
        this.canvas.addEventListener('touchstart', (e) => this.handleTouch(e));
    }
    
    gameLoop(timestamp = 0) {
        // Calculate delta time for smooth animation
        this.deltaTime = (timestamp - this.lastTime) / 16.67; // Normalized to 60fps
        this.lastTime = timestamp;
        this.frameCount++;
        
        // Clear canvas
        this.ctx.clearRect(0, 0, this.width, this.height);
        
        // Update and draw based on state
        switch(this.state) {
            case 'MENU':
                this.updateMenu();
                this.drawMenu();
                break;
                
            case 'PLAYING':
                this.updateGame();
                this.drawGame();
                break;
                
            case 'PAUSED':
                this.drawGame(); // Draw paused game
                this.drawPauseMenu();
                break;
                
            case 'GAME_OVER':
                this.drawGameOver();
                break;
                
            case 'WIN':
                this.drawWinScreen();
                break;
        }
        
        // Always draw UI on top
        this.ui.draw(this.ctx);
        
        // Request next frame
        requestAnimationFrame((time) => this.gameLoop(time));
    }
    
    updateGame() {
        // Update player
        if (this.player) {
            this.player.update(this.keys, this.deltaTime);
        }
        
        // Update enemies
        this.enemies.forEach(enemy => {
            enemy.update(this.deltaTime);
            
            // Enemy-player collision
            if (this.player && this.physics.checkCollision(enemy, this.player)) {
                this.player.takeDamage(1);
                enemy.destroy();
                this.createExplosion(enemy.x, enemy.y);
            }
        });
        
        // Update bullets
        this.bullets.forEach((bullet, index) => {
            bullet.update(this.deltaTime);
            
            // Remove bullets off screen
            if (bullet.isOffScreen(this.width, this.height)) {
                this.bullets.splice(index, 1);
            }
            
            // Bullet-enemy collision
            this.enemies.forEach((enemy, enemyIndex) => {
                if (this.physics.checkCollision(bullet, enemy)) {
                    enemy.takeDamage(bullet.damage);
                    bullet.destroy();
                    this.bullets.splice(index, 1);
                    
                    if (enemy.health <= 0) {
                        this.score += enemy.points;
                        this.enemies.splice(enemyIndex, 1);
                        this.createExplosion(enemy.x, enemy.y);
                        this.audio.play('enemy', 'death');
                    }
                }
            });
        });
        
        // Update particles
        this.particles.forEach((particle, index) => {
            particle.update(this.deltaTime);
            if (particle.life <= 0) {
                this.particles.splice(index, 1);
            }
        });
        
        // Update powerups
        this.powerups.forEach((powerup, index) => {
            powerup.update(this.deltaTime);
            
            if (this.player && this.physics.checkCollision(powerup, this.player)) {
                this.applyPowerup(powerup.type);
                this.powerups.splice(index, 1);
                this.audio.play('player', 'collect');
            }
        });
        
        // Spawn new enemies based on time
        this.gameTime += this.deltaTime;
        this.spawnEnemies();
        
        // Check win/lose conditions
        this.checkGameConditions();
    }
    
    drawGame() {
        // Draw background
        this.drawBackground();
        
        // Draw game objects (order matters!)
        this.powerups.forEach(powerup => powerup.draw(this.ctx));
        this.particles.forEach(particle => particle.draw(this.ctx));
        this.enemies.forEach(enemy => enemy.draw(this.ctx));
        this.bullets.forEach(bullet => bullet.draw(this.ctx));
        if (this.player) this.player.draw(this.ctx);
        
        // Draw debug info (optional)
        if (this.debugMode) {
            this.drawDebugInfo();
        }
    }
    
    createExplosion(x, y, size = 1.0) {
        // Create particle explosion
        for (let i = 0; i < 15 * size; i++) {
            this.particles.push(new Particle(
                x, y,
                Math.random() * 360, // Random angle
                Math.random() * 5 + 2, // Speed
                \`
                    hsl($ {
                        Math.random() * 60
                    }, 100 % , 50 % )\`, // Yellow/orange
                Math.random() * 30 + 10 // Lifetime
            ));
        }
        
        // Screen shake for large explosions
        if (size > 2) {
            this.screenShake = 10;
        }
    }
    
    startGame() {
        this.state = 'PLAYING';
        this.score = 0;
        this.lives = 3;
        this.level = 1;
        this.gameTime = 0;
        
        // Create player
        this.player = new Player(this.width / 2, this.height - 100);
        
        // Start background music
        this.audio.playMusic('level1');
        
        // Clear any existing objects
        this.enemies = [];
        this.bullets = [];
        this.particles = [];
        this.powerups = [];
    }
    
    togglePause() {
        this.state = this.state === 'PLAYING' ? 'PAUSED' : 'PLAYING';
        
        if (this.state === 'PAUSED') {
            this.audio.pauseAll();
        } else {
            this.audio.resumeAll();
        }
    }
    
    checkGameConditions() {
        // Check lose condition
        if (this.lives <= 0) {
            this.state = 'GAME_OVER';
            this.audio.playMusic('gameOver');
            this.saveHighScore();
            return;
        }
        
        // Check win condition (all enemies defeated)
        if (this.enemies.length === 0 && this.gameTime > 60) { // 60 seconds in
            this.level++;
            if (this.level > 3) {
                this.state = 'WIN';
                this.audio.playMusic('victory');
                this.saveHighScore();
            } else {
                this.loadLevel(this.level);
                this.audio.play('ui', 'levelUp');
            }
        }
    }
    
    saveHighScore() {
        const highscores = JSON.parse(localStorage.getItem('highscores') || '[]');
        highscores.push({
            score: this.score,
            level: this.level,
            date: new Date().toISOString()
        });
        
        // Sort and keep top 10
        highscores.sort((a, b) => b.score - a.score);
        if (highscores.length > 10) highscores.length = 10;
        
        localStorage.setItem('highscores', JSON.stringify(highscores));
    }
    
    // More methods would follow for specific game logic...
}

// =========== PLAYER CLASS ===========
class Player {
    constructor(x, y) {
        this.x = x;
        this.y = y;
        this.width = 64;
        this.height = 64;
        this.speed = 5;
        this.health = 100;
        this.maxHealth = 100;
        
        // Sprite
        this.sprite = new SpriteSheet('assets/sprites/player.png', 64, 64, 8, 4);
        this.animation = 'idle';
        this.lastShot = 0;
        this.shootDelay = 300; // milliseconds
        
        // Weapon upgrades
        this.damage = 1;
        this.bulletSpeed = 10;
        this.rapidFire = false;
    }
    
    update(keys, deltaTime) {
        // Movement
        if (keys['ArrowLeft'] || keys['a']) {
            this.x -= this.speed;
            this.animation = 'left';
        } else if (keys['ArrowRight'] || keys['d']) {
            this.x += this.speed;
            this.animation = 'right';
        } else {
            this.animation = 'idle';
        }
        
        if (keys['ArrowUp'] || keys['w']) {
            this.y -= this.speed;
        } else if (keys['ArrowDown'] || keys['s']) {
            this.y += this.speed;
        }
        
        // Keep player on screen
        this.x = Math.max(0, Math.min(game.width - this.width, this.x));
        this.y = Math.max(0, Math.min(game.height - this.height, this.y));
        
        // Shooting
        if ((keys[' '] || keys['x']) && Date.now() - this.lastShot > this.shootDelay) {
            this.shoot();
            this.lastShot = Date.now();
        }
        
        // Update sprite animation
        this.sprite.update();
    }
    
    shoot() {
        const bullet = new Bullet(
            this.x + this.width / 2 - 4,
            this.y,
            0, -this.bulletSpeed,
            this.damage,
            'player'
        );
        
        game.bullets.push(bullet);
        game.audio.play('player', 'shoot');
        
        // Rapid fire creates two extra bullets at angles
        if (this.rapidFire) {
            game.bullets.push(new Bullet(
                this.x + this.width / 2 - 4,
                this.y,
                -2, -this.bulletSpeed,
                this.damage,
                'player'
            ));
            game.bullets.push(new Bullet(
                this.x + this.width / 2 - 4,
                this.y,
                2, -this.bulletSpeed,
                this.damage,
                'player'
            ));
        }
    }
    
    takeDamage(amount) {
        this.health -= amount;
        game.audio.play('player', 'hurt');
        
        // Visual feedback
        game.createExplosion(this.x + this.width / 2, this.y + this.height / 2, 0.5);
        
        if (this.health <= 0) {
            game.lives--;
            if (game.lives > 0) {
                this.respawn();
            }
        }
    }
    
    respawn() {
        this.x = game.width / 2;
        this.y = game.height - 100;
        this.health = this.maxHealth;
        game.createExplosion(this.x, this.y, 2);
    }
    
    draw(ctx) {
        this.sprite.draw(ctx, this.x, this.y);
        
        // Draw health bar
        ctx.fillStyle = '#ff0000';
        ctx.fillRect(this.x, this.y - 10, this.width, 5);
        ctx.fillStyle = '#00ff00';
        ctx.fillRect(this.x, this.y - 10, (this.health / this.maxHealth) * this.width, 5);
    }
}
</pre>
