// Organize code into modules
const TodoModule = (() => {
    // Private variables
    let tasks = [];
    let nextId = 1;
    
    // Public methods
    return {
        addTask: (text) => {
            const task = { id: nextId++, text, completed: false };
            tasks.push(task);
            return task;
        },
        
        getTasks: () => [...tasks], // Return copy
        
        deleteTask: (id) => {
            tasks = tasks.filter(task => task.id !== id);
        },
        
        // Can't access tasks directly from outside
        // This is called encapsulation
    };
