    <h4>Getting System Information</h4>
    <div style="background-color: #fff; padding: 15px; border-radius: 5px; margin: 10px 0;">
        <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px;">
            <div>
                <h5>Windows System Info</h5>
                <pre style="background-color: #0C0C0C; color: #CCCCCC; padding: 10px; border-radius: 5px;">
# Comprehensive system information
systeminfo

# OS version only
ver
winver            # GUI version dialog

# Computer name
hostname

# Network configuration
ipconfig
ipconfig /all     # Detailed info
ipconfig /release # Release IP address
ipconfig /renew   # Renew IP address

# Environment variables
set               # Show all
echo %PATH%       # Show PATH variable
echo %USERNAME%   # Show username

# Date and time
date /t
time /t

# Disk information
wmic logicaldisk get size,freespace,caption
wmic diskdrive get size,model

# Memory information
wmic memorychip get capacity
systeminfo | find "Total Physical Memory"

# Process list
tasklist
tasklist /svc     # With services

# Service information
sc query           # List services
net start          # Running services
                </pre>
            </div>
            
            <div>
                <h5>Linux/macOS System Info</h5>
                <pre style="background-color: #300A24; color: #00FF00; padding: 10px; border-radius: 5px;">
# Comprehensive system information
uname -a                 # All system info
hostnamectl              # Hostname and related info

# OS version/distro
lsb_release -a           # Linux distro info
cat /etc/os-release      # OS release info
sw_vers                  # macOS version

# Computer name
hostname

# Network configuration
ifconfig                 # Old, deprecated
ip addr                  # Modern replacement
ip link                  # Network interfaces

# Environment variables
env                      # Show all
printenv                 # Alternative
echo $PATH               # Show PATH variable
echo $USER               # Show username

# Date and time
date
cal                     # Calendar

# Disk information
df -h                   # Disk free (human readable)
lsblk                   # Block device info
fdisk -l                # Partition table

# Memory information
free -h                 # Memory usage
cat /proc/meminfo       # Detailed memory info

# Process list
ps aux                  # All processes
top                     # Interactive process viewer
htop                    # Better top (install first)

# System uptime
uptime                  # How long system running
w                       # Who is logged in + uptime

# Hardware information
lshw                    # List hardware
lspci                   # PCI devices
lsusb                   # USB devices
lscpu                   # CPU info
                </pre>
            </div>
        </div>
    </div>
    
