                <h5>Linux/macOS Aliases</h5>
                <pre style="background-color: #f1f1f1; padding: 10px; border-radius: 5px;">
# Add to ~/.bashrc or ~/.bash_profile

# Simple directory aliases
alias docs='cd ~/Documents'
alias down='cd ~/Downloads'
alias proj='cd ~/projects'
alias web='cd /var/www/html'
alias logs='cd /var/log'

# Function-based bookmarks (more flexible)
# Add to .bashrc
function cdd() {
    case $1 in
        docs) cd ~/Documents ;;
        proj) cd ~/projects ;;
        logs) cd /var/log ;;
        web)  cd /var/www/html ;;
        *) echo "Unknown bookmark: $1" ;;
    esac
}

# Usage: cdd proj

# Dynamic directory creation + cd
function mkcd() {
    mkdir -p "$1" && cd "$1"
}

# Usage: mkcd newproject

# Go up N directories
function up() {
    local count=\\\${1:-1}
    for ((i=0; i<count; i++)); do
        cd ..
    done
}

# Usage: up 3 (go up 3 levels)
