# PowerShell functions (add to profile)
function Set-LocationAlias {
    param(
        [string]$Name,
        [string]$Path
    )
    New-Alias -Name $Name -Value "Set-Location $Path" -Force
}

# Usage in PowerShell profile:
Set-LocationAlias docs "$env:USERPROFILE\Documents"
Set-LocationAlias proj "C:\Projects"
Set-LocationAlias temp "C:\Temp"

# Batch file functions
:cddocs
cd /d "%USERPROFILE%\Documents"
exit /b

:cdproj  
cd /d "C:\Projects"
exit /b

# Usage: call :cddocs

# Environment variable shortcuts
