REM Basic variables
set NAME=John
set AGE=30
echo %NAME% is %AGE% years old.

REM Delayed expansion (for loops/if blocks)
setlocal enabledelayedexpansion
set COUNT=1
for %%i in (a b c) do (
    set /A COUNT+=1
    echo !COUNT! - %%i
)

REM Arithmetic
set /A RESULT=5+3*2
echo Result: %RESULT%

set /A COUNTER+=1    # Increment
set /A VALUE*=2      # Multiply by 2

REM User input
set /p NAME="Enter your name: "
echo Hello %NAME%!

REM Read from file
for /f "tokens=*" %%i in (data.txt) do (
    echo Line: %%i
)

REM Default values
if "%OPTION%"=="" set OPTION=default
echo Using: %OPTION%

REM Substrings
set TEXT=HelloWorld
echo First 5: %TEXT:~0,5%
echo Last 5: %TEXT:~-5%
echo Remove first 2: %TEXT:~2%
