    <h4>Conditional Statements</h4>
    <div style="background-color: #fff; padding: 15px; border-radius: 5px; margin: 10px 0;">
        <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px;">
            <div>
                <h5>Windows Batch Conditionals</h5>
                <pre style="background-color: #f1f1f1; padding: 10px; border-radius: 5px;">
REM Basic if
if "%var%"=="value" echo They are equal

REM If exists
if exist file.txt echo File exists
if not exist dir\ (mkdir dir)

REM If errorlevel
somecommand
if %errorlevel% neq 0 (
    echo Command failed
)

REM String comparison
if "%str1%"=="%str2%" echo Equal
if "%str1%" lss "%str2%" echo str1 less than str2

REM Numeric comparison
if %num1% equ %num2% echo Equal
if %num1% gtr %num2% echo num1 greater

REM Operators:
equ = equal
neq = not equal
lss = less than
leq = less than or equal
gtr = greater than
geq = greater than or equal

REM If-else
if "%1"=="start" (
    echo Starting...
) else if "%1"=="stop" (
    echo Stopping...
) else (
    echo Usage: script [start|stop]
)

REM Check empty
if "%var%"=="" echo Variable is empty
if defined var echo Variable is defined
                </pre>
            </div>
            
            <div>
                <h5>Linux/macOS Shell Conditionals</h5>
                <pre style="background-color: #f1f1f1; padding: 10px; border-radius: 5px;">
#!/bin/bash

# Basic if
if [ "$var" = "value" ]; then
    echo "They are equal"
fi

# File tests
if [ -f "file.txt" ]; then
    echo "File exists"
fi

if [ -d "directory" ]; then
    echo "Directory exists"
fi

# Command success
if command; then
    echo "Command succeeded"
else
    echo "Command failed"
fi

# String comparison
if [ "$str1" = "$str2" ]; then echo "Equal"; fi
if [ "$str1" != "$str2" ]; then echo "Not equal"; fi
if [ "$str1" \< "$str2" ]; then echo "str1 less"; fi
if [ -z "$str1" ]; then echo "String is empty"; fi
if [ -n "$str1" ]; then echo "String is not empty"; fi

# Numeric comparison
if [ "$num1" -eq "$num2" ]; then echo "Equal"; fi
if [ "$num1" -ne "$num2" ]; then echo "Not equal"; fi
if [ "$num1" -lt "$num2" ]; then echo "num1 less"; fi
if [ "$num1" -le "$num2" ]; then echo "num1 less or equal"; fi
if [ "$num1" -gt "$num2" ]; then echo "num1 greater"; fi
if [ "$num1" -ge "$num2" ]; then echo "num1 greater or equal"; fi

# If-elif-else
if [ "$1" = "start" ]; then
    echo "Starting..."
elif [ "$1" = "stop" ]; then
    echo "Stopping..."
else
    echo "Usage: $0 [start|stop]"
fi

# Modern [[ ]] syntax (bash only)
if [[ "$var" == "value" ]]; then
    echo "Equal"
fi

if [[ "$var" =~ ^[0-9]+$ ]]; then
    echo "Contains only digits"
fi
