    <h4>Loops and Iteration</h4>
    <div style="background-color: #fff; padding: 15px; border-radius: 5px; margin: 10px 0;">
        <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px;">
            <div>
                <h5>Windows Batch Loops</h5>
                <pre style="background-color: #f1f1f1; padding: 10px; border-radius: 5px;">
REM For loop over list
for %%i in (1 2 3 4 5) do (
    echo Number: %%i
)

REM For loop over files
for %%f in (*.txt) do (
    echo File: %%f
)

REM For loop with range
for /l %%i in (1,1,10) do (
    echo %%i
)
REM Syntax: for /l %%i in (start,step,end)

REM For loop reading file
for /f "tokens=*" %%i in (names.txt) do (
    echo Name: %%i
)

REM While loop (simulated)
set COUNT=1
:while
if %COUNT% leq 5 (
    echo Count: %COUNT%
    set /a COUNT+=1
    goto :while
)

REM Infinite loop
:infinite
echo Press Ctrl+C to stop
timeout /t 1 /nobreak > nul
goto :infinite

REM Loop through arguments
for %%a in (%*) do (
    echo Argument: %%a
)

REM Loop with delayed expansion
setlocal enabledelayedexpansion
for %%i in (a b c) do (
    set VAR=prefix_%%i
    echo !VAR!
)
                </pre>
            </div>
            
            <div>
                <h5>Linux/macOS Shell Loops</h5>
                <pre style="background-color: #f1f1f1; padding: 10px; border-radius: 5px;">
#!/bin/bash

# For loop over list
for i in 1 2 3 4 5; do
    echo "Number: $i"
done

# For loop with range
for i in {1..5}; do
    echo "Number: $i"
done

for i in {1..10..2}; do  # Step of 2
    echo "Odd: $i"
done

# For loop over files
for file in *.txt; do
    echo "File: $file"
done

# C-style for loop
for ((i=0; i<5; i++)); do
    echo "C-style: $i"
done

# While loop
COUNT=1
while [ $COUNT -le 5 ]; do
    echo "Count: $COUNT"
    ((COUNT++))
done

# Read file line by line
while IFS= read -r line; do
    echo "Line: $line"
done < file.txt

# Infinite loop
while true; do
    echo "Press Ctrl+C to stop"
    sleep 1
done

# Until loop (opposite of while)
COUNT=1
until [ $COUNT -gt 5 ]; do
    echo "Until: $COUNT"
    ((COUNT++))
done

# Loop through arguments
for arg in "$@"; do
    echo "Argument: $arg"
done

# Loop with continue/break
for i in {1..10}; do
    if [ $i -eq 3 ]; then
        continue  # Skip 3
    fi
    if [ $i -eq 8 ]; then
        break     # Stop at 8
    fi
    echo "i = $i"
done
