                <h5>Basic TCP Server</h5>
                <pre style="background: #36454f; color: white; padding: 15px; border-radius: 5px; font-size: 0.9em;">
import socket
import threading
import json
import time
from datetime import datetime

class ChatServer:
    def __init__(self, host='0.0.0.0', port=9999):
        self.host = host
        self.port = port
        self.server_socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.clients = {}  # username: (socket, address)
        self.messages = []  # message history
        
    def start(self):
        """Start the chat server"""
        self.server_socket.bind((self.host, self.port))
        self.server_socket.listen(5)
        print(f"Chat server started on {self.host}:{self.port}")
        
        # Accept connections in a loop
        while True:
            client_socket, address = self.server_socket.accept()
            print(f"New connection from {address}")
            
            # Handle client in separate thread
            client_thread = threading.Thread(
                target=self.handle_client,
                args=(client_socket, address)
            )
            client_thread.start()
    
    def handle_client(self, client_socket, address):
        """Handle individual client connection"""
        username = None
        try:
            # Authentication phase
            auth_data = client_socket.recv(1024).decode('utf-8')
            auth_msg = json.loads(auth_data)
            
            if auth_msg['type'] == 'login':
                username = auth_msg['username']
                self.clients[username] = (client_socket, address)
                
                # Send welcome message
                welcome = {
                    'type': 'status',
                    'message': f'Welcome {username}! Users online: {len(self.clients)}',
                    'timestamp': datetime.now().isoformat()
                }
                client_socket.send(json.dumps(welcome).encode('utf-8'))
                
                # Broadcast user joined
                self.broadcast({
                    'type': 'status',
                    'message': f'{username} has joined the chat',
                    'timestamp': datetime.now().isoformat()
                }, exclude=username)
                
                # Main message loop
                while True:
                    data = client_socket.recv(1024)
                    if not data:
                        break
                    
                    message = json.loads(data.decode('utf-8'))
                    self.process_message(message, username)
                    
        except (ConnectionError, json.JSONDecodeError) as e:
            print(f"Client error: {e}")
        finally:
            # Cleanup on disconnect
            if username and username in self.clients:
                del self.clients[username]
                self.broadcast({
                    'type': 'status',
                    'message': f'{username} has left the chat',
                    'timestamp': datetime.now().isoformat()
                })
            client_socket.close()
            print(f"Connection closed for {address}")
    
    def process_message(self, message, sender):
        """Process incoming message"""
        if message['type'] == 'message':
            # Store message
            message['sender'] = sender
            message['timestamp'] = datetime.now().isoformat()
            self.messages.append(message)
            
            # Send acknowledgment
            ack = {
                'type': 'ack',
                'message_id': message.get('id', ''),
                'status': 'delivered',
                'timestamp': datetime.now().isoformat()
            }
            self.send_to_user(sender, ack)
            
            # Forward message to recipient
            recipient = message.get('recipient', 'all')
            if recipient == 'all':
                self.broadcast(message, exclude=sender)
            else:
                self.send_to_user(recipient, message)
    
    def broadcast(self, message, exclude=None):
        """Send message to all connected clients"""
        for username, (client_socket, _) in self.clients.items():
            if username != exclude:
                try:
                    client_socket.send(json.dumps(message).encode('utf-8'))
                except ConnectionError:
                    # Remove disconnected client
                    del self.clients[username]
    
    def send_to_user(self, username, message):
        """Send message to specific user"""
        if username in self.clients:
            client_socket, _ = self.clients[username]
            try:
                client_socket.send(json.dumps(message).encode('utf-8'))
            except ConnectionError:
                del self.clients[username]
    
    def stop(self):
        """Stop the server"""
        self.server_socket.close()

if __name__ == "__main__":
    server = ChatServer()
    try:
        server.start()
    except KeyboardInterrupt:
        server.stop()
        print("Server stopped")
