                <pre style="background: #36454f; color: white; padding: 15px; border-radius: 5px; font-size: 0.9em;">
import socket
import threading
import json
from datetime import datetime

class ChatClient:
    def __init__(self, host='127.0.0.1', port=9999):
        self.host = host
        self.port = port
        self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.username = None
        self.running = False
        
    def connect(self, username):
        """Connect to chat server"""
        self.username = username
        self.socket.connect((self.host, self.port))
        
        # Send login message
        login_msg = {
            'type': 'login',
            'username': username,
            'timestamp': datetime.now().isoformat()
        }
        self.socket.send(json.dumps(login_msg).encode('utf-8'))
        
        # Start receiver thread
        self.running = True
        receiver_thread = threading.Thread(target=self.receive_messages)
        receiver_thread.daemon = True
        receiver_thread.start()
        
        print(f"Connected as {username}")
    
    def receive_messages(self):
        """Receive messages from server"""
        while self.running:
            try:
                data = self.socket.recv(1024)
                if not data:
                    break
                    
                message = json.loads(data.decode('utf-8'))
                self.handle_message(message)
                
            except (ConnectionError, json.JSONDecodeError):
                break
        
        print("Disconnected from server")
    
    def handle_message(self, message):
        """Handle incoming message"""
        msg_type = message.get('type')
        
        if msg_type == 'message':
            sender = message.get('sender', 'Unknown')
            content = message.get('content', '')
            timestamp = message.get('timestamp', '')
            print(f"[{timestamp}] {sender}: {content}")
            
        elif msg_type == 'status':
            print(f"Status: {message.get('message', '')}")
            
        elif msg_type == 'ack':
            print(f"Message delivered: {message.get('message_id', '')}")
    
    def send_message(self, recipient, content):
        """Send a chat message"""
        message = {
            'type': 'message',
            'id': str(datetime.now().timestamp()),
            'sender': self.username,
            'recipient': recipient,
            'content': content,
            'timestamp': datetime.now().isoformat(),
            'ack_required': True
        }
        
        self.socket.send(json.dumps(message).encode('utf-8'))
        print(f"Sent to {recipient}: {content}")
    
    def disconnect(self):
        """Disconnect from server"""
        self.running = False
        self.socket.close()

# Example usage
if __name__ == "__main__":
    client = ChatClient()
    client.connect("Alice")
    
    # Send a message
    client.send_message("Bob", "Hello Bob!")
    
    # Keep client running
    import time
    time.sleep(10)
    client.disconnect()
