                            addMessage(
                            'Student_\${Math.floor(Math.random() * 100)}',
                            replies[Math.floor(Math.random() * replies.length)],
                            'student'
                            );
                        }, 1500);
                        }
                    }
                    }

                    addMessage('System', 'Chat started. Welcome!', 'system');
                    addMessage('Teacher', 'Any questions about todayâ€™s lab?', 'teacher');

                    document.getElementById('message-input')
                    .addEventListener('keypress', e => {
                        if (e.key === 'Enter') sendMessage();
                    });
