    <h4>ğŸ‘‘ The Power of sudo</h4>
    <pre style="background: #f5f5f5; padding: 15px; border-radius: 8px;">
# /etc/sudoers syntax - ALWAYS use visudo!
User_Alias ADMINS = alice, bob
User_Alias TEACHERS = teacher1, teacher2
User_Alias STUDENTS = %students

Cmnd_Alias SOFTWARE = /usr/bin/apt, /usr/bin/apt-get
Cmnd_Alias SERVICES = /usr/bin/systemctl, /usr/sbin/service
Cmnd_Alias NETWORK = /sbin/ifconfig, /bin/ping, /usr/sbin/iptables
Cmnd_Alias NOEXEC = /usr/bin/passwd, /usr/bin/chsh

# Grant full sudo access to admins
ADMINS ALL=(ALL:ALL) ALL

# Teachers can manage software and services
TEACHERS ALL=(ALL:ALL) SOFTWARE, SERVICES

# Students can only run specific commands
STUDENTS ALL=(ALL:ALL) /usr/bin/updatedb, /usr/bin/locate

# Allow without password (use carefully)
%admin ALL=(ALL) NOPASSWD: ALL

# Allow specific user to run as specific user
alice ALL=(www-data) /usr/bin/systemctl restart apache2

# Command restrictions (with arguments)
bob ALL=(ALL) /usr/bin/apt update, /usr/bin/apt install *

# Log sudo usage
Defaults logfile=/var/log/sudo.log
Defaults log_input, log_output

# Environment variable preservation
Defaults env_keep += "HTTP_PROXY HTTPS_PROXY"
Defaults env_reset

# Sudo logging examples:
sudo -l                         # List allowed commands
sudo -U username -l             # Check another user's sudo rights
sudo -k                         # Invalidate timestamp (force re-auth)
sudo -v                         # Validate/update timestamp
sudo -u username command        # Run as specific user
sudo -i                         # Login shell as root
sudo -s                         # Shell as root
    </pre>
