# Set up guest profile cleanup
# Create scheduled task to delete guest profile at logoff
schtasks /create /tn "CleanGuestProfile" /tr "cmd /c rd /s /q C:\Users\Guest" /sc onlogon /ru SYSTEM

# Windows 10/11: Use assigned access for kiosk mode
# Settings â†’ Accounts â†’ Family & other users â†’ Set up assigned access
# Limits guest to single application (like browser in kiosk mode)

# PowerShell guest restrictions
Set-LocalUser -Name "Guest" -PasswordNeverExpires $false
Set-LocalUser -Name "Guest" -UserMayChangePassword $false
Disable-LocalUser -Name "Guest"  # Disable when not needed
