<h3>Role-Based Access Control (RBAC) Examples</h3>
<div style="background-color: #fff0e6; padding: 20px; margin: 15px 0; border-radius: 10px;">
    <h4>ğŸ« Classroom RBAC Implementation</h4>
    
    <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px; margin-top: 15px;">
        <div>
            <h5>Linux Classroom Setup</h5>
            <pre style="background: #f5f5f5; padding: 15px; border-radius: 8px;">
#!/bin/bash
# classroom_setup.sh - Complete classroom user management

# Create groups
sudo groupadd teachers
sudo groupadd students
sudo groupadd admins

# Create users with appropriate groups
create_user() {
    local username=$1
    local fullname=$2
    local role=$3
    
    sudo useradd -m -c "$fullname" -s /bin/bash "$username"
    echo "$username:Password123" | sudo chpasswd
    sudo chage -d 0 "$username"  # Force password change on first login
    
    case $role in
        teacher)
            sudo usermod -a -G teachers,admins "$username"
            sudo mkdir -p "/home/$username/gradebooks"
            sudo chmod 750 "/home/$username/gradebooks"
            ;;
        student)
            sudo usermod -a -G students "$username"
            sudo mkdir -p "/home/$username/submissions"
            sudo chmod 700 "/home/$username/submissions"
            ;;
        admin)
            sudo usermod -a -G admins "$username"
            sudo usermod -a -G sudo "$username"
            ;;
    esac
    
    # Set up personal directory structure
    sudo -u "$username" mkdir -p "/home/$username/"{documents,projects,backups}
}

# Create example users
create_user "mjones" "Ms. Jones" teacher
create_user "dwilliams" "Dr. Williams" teacher
create_user "student01" "Alex Johnson" student
create_user "student02" "Maria Garcia" student
create_user "sysadmin" "System Admin" admin

# Set up shared directories
sudo mkdir -p /shared/{class_materials,submissions,grades}
sudo chown root:teachers /shared/class_materials
sudo chmod 775 /shared/class_materials
sudo chown root:teachers /shared/grades
sudo chmod 770 /shared/grades
sudo chown root:students /shared/submissions
sudo chmod 3770 /shared/submissions  # Sticky bit + SGID

# Configure sudo for different roles
sudo tee /etc/sudoers.d/classroom << 'EOF'
# Teachers can manage services and install software
%teachers ALL=(ALL) /usr/bin/apt, /usr/bin/apt-get, /usr/bin/systemctl

# Admins have full access
%admins ALL=(ALL:ALL) ALL

# Students can only run specific educational tools
%students ALL=(ALL) /usr/bin/python3, /usr/bin/gcc, /usr/bin/make

# Defaults for classroom
Defaults logfile=/var/log/sudo_classroom.log
Defaults iolog_dir=/var/log/sudo-io, iolog_file="%{user}"
EOF
            </pre>
        </div>
        <div>
            <h5>Windows Classroom Setup</h5>
            <pre style="background: #f5f5f5; padding: 15px; border-radius: 8px;">
# PowerShell classroom setup
# Create groups
New-LocalGroup -Name "Teachers" -Description "Teaching staff"
New-LocalGroup -Name "Students" -Description "Students"
New-LocalGroup -Name "ClassroomAdmins" -Description "Classroom administrators"

# Function to create users
function Create-ClassroomUser {
    param($Username, $FullName, $Role)
    
    $Password = ConvertTo-SecureString "TempPass123" -AsPlainText -Force
    New-LocalUser -Name $Username -FullName $FullName -Password $Password -PasswordNeverExpires $false
    
    # Add to appropriate groups
    switch ($Role) {
        "Teacher" {
            Add-LocalGroupMember -Group "Teachers" -Member $Username
            Add-LocalGroupMember -Group "Remote Desktop Users" -Member $Username
            # Set up teacher directories
            New-Item -Path "C:\Classroom\Teachers\$Username" -ItemType Directory -Force
            icacls "C:\Classroom\Teachers\$Username" /grant "$($Username):(OI)(CI)F"
        }
        "Student" {
            Add-LocalGroupMember -Group "Students" -Member $Username
            # Set up student directories
            New-Item -Path "C:\Classroom\Students\$Username" -ItemType Directory -Force
            icacls "C:\Classroom\Students\$Username" /grant "$($Username):(OI)(CI)F"
        }
        "Admin" {
            Add-LocalGroupMember -Group "ClassroomAdmins" -Member $Username
            Add-LocalGroupMember -Group "Administrators" -Member $Username
        }
    }
    
    # Force password change on first login
    Set-LocalUser -Name $Username -PasswordExpired $true
}

# Create users
Create-ClassroomUser -Username "Teacher1" -FullName "Jane Smith" -Role "Teacher"
Create-ClassroomUser -Username "Student1" -FullName "John Doe" -Role "Student"
Create-ClassroomUser -Username "Admin1" -FullName "System Admin" -Role "Admin"

# Set up shared directories
New-Item -Path "C:\Classroom\Shared" -ItemType Directory -Force
New-Item -Path "C:\Classroom\Shared\Materials" -ItemType Directory -Force
New-Item -Path "C:\Classroom\Shared\Submissions" -ItemType Directory -Force

# Set permissions using icacls
# Materials: Teachers RW, Students Read
icacls "C:\Classroom\Shared\Materials" /grant "Teachers:(OI)(CI)M"
icacls "C:\Classroom\Shared\Materials" /grant "Students:(OI)(CI)R"

# Submissions: Students write, Teachers read
icacls "C:\Classroom\Shared\Submissions" /grant "Students:(OI)(CI)W"
icacls "C:\Classroom\Shared\Submissions" /grant "Teachers:(OI)(CI)R"

# Disable Guest account
Disable-LocalUser -Name "Guest"
            </pre>
        </div>
    </div>
</div>

