    <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
# Linux audit commands
# 1. Check for users without passwords
sudo awk -F: '($2 == "" ) {print $1}' /etc/shadow

# 2. Check for users with UID 0 (other than root)
sudo awk -F: '($3 == 0) {print $1}' /etc/passwd

# 3. Check sudo permissions
sudo grep -r "NOPASSWD" /etc/sudoers /etc/sudoers.d/

# 4. Check for world-writable directories
find / -type d -perm -0002 -exec ls -ld {} \; 2>/dev/null | grep -v "/proc/"

# 5. Check for SUID/SGID files
find / -type f -perm /4000 -o -perm /2000 2>/dev/null

# 6. Check user login history
last
lastlog

# 7. Check for inactive accounts
sudo chage -l username | grep "Account expires"

# 8. Check password policies
sudo grep "^PASS" /etc/login.defs

# Windows audit commands (PowerShell)
# 1. Check local users
Get-LocalUser | Format-Table Name, Enabled, PasswordRequired, PasswordLastSet

# 2. Check local groups and members
Get-LocalGroup | ForEach-Object { 
    $group = $_.Name
    Get-LocalGroupMember -Group $group | Select-Object @{N='Group';E={$group}}, Name 
}

# 3. Check UAC status
(Get-ItemProperty -Path "HKLM:\SOFTWARE\Microsoft\Windows\CurrentVersion\Policies\System" -Name EnableLUA).EnableLUA

# 4. Check for enabled guest account
Get-LocalUser -Name "Guest" | Select-Object Name, Enabled

# 5. Check recent logins
Get-WinEvent -FilterHashtable @{LogName='Security'; ID=4624} -MaxEvents 10 | 
    Select-Object TimeCreated, @{N='User';E={$_.Properties[5].Value}}
    </pre>
