#!/bin/bash
# create_users.sh
echo "=== Creating Classroom Users ==="

# Default password for all users (should be changed on first login)
DEFAULT_PASSWORD="ChangeMe123!"

# User data array: username:fullname:uid:primary_group:secondary_groups:shell:homedir
declare -a users=(
    "teacher1:Ms. Jane Smith:2001:teachers:staff,classroom_users:/bin/bash:/home/teacher1"
    "teacher2:Mr. John Davis:2002:teachers:staff,classroom_users:/bin/bash:/home/teacher2"
    "student01:Alex Johnson:3001:students:class_a,classroom_users:/bin/bash:/home/student01"
    "student02:Maria Garcia:3002:students:class_a,classroom_users:/bin/bash:/home/student02"
    "student03:David Lee:3003:students:class_b,classroom_users:/bin/bash:/home/student03"
    "student04:Sarah Wilson:3004:students:class_b,classroom_users:/bin/bash:/home/student04"
    "admin1:System Administrator:1001:admins:staff,classroom_users:/bin/bash:/home/admin1"
    "guest1:Guest User:4001:guests::/bin/rbash:/home/guest1"
)

# Create skeleton directory for new users
sudo mkdir -p /etc/skel/classroom/{documents,projects,backups}
sudo cp /etc/skel/.bashrc /etc/skel/.bashrc.backup
sudo tee -a /etc/skel/.bashrc << 'EOF'

# Classroom environment settings
export CLASSROOM_PATH="/lab/week9/classroom"
alias cls='clear'
alias ll='ls -la'
alias ..='cd ..'

# Welcome message
echo "Welcome to the System Engineering Classroom!"
echo "Your classroom directory: $CLASSROOM_PATH"
echo "Today is $(date '+%A, %B %d, %Y')"
EOF

# Function to create a user
create_user() {
    local username=$1
    local fullname=$2
    local uid=$3
    local primary_group=$4
    local secondary_groups=$5
    local shell=$6
    local homedir=$7
    
    echo "Creating user: $username ($fullname)"
    
    # Check if user exists
    if id "$username" &>/dev/null; then
        echo "  User '$username' already exists. Skipping creation."
        return 0
    fi
    
    # Create user with specified parameters
    if sudo useradd \
        -m \
        -u "$uid" \
        -g "$primary_group" \
        -G "$secondary_groups" \
        -s "$shell" \
        -d "$homedir" \
        -c "$fullname" \
        "$username"; then
        
        echo "  âœ“ User account created"
        
        # Set password
        echo "$username:$DEFAULT_PASSWORD" | sudo chpasswd
        
        # Force password change on first login
        sudo chage -d 0 "$username"
        
        # Set password expiration (90 days)
        sudo chage -M 90 "$username"
        
        # Create classroom symlink
        sudo -u "$username" ln -s "/lab/week9/classroom" "$homedir/classroom"
        
        # Add user-specific files
        sudo cp /etc/skel/classroom "$homedir/" -r
        sudo chown -R "$username:$primary_group" "$homedir/classroom"
        
        echo "  âœ“ Home directory initialized"
        return 0
    else
        echo "  âœ— Failed to create user: $username"
        return 1
    fi
}

# Create all users
success_count=0
total_users=\\\${#users[@]}

echo "Creating $total_users users..."
echo ""

for user_data in "\\\${users[@]}"; do
    IFS=':' read -r username fullname uid primary_group secondary_groups shell homedir <<< "$user_data"
    
    if create_user "$username" "$fullname" "$uid" "$primary_group" "$secondary_groups" "$shell" "$homedir"; then
        ((success_count++))
    fi
    echo ""
done

# Create user information file
echo "=== User Creation Summary ==="
echo "Successfully created: $success_count/$total_users users"
echo ""

cat > /tmp/user_info.csv << 'EOF'
Username,Full Name,UID,Primary Group,Secondary Groups,Home Directory,Shell
EOF

for user_data in "\\\${users[@]}"; do
    IFS=':' read -r username fullname uid primary_group secondary_groups shell homedir <<< "$user_data"
    echo "$username,\"$fullname\",$uid,$primary_group,\"$secondary_groups\",$homedir,$shell" >> /tmp/user_info.csv
done

echo "User information saved to: /tmp/user_info.csv"
echo ""

# Test user creation
echo "=== Testing User Accounts ==="
for user_data in "\\\${users[@]}"; do
    username=$(echo "$user_data" | cut -d: -f1)
    if id "$username" &>/dev/null; then
        echo "âœ“ $username: $(id "$username")"
    else
        echo "âœ— $username: NOT FOUND"
    fi
done

echo ""
echo "=== Next Steps ==="
echo "1. Users must change password on first login"
echo "2. Test sudo configurations for each role"
echo "3. Verify home directory permissions"
echo "=== User Creation Complete ==="
    </pre>
    
    <h4>ğŸ§ª Testing User Accounts</h4>
    <pre style="background: #f5f5f5; padding: 15px; border-radius: 8px;">
# Test script for user accounts
#!/bin/bash
# test_users.sh

echo "=== User Account Testing ==="
echo ""

# Test 1: Verify users exist
echo "1. Verifying user existence:"
for user in teacher1 teacher2 student01 student02 student03 student04 admin1 guest1; do
    if id "$user" &>/dev/null; then
        echo "  âœ“ $user exists"
    else
        echo "  âœ— $user missing"
    fi
done

echo ""
# Test 2: Check home directories
echo "2. Checking home directories:"
for user in teacher1 teacher2 student01 student02 student03 student04 admin1 guest1; do
    homedir=$(getent passwd "$user" | cut -d: -f6)
    if [ -d "$homedir" ]; then
        echo "  âœ“ $user: $homedir exists"
        
        # Check ownership
        owner=$(stat -c "%U" "$homedir")
        if [ "$owner" = "$user" ]; then
            echo "    âœ“ Correct ownership"
        else
            echo "    âœ— Wrong ownership: $owner (expected: $user)"
        fi
    else
        echo "  âœ— $user: Home directory missing"
    fi
done

echo ""
# Test 3: Check group memberships
echo "3. Verifying group memberships:"
declare -A expected_groups=(
    [teacher1]="teachers staff classroom_users"
    [teacher2]="teachers staff classroom_users"
    [student01]="students class_a classroom_users"
    [student02]="students class_a classroom_users"
    [student03]="students class_b classroom_users"
    [student04]="students class_b classroom_users"
    [admin1]="admins staff classroom_users"
    [guest1]="guests"
)

for user in "\\\${!expected_groups[@]}"; do
    actual_groups=$(id -Gn "$user")
    expected="\\\${expected_groups[$user]}"
    
    all_correct=true
    for group in $expected; do
        if [[ ! " $actual_groups " =~ " $group " ]]; then
            echo "  âœ— $user missing group: $group"
            all_correct=false
        fi
    done
    
    if $all_correct; then
        echo "  âœ“ $user has correct groups: $actual_groups"
    fi
done

echo ""
# Test 4: Test password change requirement
echo "4. Checking password policies:"
for user in teacher1 teacher2 student01 student02 student03 student04 admin1; do
