        echo "  âœ— $user may not require password change"
    fi
done

echo ""
# Test 5: Test shell access
echo "5. Testing shell configurations:"
for user in teacher1 teacher2 student01 student02 student03 student04 admin1; do
    shell=$(getent passwd "$user" | cut -d: -f7)
    if [ -x "$shell" ]; then
        echo "  âœ“ $user has valid shell: $shell"
    else
        echo "  âœ— $user has invalid shell: $shell"
    fi
done

# Guest should have restricted shell
guest_shell=$(getent passwd guest1 | cut -d: -f7)
if [[ "$guest_shell" =~ rbash ]]; then
    echo "  âœ“ guest1 has restricted shell: $guest_shell"
else
    echo "  âœ— guest1 should have restricted shell"
fi

echo ""
echo "=== Testing Complete ==="
