<h3>Part 3: Directory Permissions and ACLs</h3>
<div style="background-color: #e0e0e0; padding: 20px; margin: 15px 0; border-radius: 10px;">
    <h4>ğŸ“‚ Setting Up Classroom Directory Structure</h4>
    
    <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
#!/bin/bash
# setup_directories.sh
echo "=== Setting Up Classroom Directory Structure ==="

BASE_DIR="/lab/week9/classroom"

# Function to create directory with permissions
create_dir() {
    local dir_path="$1"
    local owner="$2"
    local group="$3"
    local permissions="$4"
    local description="$5"
    
    echo "Creating: $dir_path"
    echo "  Purpose: $description"
    
    # Create directory
    sudo mkdir -p "$dir_path"
    
    # Set ownership
    sudo chown "$owner:$group" "$dir_path"
    
    # Set permissions (octal)
    sudo chmod "$permissions" "$dir_path"
    
    # Add special permissions if specified in description
    if [[ "$description" == *"SGID"* ]]; then
        sudo chmod g+s "$dir_path"
        echo "  âœ“ Added SGID bit"
    fi
    
    if [[ "$description" == *"sticky"* ]]; then
        sudo chmod +t "$dir_path"
        echo "  âœ“ Added sticky bit"
    fi
    
    echo "  âœ“ Created with permissions: $(stat -c "%A" "$dir_path")"
    echo ""
}

# Clear existing directory
sudo rm -rf "$BASE_DIR"
sudo mkdir -p "$BASE_DIR"

# Create directory structure
create_dir "$BASE_DIR/teachers" "root" "teachers" "0775" \
    "Teacher materials - teachers: rwx, others: r-x"

create_dir "$BASE_DIR/students" "root" "students" "0755" \
    "Student work areas - owner: rwx, group: r-x, others: r-x"

create_dir "$BASE_DIR/shared" "root" "classroom_users" "2775" \
    "Shared resources - SGID for group inheritance"

create_dir "$BASE_DIR/submissions" "root" "teachers" "3770" \
    "Homework submissions - SGID + sticky bit, teachers only"

create_dir "$BASE_DIR/public" "root" "root" "0755" \
    "Public resources - everyone read-only"

create_dir "$BASE_DIR/admin" "root" "admins" "0750" \
    "Admin tools - admins only"

# Create sample files
echo "=== Creating Sample Files ==="

# Teacher materials
sudo tee "$BASE_DIR/teachers/math_lesson.md" << 'EOF'
# Math Lesson Plan
## Topic: Algebra
Date: Week 9
Students: All
EOF

sudo tee "$BASE_DIR/teachers/science_project.md" << 'EOF'
# Science Project Guidelines
## Topic: Physics Experiments
Due: Week 10
Requirements: Lab report + demonstration
EOF

# Shared resources
sudo tee "$BASE_DIR/shared/class_schedule.txt" << 'EOF'
Monday:    Math 9-10, Science 10-11
Tuesday:   History 9-10, English 10-11
Wednesday: Lab 9-12
Thursday:  Review 9-10, Projects 10-12
Friday:    Tests 9-11, Cleanup 11-12
EOF

sudo tee "$BASE_DIR/shared/resources.md" << 'EOF'
# Classroom Resources
1. Textbooks in library
2. Software in computer lab
3. Lab equipment in science room
EOF

# Public resources
sudo tee "$BASE_DIR/public/welcome.txt" << 'EOF'
Welcome to System Engineering Classroom!
This is a public resource area.
EOF

sudo tee "$BASE_DIR/public/rules.md" << 'EOF'
# Classroom Rules
1. Be respectful
2. No food in lab
3. Clean up after yourself
4. Report issues immediately
EOF

echo "âœ“ Sample files created"
echo ""

# Set file permissions
echo "=== Setting File Permissions ==="

# Teacher files: teachers can edit, students can read
for file in "$BASE_DIR/teachers/"*; do
    sudo chown root:teachers "$file"
    sudo chmod 664 "$file"
done

# Shared files: classroom_users can edit
for file in "$BASE_DIR/shared/"*; do
    sudo chown root:classroom_users "$file"
    sudo chmod 664 "$file"
done

# Public files: everyone can read
for file in "$BASE_DIR/public/"*; do
    sudo chown root:root "$file"
    sudo chmod 644 "$file"
done

echo "âœ“ File permissions set"
echo ""

echo "=== Directory Structure Complete ==="
tree "$BASE_DIR" 2>/dev/null || ls -la "$BASE_DIR"
