#!/bin/bash
# setup_acls.sh
echo "=== Implementing Access Control Lists ==="

BASE_DIR="/lab/week9/classroom"

# Install ACL tools if needed
if ! command -v setfacl &> /dev/null; then
    echo "Installing ACL utilities..."
    sudo apt-get install -y acl 2>/dev/null || sudo yum install -y acl 2>/dev/null
fi

# Enable ACLs on filesystem (if not already)
ACL_ENABLED=$(mount | grep " / " | grep acl)
if [ -z "$ACL_ENABLED" ]; then
    echo "ACLs not enabled on root filesystem. Enabling temporarily..."
    sudo mount -o remount,acl /
fi

# Function to apply ACLs
apply_acl() {
    local path="$1"
    local acl_rules="$2"
    local description="$3"
    
    echo "Setting ACLs on: $path"
    echo "  Purpose: $description"
    
    # Remove existing ACLs first
    sudo setfacl -b "$path"
    
    # Apply new ACLs
    IFS=';' read -ra rules <<< "$acl_rules"
    for rule in "\\\${rules[@]}"; do
        if [ -n "$rule" ]; then
            sudo setfacl -m "$rule" "$path"
            echo "  âœ“ Applied: $rule"
        fi
    done
    
    # Set default ACLs if it's a directory
    if [ -d "$path" ]; then
        for rule in "\\\${rules[@]}"; do
            if [ -n "$rule" ]; then
                sudo setfacl -d -m "$rule" "$path"
                echo "  âœ“ Set default: $rule"
            fi
        done
    fi
    
    echo ""
}

# Apply ACLs to each directory
apply_acl "$BASE_DIR/students" \
    "u:teacher1:rwx;u:teacher2:rwx;g:teachers:rwx;m::rwx" \
    "Teachers need write access to student directories"

apply_acl "$BASE_DIR/shared" \
    "g:teachers:rwx;g:students:r-x;m::rwx" \
    "Teachers: rwx, Students: r-x"

apply_acl "$BASE_DIR/submissions" \
    "g:students:rwx;m::rwx" \
    "Students can write but mask limits to prevent reading others' files"

# Special ACL for specific student
apply_acl "$BASE_DIR/shared/class_schedule.txt" \
    "u:student01:rw-;m::rw-" \
    "Student01 can edit class schedule (class representative)"

# Special ACL for admin access everywhere
for dir in "$BASE_DIR"/*/; do
    if [ -d "$dir" ]; then
        sudo setfacl -m "g:admins:rwx" "$dir"
        sudo setfacl -d -m "g:admins:rwx" "$dir"
    fi
done
echo "âœ“ Admin access granted to all directories"

# Verify ACLs
echo "=== Verifying ACLs ==="
for dir in "$BASE_DIR"/*/; do
    if [ -d "$dir" ]; then
        dirname=$(basename "$dir")
        echo "ACLs for $dirname/:"
        getfacl "$dir" | grep -E "(^#|teachers|students|admins|mask)" | head -10
        echo ""
    fi
done

# Create ACL test script
cat > /tmp/test_acls.sh << 'EOF'
