<div style="background-color: #f0f8ff; padding: 20px; margin: 15px 0; border-radius: 10px;">
    <h4>ğŸ§ª Final Lab Testing and Validation</h4>
    
    <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
#!/bin/bash
# final_lab_test.sh
echo "=== FINAL LAB TEST: Classroom User Management System ==="
echo "Date: $(date)"
echo ""

# Scoring variables
TOTAL_POINTS=0
MAX_POINTS=100

# Function to award points
award_points() {
    local points=$1
    local message=$2
    TOTAL_POINTS=$((TOTAL_POINTS + points))
    echo "âœ“ +\\\${points}pts: $message"
}

# Function to deduct points
deduct_points() {
    local points=$1
    local message=$2
    echo "âœ— -\\\${points}pts: $message"
}

echo "1. Testing Group Creation (10 points)"
echo "-----------------------------------"
required_groups=("teachers" "students" "staff" "admins" "guests" "class_a" "class_b" "classroom_users")
group_score=0
for group in "\\\${required_groups[@]}"; do
    if getent group "$group" >/dev/null; then
        group_score=$((group_score + 1))
        echo "  âœ“ Group exists: $group"
    else
        echo "  âœ— Missing group: $group"
    fi
done

if [ $group_score -eq \\\${#required_groups[@]} ]; then
    award_points 10 "All groups created successfully"
else
    points=$((group_score * 10 / \\\${#required_groups[@]}))
    award_points $points "Partial group creation ($group_score/\\\${#required_groups[@]})"
fi

echo ""
echo "2. Testing User Creation (15 points)"
echo "-----------------------------------"
required_users=("teacher1" "teacher2" "student01" "student02" "student03" "student04" "admin1" "guest1")
user_score=0
for user in "\\\${required_users[@]}"; do
    if id "$user" &>/dev/null; then
        user_score=$((user_score + 1))
        
        # Check home directory
        homedir=$(getent passwd "$user" | cut -d: -f6)
        if [ -d "$homedir" ]; then
            echo "  âœ“ User exists with home: $user"
        else
            echo "  âš  User exists but missing home: $user"
        fi
    else
        echo "  âœ— Missing user: $user"
    fi
done

if [ $user_score -eq \\\${#required_users[@]} ]; then
    award_points 15 "All users created with home directories"
else
    points=$((user_score * 15 / \\\${#required_users[@]}))
    award_points $points "Partial user creation ($user_score/\\\${#required_users[@]})"
fi

echo ""
echo "3. Testing Directory Permissions (20 points)"
echo "-------------------------------------------"
directories=(
    "/lab/week9/classroom/teachers:drwxrwxr-x"
    "/lab/week9/classroom/students:drwxr-xr-x"
    "/lab/week9/classroom/shared:drwxrwsr-x"
    "/lab/week9/classroom/submissions:drwxrws---"
    "/lab/week9/classroom/public:drwxr-xr-x"
    "/lab/week9/classroom/admin:drwxr-x---"
)

perm_score=0
for dir_spec in "\\\${directories[@]}"; do
    dir=$(echo "$dir_spec" | cut -d: -f1)
    expected=$(echo "$dir_spec" | cut -d: -f2)
    
    if [ -d "$dir" ]; then
        actual=$(stat -c "%A" "$dir")
        if [ "$actual" = "$expected" ]; then
            perm_score=$((perm_score + 3))
            echo "  âœ“ Permissions correct: $dir"
            echo "    Expected: $expected, Got: $actual"
        else
            # Check for special bits
            if [[ "$expected" == *"s"* ]] && [[ "$actual" == *"s"* ]]; then
                perm_score=$((perm_score + 2))
                echo "  âš  Special bits correct, other perms differ: $dir"
            else
                echo "  âœ— Permissions incorrect: $dir"
                echo "    Expected: $expected, Got: $actual"
            fi
        fi
    else
        echo "  âœ— Directory missing: $dir"
    fi
done

# Extra points for sticky bit on submissions
if [ -d "/lab/week9/classroom/submissions" ]; then
    if stat -c "%A" "/lab/week9/classroom/submissions" | grep -q "^d.*t"; then
        perm_score=$((perm_score + 2))
        echo "  âœ“ Sticky bit set on submissions directory"
    else
        echo "  âœ— Missing sticky bit on submissions"
    fi
fi

award_points $perm_score "Directory permissions ($perm_score/20)"

echo ""
echo "4. Testing ACL Implementation (20 points)"
echo "----------------------------------------"
acl_score=0

# Check if ACLs are enabled
if command -v getfacl &>/dev/null; then
    # Test teacher ACL on students directory
    if sudo -u teacher1 touch /lab/week9/classroom/students/test_acl 2>/dev/null; then
        acl_score=$((acl_score + 5))
        echo "  âœ“ Teachers can write to students directory (ACL working)"
        sudo rm -f /lab/week9/classroom/students/test_acl
    else
        echo "  âœ— Teachers cannot write to students directory"
    fi
    
    # Test student ACL on shared directory (read-only)
    if sudo -u student01 touch /lab/week9/classroom/shared/test_acl 2>/dev/null; then
        echo "  âœ— Students can write to shared directory (ACL NOT working)"
        sudo rm -f /lab/week9/classroom/shared/test_acl
    else
        acl_score=$((acl_score + 5))
        echo "  âœ“ Students cannot write to shared directory (ACL working)"
    fi
    
    # Test specific student ACL on schedule
    sudo -u student01 bash -c 'echo "test" >> /lab/week9/classroom/shared/class_schedule.txt' 2>/dev/null
    if [ $? -eq 0 ]; then
        acl_score=$((acl_score + 5))
        echo "  âœ“ Student01 can edit schedule (specific ACL working)"
        # Remove test line
        sudo sed -i '/^test$/d' /lab/week9/classroom/shared/class_schedule.txt
    else
        echo "  âœ— Student01 cannot edit schedule"
    fi
    
    # Test admin ACL everywhere
    if sudo -u admin1 touch /lab/week9/classroom/admin/test_acl 2>/dev/null; then
        acl_score=$((acl_score + 5))
        echo "  âœ“ Admins have access everywhere"
        sudo rm -f /lab/week9/classroom/admin/test_acl
    else
        echo "  âœ— Admins cannot access admin directory"
    fi
else
    echo "  âš  ACL tools not installed, skipping ACL tests"
fi

award_points $acl_score "ACL implementation ($acl_score/20)"

echo ""
echo "5. Testing Sudo Configuration (15 points)"
echo "----------------------------------------"
sudo_score=0

# Test teacher sudo
if sudo -u teacher1 sudo -l 2>/dev/null | grep -q "SOFTWARE"; then
    sudo_score=$((sudo_score + 5))
    echo "  âœ“ Teachers have software management sudo access"
else
    echo "  âœ— Teachers missing software sudo access"
fi

# Test student sudo (limited)
if sudo -u student01 sudo -l 2>/dev/null | grep -q "STUDENT_TOOLS"; then
    sudo_score=$((sudo_score + 5))
    echo "  âœ“ Students have tool sudo access"
else
    echo "  âœ— Students missing tool sudo access"
fi

# Test admin sudo (full)
if sudo -u admin1 sudo -l 2>/dev/null | grep -q "ALL"; then
    sudo_score=$((sudo_score + 5))
    echo "  âœ“ Admins have full sudo access"
else
    echo "  âœ— Admins missing full sudo access"
fi

award_points $sudo_score "Sudo configuration ($sudo_score/15)"

echo ""
echo "6. Testing Home Directory Setup (10 points)"
echo "------------------------------------------"
home_score=0

# Check skeleton files in user homes
for user in teacher1 student01 admin1; do
    if [ -d "/home/$user/classroom" ]; then
        home_score=$((home_score + 2))
        echo "  âœ“ $user has classroom symlink"
    else
        echo "  âœ— $user missing classroom symlink"
    fi
    
    if [ -d "/home/$user/classroom/documents" ]; then
        home_score=$((home_score + 1))
    fi
done

award_points $home_score "Home directory setup ($home_score/10)"

echo ""
echo "7. Final Validation (10 points)"
echo "------------------------------"
validation_score=0

# Test password change requirement
if sudo chage -l teacher1 | grep -q "password must be changed"; then
    validation_score=$((validation_score + 3))
    echo "  âœ“ Users must change password on first login"
else
    echo "  âœ— Password change not required"
fi

# Test guest restricted shell
guest_shell=$(getent passwd guest1 | cut -d: -f7)
if [[ "$guest_shell" =~ rbash ]]; then
    validation_score=$((validation_score + 4))
    echo "  âœ“ Guest has restricted shell: $guest_shell"
else
    echo "  âœ— Guest should have restricted shell"
fi

# Test class group memberships
if id student01 | grep -q "class_a"; then
    validation_score=$((validation_score + 3))
    echo "  âœ“ Students in correct class groups"
else
    echo "  âœ— Student not in class group"
fi

award_points $validation_score "Final validation ($validation_score/10)"

echo ""
echo "=== FINAL SCORE ==="
echo "Total: $TOTAL_POINTS / $MAX_POINTS"
echo ""

# Grade calculation
percentage=$((TOTAL_POINTS * 100 / MAX_POINTS))
if [ $percentage -ge 90 ]; then
    grade="A ğŸ‰"
elif [ $percentage -ge 80 ]; then
    grade="B ğŸ‘"
elif [ $percentage -ge 70 ]; then
    grade="C ğŸ‘Œ"
elif [ $percentage -ge 60 ]; then
    grade="D âš "
else
    grade="F âŒ"
fi

echo "Grade: $grade ($percentage%)"
echo ""
echo "=== LAB COMPLETE ==="

# Cleanup instructions
echo ""
echo "=== CLEANUP INSTRUCTIONS ==="
echo "To remove all lab users and groups:"
echo "1. Backup any important data"
echo "2. Run: sudo deluser --remove-home teacher1 teacher2 student01 student02 student03 student04 admin1 guest1"
echo "3. Run: sudo delgroup teachers students staff admins guests class_a class_b classroom_users"
echo "4. Remove lab directory: sudo rm -rf /lab/week9"
echo ""
