<h3>Server-Specific Permission Models</h3>
<div style="background-color: #fff0e6; padding: 20px; margin: 15px 0; border-radius: 10px;">
    <h4>ğŸ—‚ï¸ File Server Permission Mapping</h4>
    
    <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px; margin-top: 15px;">
        <div>
            <h5>Samba/Windows File Server</h5>
            <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
# /etc/samba/smb.conf - Role-based shares
[Company_Shared]
    path = /srv/samba/company
    valid users = @employees
    read list = @employees
    write list = @managers, @team_leads
    force create mode = 0660
    force directory mode = 0770

[Finance_Department]
    path = /srv/samba/finance
    valid users = @finance, @executives, @it_admins
    read list = @finance, @executives
    write list = @finance_managers
    admin users = @cfo, @it_director
    force group = finance

[IT_Infrastructure]
    path = /srv/samba/it
    valid users = @it_admins, @system_architects
    read list = @it_admins
    write list = @it_admins, @system_architects
    admin users = @it_director
    hide files = /passwords/*/secrets/*

[Public_Resources]
    path = /srv/samba/public
    guest ok = yes
    read only = yes
    browsable = yes

# Windows ACL equivalents
# Using icacls on Windows:
icacls "\\server\Finance" /grant "FinanceManagers:(OI)(CI)F"
icacls "\\server\Finance" /grant "FinanceStaff:(OI)(CI)RX"
icacls "\\server\Finance" /grant "Executives:(OI)(CI)R"
icacls "\\server\Finance" /deny "Everyone:(OI)(CI)W"
            </pre>
        </div>
        <div>
            <h5>Linux NFS Server</h5>
