# /etc/exports - NFS role-based exports
# Department shares with squashing
/srv/nfs/sales      192.168.1.0/24(rw,sync,no_subtree_check,root_squash,anonuid=1001,anongid=1001)
/srv/nfs/marketing  192.168.1.0/24(rw,sync,no_subtree_check,root_squash,anonuid=1002,anongid=1002)
/srv/nfs/engineering 192.168.1.0/24(rw,sync,no_subtree_check,no_root_squash)  # For devs

