[General]
Domain = company.local

[Translation]
Method = nsswitch

[Mapping]
Nobody-User = nobody
Nobody-Group = nogroup

# Exporting with ACL support
/srv/nfs/shared     192.168.1.0/24(rw,sync,no_subtree_check,acl,no_root_squash)

# Mount options for clients
mount -t nfs4 -o sec=krb5p,acl server:/sales /mnt/sales
