    <h4>ğŸŒ Web Server Permission Mapping</h4>
    
    <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px; margin-top: 15px;">
        <div>
            <h5>Apache Virtual Hosts by Role</h5>
            <pre style="background: #f5f5f5; padding: 15px; border-radius: 8px;">
# /etc/apache2/sites-available/roles.conf
# Employee Portal
<VirtualHost *:80>
    ServerName portal.company.local
    DocumentRoot /var/www/portal
    
    # Public area - everyone
    <Directory /var/www/portal/public>
        Require all granted
        Options Indexes FollowSymLinks
    </Directory>
    
    # Employee area - authenticated employees
    <Directory /var/www/portal/employee>
        AuthType Basic
        AuthName "Employee Portal"
        AuthUserFile /etc/apache2/.htpasswd-employees
        Require valid-user
    </Directory>
    
    # Manager area - managers only
    <Directory /var/www/portal/manager>
        AuthType Basic
        AuthName "Manager Portal"
        AuthUserFile /etc/apache2/.htpasswd-managers
        Require user manager1 manager2 director1
        # Or use group:
        # Require group managers
    </Directory>
    
    # Admin area - IT only
    <Directory /var/www/portal/admin>
        AuthType Basic
        AuthName "Admin Portal"
        AuthUserFile /etc/apache2/.htpasswd-admins
        Require user admin1 admin2
        Order deny,allow
        Deny from all
        Allow from 192.168.1.0/24
    </Directory>
</VirtualHost>

# Department-specific sites
<VirtualHost *:80>
    ServerName finance.company.local
    DocumentRoot /var/www/finance
    
    <Directory /var/www/finance>
        AuthType Basic
        AuthName "Finance Department"
        AuthUserFile /etc/apache2/.htpasswd-finance
        Require valid-user
        
        # IP restriction for sensitive data
        Order deny,allow
        Deny from all
        Allow from 192.168.1.100-192.168.1.150
        Allow from 10.0.0.0/8
    </Directory>
</VirtualHost>
            </pre>
        </div>
        <div>
            <h5>Nginx Location Blocks by Role</h5>
            <pre style="background: #f5f5f5; padding: 15px; border-radius: 8px;">
# /etc/nginx/sites-available/roles
server {
    listen 80;
    server_name apps.company.local;
    
    # Public application
    location /public {
        root /var/www/apps;
        index index.html;
        
        # Allow everyone
        allow all;
        
        # Rate limiting for public access
        limit_req zone=public burst=10 nodelay;
    }
    
    # Employee application (requires auth)
    location /employee {
        root /var/www/apps;
        index index.html;
        
        # Basic authentication
        auth_basic "Employee Area";
        auth_basic_user_file /etc/nginx/.htpasswd-employees;
        
        # IP restriction
        allow 192.168.1.0/24;
        deny all;
    }
    
    # Manager dashboard
    location /manager {
        root /var/www/apps;
        index index.html;
        
        # Dual authentication
        auth_basic "Manager Dashboard";
        auth_basic_user_file /etc/nginx/.htpasswd-managers;
        
        # Client certificate authentication
        ssl_client_certificate /etc/ssl/certs/manager-ca.crt;
        ssl_verify_client on;
        
        # IP restriction
        allow 192.168.1.50-192.168.1.100;
        deny all;
    }
    
    # Admin API
    location /admin/api {
        # Proxy to backend with headers
        proxy_pass http://backend:8080;
        
        # Pass authentication headers
        proxy_set_header X-Forwarded-User $remote_user;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        
        # IP whitelist for admin API
        allow 10.0.0.0/8;
        allow 192.168.1.1;
        deny all;
    }
    
    # Department-specific access
    location ~ ^/(sales|marketing|engineering) {
        # Capture department
        set $dept $1;
        
        root /var/www/dept/$dept;
        
        # Department-specific authentication
        auth_basic "$dept Department";
        auth_basic_user_file /etc/nginx/.htpasswd-$dept;
        
        # Department IP ranges
        if ($dept = sales) {
            allow 192.168.2.0/24;
        }
        if ($dept = engineering) {
            allow 192.168.3.0/24;
        }
        deny all;
    }
}
            </pre>
        </div>
    </div>
</div>

<h3>Database Permission Models</h3>
<div style="background-color: #e8f5e8; padding: 20px; margin: 15px 0; border-radius: 10px;">
    <h4>ğŸ—ƒï¸ Database Role-Based Access Control</h4>
    
    <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px; margin-top: 15px;">
        <div>
            <h5>MySQL/MariaDB Roles</h5>
            <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
-- Create roles for different positions
CREATE ROLE 'employee_readonly';
CREATE ROLE 'employee_readwrite';
CREATE ROLE 'manager_role';
CREATE ROLE 'developer_role';
CREATE ROLE 'dba_role';

-- Grant privileges to roles
-- Employee read-only
GRANT SELECT ON company_db.* TO 'employee_readonly';
GRANT SELECT ON company_db.employee_directory TO 'employee_readonly';

-- Employee read-write (department specific)
GRANT SELECT, INSERT, UPDATE ON company_db.sales.* TO 'employee_readwrite';
GRANT SELECT, INSERT, UPDATE ON company_db.marketing.* TO 'employee_readwrite';

-- Manager role
GRANT SELECT, INSERT, UPDATE, DELETE ON company_db.department.* TO 'manager_role';
GRANT SELECT ON company_db.hr.* TO 'manager_role';
GRANT EXECUTE ON PROCEDURE company_db.generate_report TO 'manager_role';

-- Developer role
GRANT SELECT, INSERT, UPDATE, DELETE, CREATE TEMPORARY TABLES, 
      EXECUTE ON company_db.* TO 'developer_role';
GRANT CREATE, ALTER, DROP ON company_db.dev_* TO 'developer_role';

-- DBA role (full access)
GRANT ALL PRIVILEGES ON *.* TO 'dba_role' WITH GRANT OPTION;

-- Create users and assign roles
CREATE USER 'jsmith'@'192.168.1.%' IDENTIFIED BY 'secure_password';
CREATE USER 'mjones'@'192.168.1.%' IDENTIFIED BY 'secure_password';
CREATE USER 'dev1'@'192.168.3.%' IDENTIFIED BY 'secure_password';
CREATE USER 'dba1'@'localhost' IDENTIFIED BY 'secure_password';

-- Assign roles to users
GRANT 'employee_readonly' TO 'jsmith'@'192.168.1.%';
GRANT 'manager_role' TO 'mjones'@'192.168.1.%';
GRANT 'developer_role' TO 'dev1'@'192.168.3.%';
GRANT 'dba_role' TO 'dba1'@'localhost';

-- Set default roles
SET DEFAULT ROLE 'employee_readonly' FOR 'jsmith'@'192.168.1.%';
SET DEFAULT ROLE 'manager_role' FOR 'mjones'@'192.168.1.%';
SET DEFAULT ROLE 'developer_role' FOR 'dev1'@'192.168.3.%';
SET DEFAULT ROLE 'dba_role' FOR 'dba1'@'localhost';

-- Department-specific access
CREATE DATABASE sales_db;
CREATE DATABASE marketing_db;
CREATE DATABASE hr_db;

-- Sales department users
CREATE USER 'sales_user'@'192.168.2.%' IDENTIFIED BY 'password';
GRANT ALL PRIVILEGES ON sales_db.* TO 'sales_user'@'192.168.2.%';
GRANT SELECT ON company_db.products TO 'sales_user'@'192.168.2.%';

-- HR department (highly restricted)
CREATE USER 'hr_user'@'192.168.10.%' IDENTIFIED BY 'password';
GRANT SELECT, INSERT, UPDATE ON hr_db.employees TO 'hr_user'@'192.168.10.%';
GRANT SELECT ON hr_db.benefits TO 'hr_user'@'192.168.10.%';
REVOKE DELETE ON hr_db.* FROM 'hr_user'@'192.168.10.%';

-- View role assignments
SELECT * FROM mysql.role_edges;
SELECT * FROM mysql.default_roles;
            </pre>
        </div>
        <div>
            <h5>PostgreSQL Roles</h5>
            <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
-- Create role groups
CREATE ROLE read_only_role NOLOGIN;
CREATE ROLE read_write_role NOLOGIN;
CREATE ROLE manager_role NOLOGIN;
CREATE ROLE developer_role NOLOGIN;
CREATE ROLE dba_role NOLOGIN;

-- Grant schema usage
GRANT USAGE ON SCHEMA public TO read_only_role;
GRANT USAGE ON SCHEMA public TO read_write_role;
GRANT USAGE ON SCHEMA public TO manager_role;

-- Grant table privileges
-- Read-only role
GRANT SELECT ON ALL TABLES IN SCHEMA public TO read_only_role;
ALTER DEFAULT PRIVILEGES IN SCHEMA public GRANT SELECT ON TABLES TO read_only_role;

-- Read-write role
GRANT SELECT, INSERT, UPDATE ON ALL TABLES IN SCHEMA public TO read_write_role;
ALTER DEFAULT PRIVILEGES IN SCHEMA public 
    GRANT SELECT, INSERT, UPDATE ON TABLES TO read_write_role;

-- Manager role (with delete capability)
GRANT SELECT, INSERT, UPDATE, DELETE ON ALL TABLES IN SCHEMA public TO manager_role;
GRANT EXECUTE ON ALL FUNCTIONS IN SCHEMA public TO manager_role;
ALTER DEFAULT PRIVILEGES IN SCHEMA public 
    GRANT SELECT, INSERT, UPDATE, DELETE ON TABLES TO manager_role;

-- Developer role (can create objects)
GRANT CREATE ON SCHEMA public TO developer_role;
GRANT ALL PRIVILEGES ON ALL TABLES IN SCHEMA public TO developer_role;
GRANT ALL PRIVILEGES ON ALL SEQUENCES IN SCHEMA public TO developer_role;
GRANT ALL PRIVILEGES ON ALL FUNCTIONS IN SCHEMA public TO developer_role;

-- DBA role (full control)
GRANT ALL PRIVILEGES ON DATABASE company_db TO dba_role;

-- Create users and assign roles
CREATE USER jsmith WITH PASSWORD 'secure_password';
CREATE USER mjones WITH PASSWORD 'secure_password';
CREATE USER dev1 WITH PASSWORD 'secure_password';
CREATE USER dba1 WITH PASSWORD 'secure_password';

-- Grant roles to users
GRANT read_only_role TO jsmith;
GRANT manager_role TO mjones;
GRANT developer_role TO dev1;
GRANT dba_role TO dba1;

-- Row-level security (PostgreSQL 9.5+)
-- Enable RLS on sensitive table
ALTER TABLE employees ENABLE ROW LEVEL SECURITY;

-- Managers can only see their department
CREATE POLICY department_policy ON employees
    FOR ALL
    USING (department_id = current_setting('app.current_department_id')::integer);

-- HR can see all but only update non-sensitive fields
CREATE POLICY hr_policy ON employees
    FOR SELECT
    USING (true)
    WITH CHECK (current_user = 'hr_user');

-- Create department-specific roles
CREATE ROLE sales_dept NOLOGIN;
CREATE ROLE marketing_dept NOLOGIN;

GRANT CONNECT ON DATABASE company_db TO sales_dept, marketing_dept;
GRANT USAGE ON SCHEMA sales TO sales_dept;
GRANT USAGE ON SCHEMA marketing TO marketing_dept;

GRANT SELECT ON ALL TABLES IN SCHEMA sales TO sales_dept;
GRANT SELECT ON ALL TABLES IN SCHEMA marketing TO marketing_dept;

-- Assign department roles
GRANT sales_dept TO sales_user1, sales_user2;
GRANT marketing_dept TO marketing_user1, marketing_user2;

-- View role hierarchy
SELECT * FROM pg_roles WHERE rolname LIKE '%role%' OR rolname LIKE '%dept%';
SELECT * FROM pg_auth_members;
