<h3>Unified Permission Management Systems</h3>
<div style="background-color: #e0e0e0; padding: 20px; margin: 15px 0; border-radius: 10px;">
    <h4>ğŸ”— LDAP/Active Directory Integration</h4>
    
    <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
# /etc/sssd/sssd.conf - LDAP integration for Linux
[sssd]
domains = company.local
config_file_version = 2
services = nss, pam, sudo

[domain/company.local]
id_provider = ldap
auth_provider = ldap
ldap_uri = ldap://dc1.company.local, ldap://dc2.company.local
ldap_search_base = dc=company,dc=local
ldap_default_bind_dn = cn=sssd,ou=ServiceAccounts,dc=company,dc=local
ldap_default_authtok = password
ldap_tls_reqcert = demand
ldap_tls_cacert = /etc/ssl/certs/company-ca.crt

# Group mapping from AD
ldap_group_member = member
ldap_group_object_class = group
ldap_group_name = cn
ldap_group_search_base = ou=Groups,dc=company,dc=local

# User mapping
ldap_user_object_class = user
ldap_user_name = sAMAccountName
ldap_user_fullname = displayName
ldap_user_home_directory = unixHomeDirectory
ldap_user_shell = loginShell

# Sudo integration
ldap_sudo_search_base = ou=SUDOers,dc=company,dc=local
ldap_sudo_full_refresh_interval = 3600
ldap_sudo_smart_refresh_interval = 1800

# /etc/nsswitch.conf changes
passwd: compat sss
group: compat sss
sudoers: files sss

# /etc/pam.d/common-session
session optional pam_mkhomedir.so skel=/etc/skel umask=0077

# /etc/sudoers.d/ldap
# Include sudoers from LDAP
%sudoers ALL=(ALL:ALL) ALL

# Mapping AD groups to Linux groups
# /etc/security/group.conf
*;*;*;Al0000-2400;admins,cdrom,dip,plugdev

# Script to sync AD groups to local
#!/bin/bash
# sync_ad_groups.sh
for group in $(ldapsearch -x -H ldap://dc1.company.local \
              -b "ou=Groups,dc=company,dc=local" \
              "(&(objectClass=group)(cn=linux_*))" cn | grep "^cn:" | cut -d' ' -f2); do
    
    # Remove linux_ prefix
    local_group=\\\${group#linux_}
    
    if ! getent group "$local_group" >/dev/null; then
        sudo groupadd "$local_group"
        echo "Created group: $local_group"
    fi
    
    # Get members from AD
    members=$(ldapsearch -x -H ldap://dc1.company.local \
              -b "cn=$group,ou=Groups,dc=company,dc=local" \
              member | grep "^member:" | cut -d' ' -f2-)
    
    for member in $members; do
        # Extract username from DN
        username=$(echo "$member" | grep -o "cn=[^,]*" | cut -d= -f2)
        if id "$username" &>/dev/null; then
            sudo usermod -a -G "$local_group" "$username"
            echo "  Added $username to $local_group"
        fi
    done
done
    </pre>
    
    <h4>ğŸ”„ Single Sign-On (SSO) Integration</h4>
    <pre style="background: #f5f5f5; padding: 15px; border-radius: 8px;">
# SAML/OAuth2 configuration examples

# Apache with mod_auth_mellon (SAML)
<VirtualHost *:443>
    ServerName app.company.local
    
    MellonEnable "info"
    MellonSPPrivateKeyFile /etc/apache2/mellon/sp.key
    MellonSPCertFile /etc/apache2/mellon/sp.crt
    MellonSPMetadataFile /etc/apache2/mellon/sp.xml
    MellonIdPMetadataFile /etc/apache2/mellon/idp.xml
    
    MellonEndpointPath "/mellon"
    MellonSecureCookie On
    MellonCookieSameSite none
    
    # Map SAML attributes to headers
    MellonSetEnv noPrefix
    MellonVariable "sp-cookie"
    
    # Role mapping from SAML
    MellonSetEnv REMOTE_USER_NAME uid
    MellonSetEnv REMOTE_USER_EMAIL mail
    MellonSetEnv REMOTE_USER_GROUPS groups
    
    <Location "/">
        AuthType Mellon
        MellonEnable auth
        Require valid-user
    </Location>
    
    <Location "/admin">
        AuthType Mellon
        MellonEnable auth
        Require claim groups:Admin
    </Location>
    
    <Location "/manager">
        AuthType Mellon
        MellonEnable auth
        Require claim groups:Manager
    </Location>
</VirtualHost>

# Nginx with OAuth2 proxy
server {
    listen 443 ssl;
    server_name app.company.local;
    
    location / {
        # OAuth2 proxy
        auth_request /oauth2/auth;
        error_page 401 = /oauth2/sign_in;
        
        # Pass user info to backend
        auth_request_set $user $upstream_http_x_auth_request_user;
        auth_request_set $email $upstream_http_x_auth_request_email;
        auth_request_set $groups $upstream_http_x_auth_request_groups;
        
        proxy_set_header X-User $user;
        proxy_set_header X-Email $email;
        proxy_set_header X-Groups $groups;
        
        proxy_pass http://backend:8080;
    }
    
    location /oauth2/ {
        proxy_pass http://oauth2-proxy:4180;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Scheme $scheme;
    }
    
    # Role-based locations
    location ~ ^/(admin|config) {
        # Check for admin group
        auth_request /oauth2/auth;
        auth_request_set $groups $upstream_http_x_auth_request_groups;
        
        # Validate group membership
        if ($groups !~* "admin") {
            return 403;
        }
        
        proxy_pass http://backend:8080;
    }
}

# Keycloak/OIDC configuration
# /etc/keycloak.conf
realm=company
auth-server-url=https://sso.company.local/auth
ssl-required=external
resource=company-app
public-client=true

# Role mapping in application
@RolesAllowed({"admin", "manager"})
@Path("/api/admin")
public class AdminResource {
    // Only admin/manager can access
}

@PermitAll
@Path("/api/public")
public class PublicResource {
    // Everyone can access
}

@RolesAllowed("employee")
@Path("/api/internal")
public class InternalResource {
    // Only employees can access
}
    </pre>
