    <b>Task 2: Implement Cross-Server Role Synchronization (60 minutes)</b>
    
    <p>Create scripts to synchronize roles across multiple servers:</p>
    <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
#!/bin/bash
# sync_roles.sh - Synchronize user roles across servers

# Configuration
declare -A SERVERS=(
    [fileserver]="192.168.1.10"
    [webserver]="192.168.1.11"
    [dbserver]="192.168.1.12"
    [cluster]="192.168.1.13"
)

# Role definitions
declare -A ROLE_PERMISSIONS=(
    [student]="course_access submission_access grade_view"
    [ta]="student grading course_moderation"
    [professor]="ta course_management enrollment grade_submission"
    [dept_chair]="professor department_oversight budget"
    [it_admin]="user_management system_maintenance backup security"
    [registrar]="enrollment transcript degree_audit"
    [researcher]="library lab"
)

# User database (simulated)
declare -A USER_ROLES=(
    [alice]="student"
    [bob]="ta"
    [carol]="professor"
    [dave]="dept_chair"
    [eve]="it_admin"
    [frank]="registrar"
    [grace]="researcher"
)

# Function to apply permissions on file server
apply_fileserver_permissions() {
    local user=$1
    local role=$2
    
    echo "Applying file server permissions for $user as $role"
    
    case $role in
        student)
            ssh \\\${SERVERS[fileserver]} "
                sudo setfacl -m u:$user:r-x /srv/university/courses
                sudo setfacl -m u:$user:rwx /srv/university/submissions/$user
                sudo setfacl -m u:$user:r-- /srv/university/grades/$user
            "
            ;;
        professor)
            ssh \\\${SERVERS[fileserver]} "
                sudo setfacl -R -m u:$user:rwx /srv/university/courses/assigned
                sudo setfacl -m u:$user:r-x /srv/university/submissions
                sudo setfacl -m u:$user:rw- /srv/university/grades
            "
            ;;
        # Add other roles...
    esac
}

# Function to apply permissions on web server
apply_webserver_permissions() {
    local user=$1
    local role=$2
    
    echo "Applying web server permissions for $user as $role"
    
    # Create Apache .htpasswd entry
    ssh \\\${SERVERS[webserver]} "
        if ! grep -q '^$user:' /etc/apache2/.htpasswd-university; then
            htpasswd -b /etc/apache2/.htpasswd-university $user 'TempPass123'
        fi
    "
    
    # Add to appropriate groups in web server
    case $role in
        student)
            ssh \\\${SERVERS[webserver]} "echo '$user' >> /etc/apache2/groups/students"
            ;;
        professor)
            ssh \\\${SERVERS[webserver]} "
                echo '$user' >> /etc/apache2/groups/professors
                echo '$user' >> /etc/apache2/groups/faculty
            "
            ;;
    esac
}

# Function to apply database permissions
apply_database_permissions() {
    local user=$1
    local role=$2
    
    echo "Applying database permissions for $user as $role"
    
    case $role in
        student)
            ssh \\\${SERVERS[dbserver]} "
                mysql -e \"GRANT SELECT ON university.student_courses TO '$user'@'%';"
                mysql -e \"GRANT SELECT ON university.assignments TO '$user'@'%';"
            "
            ;;
        professor)
            ssh \\\${SERVERS[dbserver]} "
                mysql -e \"GRANT SELECT, INSERT, UPDATE ON university.* TO '$user'@'%';"
                mysql -e \"GRANT SELECT ON university.grades TO '$user'@'%';"
            "
            ;;
    esac
}

# Main synchronization loop
main() {
    echo "=== Starting Role Synchronization ==="
    echo "Time: $(date)"
    echo ""
    
    for user in "\\\${!USER_ROLES[@]}"; do
        role="\\\${USER_ROLES[$user]}"
        echo "Processing: $user -> $role"
        
        # Apply permissions on each server
        apply_fileserver_permissions "$user" "$role"
        apply_webserver_permissions "$user" "$role"
        apply_database_permissions "$user" "$role"
        
        echo ""
    done
    
    echo "=== Synchronization Complete ==="
    
    # Generate report
    echo "=== Permission Report ==="
    for server in "\\\${!SERVERS[@]}"; do
        echo "Server: $server (\\\${SERVERS[$server]})"
        # Could add verification commands here
        echo ""
    done
}

# Run main function
main
    </pre>
