# Complete Windows firewall script
@echo off
echo Configuring Windows Firewall...

REM Enable firewall for all profiles
netsh advfirewall set allprofiles state on

REM Set default policies
netsh advfirewall set allprofiles firewallpolicy blockinbound,allowoutbound

REM Allow essential services
netsh advfirewall firewall add rule name="ICMPv4-In" protocol=icmpv4:any,any dir=in action=allow
netsh advfirewall firewall add rule name="ICMPv4-Out" protocol=icmpv4:any,any dir=out action=allow

REM Allow DNS
netsh advfirewall firewall add rule name="DNS-UDP-In" protocol=udp localport=53 dir=in action=allow
netsh advfirewall firewall add rule name="DNS-TCP-In" protocol=tcp localport=53 dir=in action=allow

REM Allow DHCP
netsh advfirewall firewall add rule name="DHCP-In" protocol=udp localport=67-68 dir=in action=allow
netsh advfirewall firewall add rule name="DHCP-Out" protocol=udp localport=67-68 dir=out action=allow

REM Allow HTTP/HTTPS
netsh advfirewall firewall add rule name="HTTP-In" protocol=tcp localport=80 dir=in action=allow
netsh advfirewall firewall add rule name="HTTPS-In" protocol=tcp localport=443 dir=in action=allow

REM Allow RDP from specific subnet
netsh advfirewall firewall add rule name="RDP-In" protocol=tcp localport=3389 dir=in action=allow remoteip=192.168.1.0/24

REM Block malicious ports
netsh advfirewall firewall add rule name="Block-SMB" protocol=tcp localport=445 dir=in action=block
netsh advfirewall firewall add rule name="Block-NetBIOS" protocol=udp localport=137-138 dir=in action=block
netsh advfirewall firewall add rule name="Block-RPC" protocol=tcp localport=135 dir=in action=block

REM Logging
netsh advfirewall set allprofiles logging filename %systemroot%\system32\LogFiles\Firewall\pfirewall.log
netsh advfirewall set allprofiles logging maxfilesize 4096
netsh advfirewall set allprofiles logging droppedconnections enable
netsh advfirewall set allprofiles logging successfulconnections enable

echo Firewall configuration complete!
pause
    </pre>
