# Install nmap
sudo apt install nmap        # Ubuntu/Debian
sudo yum install nmap        # CentOS/RHEL
brew install nmap            # macOS

# Basic scans
nmap 192.168.1.1                    # Default scan (top 1000 ports)
nmap 192.168.1.0/24                 # Scan entire subnet
nmap -sP 192.168.1.0/24             # Ping scan only
nmap -p 1-65535 192.168.1.1         # All ports
nmap -p 22,80,443 192.168.1.1       # Specific ports

# Advanced scan types
nmap -sS 192.168.1.1                # SYN stealth scan
nmap -sT 192.168.1.1                # TCP connect scan
nmap -sU 192.168.1.1                # UDP scan
nmap -sN 192.168.1.1                # NULL scan (no flags)
nmap -sF 192.168.1.1                # FIN scan
nmap -sX 192.168.1.1                # XMAS scan (FIN,URG,PSH)

# Service/version detection
nmap -sV 192.168.1.1                # Version detection
nmap -sV --version-intensity 9 192.168.1.1  # Aggressive version detection
nmap -A 192.168.1.1                 # Aggressive scan (OS, version, script)

# Timing and evasion
nmap -T0 192.168.1.1                # Paranoid (very slow)
nmap -T1 192.168.1.1                # Sneaky
nmap -T2 192.168.1.1                # Polite
nmap -T3 192.168.1.1                # Normal (default)
nmap -T4 192.168.1.1                # Aggressive
nmap -T5 192.168.1.1                # Insane
nmap -f 192.168.1.1                 # Fragment packets
nmap --data-length 100 192.168.1.1  # Append random data

# Script scanning
nmap --script=safe 192.168.1.1      # Safe scripts only
nmap --script=vuln 192.168.1.1      # Vulnerability scripts
nmap --script=auth 192.168.1.1      # Authentication scripts
nmap --script=brute 192.168.1.1     # Brute force scripts

# Output formats
nmap -oN scan.txt 192.168.1.1       # Normal output
nmap -oX scan.xml 192.168.1.1       # XML output
nmap -oG scan.gnmap 192.168.1.1     # Grepable output
nmap -oA scan 192.168.1.1           # All formats

# Useful scripts
nmap --script=http-title 192.168.1.1                # Get webpage title
nmap --script=ssl-enum-ciphers -p 443 192.168.1.1   # Check SSL ciphers
nmap --script=vulners 192.168.1.1                   # Check for known vulnerabilities
nmap --script=smb-os-discovery 192.168.1.1          # SMB OS discovery
