#!/bin/bash
# firewall_test.sh
TARGET="localhost"

echo "=== Testing Firewall Rules ==="
echo "Target: $TARGET"
echo ""

# Test 1: Standard TCP scan
echo "1. Standard TCP Connect Scan:"
nmap -sT -p 1-100 $TARGET | grep -E "(open|filtered|closed)"
echo ""

# Test 2: SYN stealth scan
echo "2. SYN Stealth Scan:"
nmap -sS -p 22,80,443 $TARGET | grep -E "(open|filtered)"
echo ""

# Test 3: UDP scan (if firewall allows)
echo "3. UDP Scan (common services):"
nmap -sU -p 53,123,161 $TARGET 2>/dev/null | grep -E "(open|filtered)"
echo ""

# Test 4: ACK scan (to detect stateful filtering)
echo "4. ACK Scan (stateful firewall test):"
nmap -sA -p 80 $TARGET | grep "unfiltered"
echo ""

# Test 5: Window scan (older systems)
echo "5. Window Scan:"
nmap -sW -p 1-100 $TARGET 2>/dev/null | grep -E "(open|closed)"
echo ""

# Test 6: NULL scan
echo "6. NULL Scan (should be filtered):"
nmap -sN -p 22 $TARGET | grep -E "(open|filtered|closed)"
echo ""

# Test 7: FIN scan
echo "7. FIN Scan:"
nmap -sF -p 22 $TARGET | grep -E "(open|filtered|closed)"
echo ""

# Test 8: XMAS scan
echo "8. XMAS Scan:"
nmap -sX -p 22 $TARGET | grep -E "(open|filtered|closed)"
echo ""

# Test 9: Fragmentation (evasion)
echo "9. Fragmented Packet Scan:"
nmap -f -p 22 $TARGET 2>/dev/null | grep -E "(open|filtered)"
echo ""

# Test 10: Timing manipulation
echo "10. Slow Scan (T1):"
nmap -T1 -p 22,80,443 $TARGET | grep -E "(open|filtered)"
echo ""

echo "=== Analysis ==="
echo "Based on scan results:"
echo "- Which ports are truly open?"
echo "- Which ports are filtered by firewall?"
echo "- Does firewall perform stateful inspection?"
echo "- Can scanning be detected based on responses?"
    </pre>
