<h3>Part 1: Basic iptables Configuration</h3>
<div style="background-color: #fff0e6; padding: 20px; margin: 15px 0; border-radius: 10px;">
    <h4>ğŸ§ Step-by-Step iptables Setup</h4>
    
    <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
#!/bin/bash
# basic_firewall_setup.sh
echo "=== Basic Firewall Setup ==="
echo "Starting at: $(date)"
echo ""

# Function to print status
print_status() {
    echo "âœ“ $1"
}

# Backup current rules
echo "1. Backing up current rules..."
sudo iptables-save > /lab/week10/firewall/backup_$(date +%Y%m%d_%H%M%S).rules
print_status "Rules backed up"

# Flush all existing rules
echo ""
echo "2. Flushing existing rules..."
sudo iptables -F
sudo iptables -X
sudo iptables -Z
print_status "Rules flushed"

# Set default policies
echo ""
echo "3. Setting default policies..."
sudo iptables -P INPUT DROP
sudo iptables -P FORWARD DROP
sudo iptables -P OUTPUT ACCEPT
print_status "Default policies set: INPUT DROP, FORWARD DROP, OUTPUT ACCEPT"

# Allow loopback interface
echo ""
echo "4. Configuring loopback interface..."
sudo iptables -A INPUT -i lo -j ACCEPT
sudo iptables -A OUTPUT -o lo -j ACCEPT
print_status "Loopback traffic allowed"

# Allow established connections
echo ""
echo "5. Allowing established connections..."
sudo iptables -A INPUT -m state --state ESTABLISHED,RELATED -j ACCEPT
print_status "Established connections allowed"

# Allow ICMP (ping)
echo ""
echo "6. Configuring ICMP..."
sudo iptables -A INPUT -p icmp --icmp-type echo-request -j ACCEPT
sudo iptables -A INPUT -p icmp --icmp-type echo-reply -j ACCEPT
sudo iptables -A INPUT -p icmp --icmp-type destination-unreachable -j ACCEPT
sudo iptables -A INPUT -p icmp --icmp-type time-exceeded -j ACCEPT
print_status "ICMP traffic allowed"

# Allow SSH with restrictions
echo ""
echo "7. Configuring SSH access..."
# Allow SSH from teacher network
sudo iptables -A INPUT -p tcp --dport 22 -s 192.168.1.0/24 -j ACCEPT
# Rate limiting for SSH
sudo iptables -A INPUT -p tcp --dport 22 -m state --state NEW \
    -m recent --set --name SSH
sudo iptables -A INPUT -p tcp --dport 22 -m state --state NEW \
    -m recent --update --seconds 60 --hitcount 4 --name SSH -j DROP
print_status "SSH configured (restricted to teacher network, rate limited)"

# Allow HTTP/HTTPS
echo ""
echo "8. Configuring web services..."
sudo iptables -A INPUT -p tcp --dport 80 -j ACCEPT
sudo iptables -A INPUT -p tcp --dport 443 -j ACCEPT
print_status "HTTP (80) and HTTPS (443) allowed"

# Allow DNS
echo ""
echo "9. Configuring DNS..."
sudo iptables -A INPUT -p udp --dport 53 -j ACCEPT
sudo iptables -A INPUT -p tcp --dport 53 -j ACCEPT
print_status "DNS (UDP/TCP 53) allowed"

# Allow custom API port
echo ""
echo "10. Configuring custom API..."
sudo iptables -A INPUT -p tcp --dport 8080 -m iprange --src-range 192.168.1.100-192.168.1.200 -j ACCEPT
print_status "API (8080) allowed from student IP range"

# Block dangerous ports
echo ""
echo "11. Blocking dangerous ports..."
DANGEROUS_PORTS="21 23 135 137 138 139 445 3306"
for port in $DANGEROUS_PORTS; do
    sudo iptables -A INPUT -p tcp --dport $port -j DROP
    sudo iptables -A INPUT -p udp --dport $port -j DROP 2>/dev/null || true
done
print_status "Dangerous ports blocked: $DANGEROUS_PORTS"

# Logging
echo ""
echo "12. Configuring logging..."
sudo iptables -N LOGGING
sudo iptables -A INPUT -j LOGGING
sudo iptables -A LOGGING -m limit --limit 2/min -j LOG --log-prefix "IPTables-Dropped: " --log-level 4
sudo iptables -A LOGGING -j DROP
print_status "Logging configured for dropped packets"

# Protection against port scans
echo ""
echo "13. Adding port scan protection..."
sudo iptables -A INPUT -p tcp --tcp-flags ALL FIN,URG,PSH -j DROP
sudo iptables -A INPUT -p tcp --tcp-flags ALL ALL -j DROP
sudo iptables -A INPUT -p tcp --tcp-flags ALL NONE -j DROP
sudo iptables -A INPUT -p tcp --tcp-flags SYN,FIN SYN,FIN -j DROP
sudo iptables -A INPUT -p tcp --tcp-flags SYN,RST SYN,RST -j DROP
print_status "Port scan protection added"

# Save rules
echo ""
echo "14. Saving rules..."
sudo iptables-save > /etc/iptables/rules.v4
print_status "Rules saved to /etc/iptables/rules.v4"

# Display final configuration
echo ""
echo "=== Final Configuration ==="
sudo iptables -L -n -v --line-numbers

echo ""
echo "=== Setup Complete ==="
echo "Firewall rules configured successfully!"
echo "Rules saved to: /etc/iptables/rules.v4"
echo "Backup saved in: /lab/week10/firewall/"
