#!/bin/bash
# firewall_monitoring.sh

echo "=== Firewall Monitoring Dashboard ==="
echo "Last updated: $(date)"
echo ""

# Show recent drops
echo "1. Recent Dropped Packets (last 10):"
sudo iptables -L INPUT -n -v | grep DROP | head -10
echo ""

# Show hit counts for rules
echo "2. Rule Hit Counts:"
sudo iptables -L INPUT -n -v --line-numbers | awk '{print NR, $1, $2, $7}' | head -20
echo ""

# Check for brute force attempts
echo "3. Brute Force Detection:"
for chain in $(sudo iptables -L -n | grep -E "brute_[0-9]+" | awk '{print $1}'); do
    echo "Chain $chain:"
    sudo iptables -L $chain -n -v
    echo ""
done

# Monitor port scan attempts
echo "4. Port Scan Detection:"
sudo iptables -L PORT_SCAN_DETECT -n -v
echo ""

# Connection tracking
echo "5. Current Connections:"
sudo conntrack -L 2>/dev/null | head -10 || echo "conntrack not available"
echo ""

# Log analysis
echo "6. Firewall Logs (last 5 entries):"
sudo dmesg | grep -i "iptables\|firewall" | tail -5
echo ""

# Service status
echo "7. Protected Services Status:"
for port in 22 80 443 53; do
    count=$(sudo netstat -tulpn | grep ":$port " | wc -l)
    status=$([ $count -gt 0 ] && echo "âœ“ Running" || echo "âœ— Not listening")
    echo "Port $port: $status"
done
echo ""

# Generate report
echo "8. Generating monitoring report..."
REPORT_FILE="/lab/week10/firewall/monitoring_report_$(date +%Y%m%d_%H%M).txt"
{
    echo "Firewall Monitoring Report"
    echo "Generated: $(date)"
    echo "================================="
    echo ""
    echo "Current Rules:"
    sudo iptables -L -n -v
    echo ""
    echo "Connection Counts:"
    sudo netstat -tulpn | grep -v "127.0.0.1"
} > "$REPORT_FILE"

echo "Report saved to: $REPORT_FILE"
echo ""
echo "=== Monitoring Complete ==="
