            <h5>PowerShell Firewall Script</h5>
            <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
# Windows_Firewall_Setup.ps1
Write-Host "=== Windows Firewall Configuration ===" -ForegroundColor Cyan
Write-Host "Starting at: $(Get-Date)"
Write-Host ""

# Backup current rules
Write-Host "1. Backing up current rules..." -ForegroundColor Yellow
$backupFile = "C:\Firewall_Backup_$(Get-Date -Format 'yyyyMMdd_HHmm').xml"
netsh advfirewall export $backupFile
Write-Host "Backup saved to: $backupFile" -ForegroundColor Green

# Enable firewall for all profiles
Write-Host "2. Enabling firewall..." -ForegroundColor Yellow
Set-NetFirewallProfile -Profile Domain,Public,Private -Enabled True

# Set default policies
Write-Host "3. Setting default policies..." -ForegroundColor Yellow
Set-NetFirewallProfile -Profile Domain,Public,Private -DefaultInboundAction Block
Set-NetFirewallProfile -Profile Domain,Public,Private -DefaultOutboundAction Allow

# Allow essential services
Write-Host "4. Configuring essential services..." -ForegroundColor Yellow

# ICMP (ping)
New-NetFirewallRule -DisplayName "Allow ICMPv4" -Direction Inbound -Protocol ICMPv4 -IcmpType 8 -Action Allow

# DNS
New-NetFirewallRule -DisplayName "Allow DNS UDP" -Direction Inbound -Protocol UDP -LocalPort 53 -Action Allow
New-NetFirewallRule -DisplayName "Allow DNS TCP" -Direction Inbound -Protocol TCP -LocalPort 53 -Action Allow

# DHCP
New-NetFirewallRule -DisplayName "Allow DHCP" -Direction Inbound -Protocol UDP -LocalPort 67-68 -Action Allow

# Allow HTTP/HTTPS
Write-Host "5. Configuring web services..." -ForegroundColor Yellow
New-NetFirewallRule -DisplayName "Allow HTTP" -Direction Inbound -Protocol TCP -LocalPort 80 -Action Allow
New-NetFirewallRule -DisplayName "Allow HTTPS" -Direction Inbound -Protocol TCP -LocalPort 443 -Action Allow

# Allow SSH (if installed)
New-NetFirewallRule -DisplayName "Allow SSH" -Direction Inbound -Protocol TCP -LocalPort 22 -Action Allow

# Block dangerous ports
Write-Host "6. Blocking dangerous ports..." -ForegroundColor Yellow
$dangerousPorts = @(21, 23, 135, 137, 138, 139, 445, 3389, 5900)
foreach ($port in $dangerousPorts) {
    New-NetFirewallRule -DisplayName "Block TCP $port" -Direction Inbound -Protocol TCP -LocalPort $port -Action Block
    New-NetFirewallRule -DisplayName "Block UDP $port" -Direction Inbound -Protocol UDP -LocalPort $port -Action Block
}

# Allow specific programs
Write-Host "7. Configuring application rules..." -ForegroundColor Yellow
# Example: Allow Chrome
$chromePath = "C:\Program Files\Google\Chrome\Application\chrome.exe"
if (Test-Path $chromePath) {
    New-NetFirewallRule -DisplayName "Allow Chrome" -Direction Outbound -Program $chromePath -Action Allow
}

# Enable logging
Write-Host "8. Configuring logging..." -ForegroundColor Yellow
Set-NetFirewallProfile -Profile Domain,Public,Private -LogFileName %systemroot%\system32\LogFiles\Firewall\pfirewall.log
Set-NetFirewallProfile -Profile Domain,Public,Private -LogMaxSizeKilobytes 4096
Set-NetFirewallProfile -Profile Domain,Public,Private -LogAllowed True
Set-NetFirewallProfile -Profile Domain,Public,Private -LogBlocked True

# Display configuration
Write-Host "9. Displaying configuration..." -ForegroundColor Yellow
Get-NetFirewallProfile | Format-List Name, Enabled, DefaultInboundAction, DefaultOutboundAction

Write-Host ""
Write-Host "=== Windows Firewall Configuration Complete ===" -ForegroundColor Green
Write-Host "Backup file: $backupFile"
Write-Host "Log file: %systemroot%\system32\LogFiles\Firewall\pfirewall.log"
            </pre>
