#!/bin/bash
# firewall_test_suite.sh

echo "=== Firewall Testing Suite ==="
echo "Target: localhost"
echo "Time: $(date)"
echo ""

test_port () {
    local port=$1
    local proto=$2
    local expected=$3

    echo -n "Testing $proto/$port: "

    if [ "$proto" = "tcp" ]; then
        timeout 2 nc -z localhost $port &>/dev/null
    else
        timeout 2 nc -zu localhost $port &>/dev/null
    fi

    if [ $? -eq 0 ]; then
        actual="open"
    else
        actual="closed"
    fi

    if [ "$actual" = "$expected" ]; then
        echo "âœ“ PASS (expected $expected)"
        return 0
    else
        echo "âœ— FAIL (expected $expected, got $actual)"
        return 1
    fi
}

echo "1. Required Services"
test_port 22 tcp open
test_port 80 tcp open
test_port 443 tcp open
test_port 53 udp open
test_port 53 tcp open

echo ""
echo "2. Blocked Services"
test_port 21 tcp closed
test_port 23 tcp closed
test_port 445 tcp closed

echo ""
echo "=== Testing Complete ==="


