                <h3>Windows Content Filtering</h3>
                <div style="background-color: #e8f5e8; padding: 20px; margin: 15px 0; border-radius: 10px;">
                    <h4>ğŸªŸ Windows Filtering Methods</h4>
                    
                    <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px; margin-top: 15px;">
                        <div>
                            <h5>Method 1: hosts File (Windows)</h5>
                            <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
                # Windows hosts file location:
                # C:\Windows\System32\drivers\etc\hosts

                # Block social media sites
                127.0.0.1 www.facebook.com
                127.0.0.1 facebook.com
                127.0.0.1 www.youtube.com
                127.0.0.1 youtube.com
                127.0.0.1 www.twitter.com
                127.0.0.1 twitter.com
                127.0.0.1 www.instagram.com
                127.0.0.1 instagram.com

                # Block gaming sites
                127.0.0.1 www.steampowered.com
                127.0.0.1 steampowered.com

                # PowerShell script to manage hosts file
                # block_site.ps1
                param(
                    [string]$Site,
                    [switch]$Unblock
                )

                $hostsPath = "$env:SystemRoot\System32\drivers\etc\hosts"
                $hostsContent = Get-Content $hostsPath

                if ($Unblock) {
                    Write-Host "Unblocking $Site..." -ForegroundColor Yellow
                    $newContent = $hostsContent | Where-Object { $_ -notmatch $Site }
                } else {
                    Write-Host "Blocking $Site..." -ForegroundColor Yellow
                    if ($hostsContent -match $Site) {
                        Write-Host "$Site is already blocked" -ForegroundColor Red
                        exit
                    }
                    $newContent = $hostsContent + "'n127.0.0.1 $Site'n127.0.0.1 www.$Site"
                }

                # Requires administrator privileges
                try {
                    Set-Content -Path $hostsPath -Value $newContent -Force
                    Write-Host "Operation completed successfully" -ForegroundColor Green
                    
                    # Clear DNS cache
                    ipconfig /flushdns
                } catch {
                    Write-Host "Error: $_" -ForegroundColor Red
                    Write-Host "Run PowerShell as Administrator" -ForegroundColor Red
                }

                # Usage:
                # Run as Administrator
                # .\block_site.ps1 -Site facebook.com
                # .\block_site.ps1 -Site facebook.com -Unblock
