                            <h5>Method 2: Windows Firewall Rules</h5>
                            <pre style="background: #36454f; color: white; padding: 15px; border-radius: 8px;">
                # PowerShell: Block applications
                # Block specific executables

                # Block Chrome during work hours
                $ruleName = "Block Chrome Work Hours"
                $chromePath = "C:\Program Files\Google\Chrome\Application\chrome.exe"

                # Check if rule exists
                $existingRule = Get-NetFirewallRule -DisplayName $ruleName -ErrorAction SilentlyContinue

                if (-not $existingRule) {
                    # Create time-based rule
                    New-NetFirewallRule '
                        -DisplayName $ruleName '
                        -Program $chromePath '
                        -Direction Outbound '
                        -Action Block '
                        -Enabled True '
                        -Profile Any '
                        -Description "Blocks Chrome during work hours (9-5 Mon-Fri)"
                    
                    # Note: Windows Firewall doesn't support time-based rules natively
                    # You would need to enable/disable via scheduled task
                }

                # Scheduled task to enable/disable rule
                $action = New-ScheduledTaskAction -Execute 'powershell.exe' '
                    -Argument "-Command '"Enable-NetFirewallRule -DisplayName '$ruleName''""

                $trigger = New-ScheduledTaskTrigger -Daily -At "5:00PM"

                Register-ScheduledTask '
                    -TaskName "Enable Chrome After Hours" '
                    -Action $action '
                    -Trigger $trigger '
                    -Description "Enables Chrome access after work hours"

                # Block by IP address (if known)
                $blockedIPs = @(
                    "31.13.64.0/24",    # Facebook
                    "157.240.0.0/16",   # More Facebook
                    "172.217.0.0/16",   # Google
                    "104.244.42.0/24"   # Twitter
                )

                foreach ($ip in $blockedIPs) {
                    $ruleName = "Block IP Range $ip"
                    New-NetFirewallRule '
                        -DisplayName $ruleName '
                        -Direction Outbound '
                        -RemoteAddress $ip '
                        -Action Block '
                        -Protocol Any '
                        -Enabled True
                }

                # Family Safety / Parental Controls
                # Windows 10/11 includes built-in content filtering
                # Settings â†’ Accounts â†’ Family & other users â†’ Family safety

                # Configure via PowerShell
                # Install Family Safety module
                Install-Module -Name Microsoft.Family.Safety

                # Set up filtering
                Set-MsolUser -UserPrincipalName student@school.edu '
                    -StrongPasswordRequired $true '
                    -BlockCredential $false

                # Note: Enterprise versions have more advanced controls
                # through Group Policy and Intune
