                    </div>
                    
                    <div style="background: white; padding: 15px; border-radius: 8px; margin: 15px 0;">
                        <h5>Step 2: Configure Storage & Shares</h5>
                        <pre style="background: #f5f5f5; padding: 15px; border-radius: 5px;">
                # Initialize and format data disk
                Initialize-Disk -Number 1 -PartitionStyle GPT
                New-Partition -DiskNumber 1 -UseMaximumSize -DriveLetter D
                Format-Volume -DriveLetter D -FileSystem NTFS -NewFileSystemLabel "Data" -Confirm:$false

                # Create folder structure
                New-Item -Path "D:\Shares" -ItemType Directory
                New-Item -Path "D:\Shares\Sales" -ItemType Directory
                New-Item -Path "D:\Shares\Engineering" -ItemType Directory
                New-Item -Path "D:\Shares\Administration" -ItemType Directory
                New-Item -Path "D:\HomeFolders" -ItemType Directory

                # Create shared folders
                New-SmbShare -Name "Sales" -Path "D:\Shares\Sales" -FullAccess "SG_Sales_RW"
                New-SmbShare -Name "Engineering" -Path "D:\Shares\Engineering" -FullAccess "SG_Engineering_RW"
                New-SmbShare -Name "Admin" -Path "D:\Shares\Administration" -FullAccess "SG_Admin_Full"
                New-SmbShare -Name "Home$" -Path "D:\HomeFolders" -FullAccess "Administrators"

                # Set NTFS permissions
                $acl = Get-Acl "D:\Shares\Sales"
                $accessRule = New-Object System.Security.AccessControl.FileSystemAccessRule("SG_Sales_RW","FullControl","Allow")
                $acl.SetAccessRule($accessRule)
                Set-Acl -Path "D:\Shares\Sales" -AclObject $acl
                        </pre>
                    </div>
                    
                    <div style="background: white; padding: 15px; border-radius: 8px; margin: 15px 0;">
                        <h5>Step 3: Configure Quotas & Shadow Copies</h5>
