                vssadmin Add ShadowStorage /For=D: /On=D: /MaxSize=10GB

                # Schedule shadow copies
                schtasks /create /tn "ShadowCopySchedule" /tr "vssadmin create shadow /for=D:" /sc daily /st 18:00

