        </tr>
    </table>
    
    <hr/>
    
    <b>b. Adding Event Listeners</b>
    
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Three Ways to Add Event Listeners:</b><br/>
        <pre>
            // Method 1: HTML attribute (not recommended)<br/>
            &lt;button onclick="handleClick()"&gt;Click me&lt;/button&gt;<br/>
            <br/>
            // Method 2: DOM property (limited - one handler per event)<br/>
            const button = document.querySelector('button');<br/>
            button.onclick = function() {<br/>
            &nbsp;&nbsp;console.log('Clicked!');<br/>
            };<br/>
            <br/>
            // Method 3: addEventListener (recommended)<br/>
            button.addEventListener('click', function(event) {<br/>
            &nbsp;&nbsp;console.log('Clicked!', event);<br/>
            });<br/>
            <br/>
            // With arrow function<br/>
            button.addEventListener('click', (event) => {<br/>
            &nbsp;&nbsp;console.log('Clicked!', event);<br/>
            });<br/>
            <br/>
            // With named function<br/>
            function handleClick(event) {<br/>
            &nbsp;&nbsp;console.log('Clicked!', event);<br/>
            }<br/>
            button.addEventListener('click', handleClick);<br/>
            <br/>
            // Multiple events<br/>
            button.addEventListener('click', handleClick);<br/>
            button.addEventListener('mouseenter', handleMouseEnter);<br/>
            button.addEventListener('mouseleave', handleMouseLeave);<br/>
            <br/>
            // Multiple handlers for same event<br/>
            button.addEventListener('click', firstHandler);<br/>
            button.addEventListener('click', secondHandler); // Both run!
        </pre>
    </div>
    
    <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px; margin: 20px 0;">
        <div style="background-color: #f0f8ff; padding: 15px; border-radius: 5px;">
            <b>Removing Event Listeners:</b><br/>
            <pre>
                // Must use named function to remove<br/>
                function handleClick(event) {<br/>
                &nbsp;&nbsp;console.log('Clicked!');<br/>
                }<br/>
                <br/>
                // Add listener<br/>
                button.addEventListener('click', handleClick);<br/>
                <br/>
                // Remove listener (exact same function reference)<br/>
                button.removeEventListener('click', handleClick);<br/>
                <br/>
                // ‚ùå Won't work (anonymous function)<br/>
                button.addEventListener('click', () => console.log('Clicked!'));<br/>
                button.removeEventListener('click', () => console.log('Clicked!')); // FAILS<br/>
                <br/>
                // Remove all listeners (nuclear option)<br/>
                button.cloneNode(true); // Clone without listeners<br/>
                button.replaceWith(button.cloneNode(true));<br/>
                <br/>
                // One-time event<br/>
                button.addEventListener('click', handleClick, { once: true });<br/>
                <br/>
                // With AbortController (modern)<br/>
                const controller = new AbortController();<br/>
                button.addEventListener('click', handleClick, {<br/>
                &nbsp;&nbsp;signal: controller.signal<br/>
                });<br/>
                controller.abort(); // Removes all listeners with this signal
            </pre>
        </div>
        
        <div style="background-color: #fff0f0; padding: 15px; border-radius: 5px;">
            <b>Event Listener Options:</b><br/>
            <pre>
                // Third parameter options object<br/>
                element.addEventListener('click', handler, {<br/>
                &nbsp;&nbsp;capture: false,        // Use capturing phase?<br/>
                &nbsp;&nbsp;once: false,           // Remove after first trigger?<br/>
                &nbsp;&nbsp;passive: false,        // Performance optimization<br/>
                &nbsp;&nbsp;signal: undefined      // AbortController signal<br/>
                });<br/>
                <br/>
                // Old way (capture only)<br/>
                element.addEventListener('click', handler, true); // Capture phase<br/>
                element.addEventListener('click', handler, false); // Bubbling phase (default)<br/>
                <br/>
                <b>passive: true</b> - Improves scroll performance<br/>
                // Tells browser you won't call preventDefault()<br/>
                // Browser can scroll immediately without waiting<br/>
                window.addEventListener('scroll', handler, { passive: true });<br/>
                <br/>
                <b>once: true</b> - Auto-remove after firing<br/>
                // Perfect for one-time initialization<br/>
                window.addEventListener('load', initApp, { once: true });<br/>
                <br/>
                <b>capture: true</b> - Catch in capturing phase<br/>
                // Useful for intercepting events before they reach target<br/>
                document.addEventListener('click', globalClick, { capture: true });
            </pre>
        </div>
    </div>
    
    <hr/>
    
    <b>c. The Event Object</b>
    
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Common Event Properties:</b><br/>
        <pre>
            function handleEvent(event) {<br/>
            &nbsp;&nbsp;// Event type<br/>
            &nbsp;&nbsp;console.log('Event type:', event.type); // 'click', 'keydown', etc.<br/>
            <br/>
            &nbsp;&nbsp;// Target element<br/>
            &nbsp;&nbsp;console.log('Target:', event.target); // Element that triggered event<br/>
            &nbsp;&nbsp;console.log('Current target:', event.currentTarget); // Element with listener<br/>
            <br/>
            &nbsp;&nbsp;// Mouse events<br/>
            &nbsp;&nbsp;console.log('Client X/Y:', event.clientX, event.clientY); // Viewport coordinates<br/>
            &nbsp;&nbsp;console.log('Page X/Y:', event.pageX, event.pageY); // Document coordinates<br/>
            &nbsp;&nbsp;console.log('Screen X/Y:', event.screenX, event.screenY); // Screen coordinates<br/>
            &nbsp;&nbsp;console.log('Button:', event.button); // 0=left, 1=middle, 2=right<br/>
            &nbsp;&nbsp;console.log('Buttons:', event.buttons); // Multiple buttons pressed<br/>
            &nbsp;&nbsp;console.log('Alt key:', event.altKey); // Boolean<br/>
            &nbsp;&nbsp;console.log('Ctrl key:', event.ctrlKey); // Boolean<br/>
            &nbsp;&nbsp;console.log('Shift key:', event.shiftKey); // Boolean<br/>
            &nbsp;&nbsp;console.log('Meta key:', event.metaKey); // Boolean (Cmd on Mac)<br/>
            <br/>
            &nbsp;&nbsp;// Keyboard events<br/>
            &nbsp;&nbsp;console.log('Key:', event.key); // 'a', 'Enter', 'ArrowUp'<br/>
            &nbsp;&nbsp;console.log('Code:', event.code); // 'KeyA', 'Enter', 'ArrowUp'<br/>
            &nbsp;&nbsp;console.log('Key code (deprecated):', event.keyCode);<br/>
            &nbsp;&nbsp;console.log('Repeat:', event.repeat); // True if key is held<br/>
            <br/>
            &nbsp;&nbsp;// Form events<br/>
            &nbsp;&nbsp;console.log('Value:', event.target.value);<br/>
            &nbsp;&nbsp;console.log('Checked:', event.target.checked); // For checkboxes<br/>
            <br/>
            &nbsp;&nbsp;// Prevent default behavior<br/>
            &nbsp;&nbsp;event.preventDefault();<br/>
            <br/>
            &nbsp;&nbsp;// Stop event propagation<br/>
            &nbsp;&nbsp;event.stopPropagation(); // Stop bubbling/capturing<br/>
            &nbsp;&nbsp;event.stopImmediatePropagation(); // Also stop other handlers<br/>
            <br/>
            &nbsp;&nbsp;// Time stamp<br/>
            &nbsp;&nbsp;console.log('Time:', event.timeStamp); // Milliseconds since page load<br/>
            }<br/>
            <br/>
            // Check event compatibility<br/>
            if (event instanceof MouseEvent) {<br/>
            &nbsp;&nbsp;// Mouse-specific properties available<br/>
            }<br/>
            <br/>
            if (event instanceof KeyboardEvent) {<br/>
            &nbsp;&nbsp;// Keyboard-specific properties available<br/>
            }
        </pre>
    </div>
    
    <hr/>
    
    <b>d. Event Propagation: Bubbling and Capturing</b>
    
    <div style="background-color: #f0f8ff; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Event Propagation Phases:</b><br/>
        1. <b>Capturing Phase:</b> Window ‚Üí Document ‚Üí ... ‚Üí Target (top-down)<br/>
        2. <b>Target Phase:</b> Event reaches target element<br/>
        3. <b>Bubbling Phase:</b> Target ‚Üí ... ‚Üí Document ‚Üí Window (bottom-up)<br/><br/>
        
        <b>Visual Example:</b><br/>
        <pre>
            &lt;div id="grandparent"&gt;<br/>
            &nbsp;&nbsp;&lt;div id="parent"&gt;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&lt;button id="child"&gt;Click me&lt;/button&gt;<br/>
            &nbsp;&nbsp;&lt;/div&gt;<br/>
            &lt;/div&gt;<br/>
            <br/>
            // Event flow when clicking button:<br/>
            // 1. Capturing: window ‚Üí document ‚Üí html ‚Üí body ‚Üí #grandparent ‚Üí #parent<br/>
            // 2. Target: #child<br/>
            // 3. Bubbling: #child ‚Üí #parent ‚Üí #grandparent ‚Üí body ‚Üí html ‚Üí document ‚Üí window
        </pre>
    </div>
    
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Controlling Event Propagation:</b><br/>
        <pre>
            const grandparent = document.getElementById('grandparent');<br/>
            const parent = document.getElementById('parent');<br/>
            const child = document.getElementById('child');<br/>
            <br/>
            // Capture phase (third parameter: true)<br/>
            grandparent.addEventListener('click', () => {<br/>
            &nbsp;&nbsp;console.log('Grandparent captured');<br/>
            }, true);<br/>
            <br/>
            // Bubbling phase (default)<br/>
            parent.addEventListener('click', () => {<br/>
            &nbsp;&nbsp;console.log('Parent bubbled');<br/>
            });<br/>
            <br/>
            child.addEventListener('click', (event) => {<br/>
            &nbsp;&nbsp;console.log('Child clicked');<br/>
            <br/>
            &nbsp;&nbsp;// Stop propagation - parent won't see event<br/>
            &nbsp;&nbsp;event.stopPropagation();<br/>
            <br/>
            &nbsp;&nbsp;// Stop immediate propagation - other handlers on child won't run<br/>
            &nbsp;&nbsp;event.stopImmediatePropagation();<br/>
            });<br/>
            <br/>
            // Another handler on child<br/>
            child.addEventListener('click', () => {<br/>
            &nbsp;&nbsp;console.log('Second child handler'); // Won't run if stopImmediatePropagation called<br/>
            });<br/>
            <br/>
            // Clicking child logs:<br/>
            // 1. "Grandparent captured" (capturing phase)<br/>
            // 2. "Child clicked"<br/>
            // (Parent handler doesn't run due to stopPropagation)
        </pre>
    </div>
    
    <div style="background-color: #fff0f0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>event.target vs event.currentTarget:</b><br/>
        <pre>
            &lt;div id="container"&gt;<br/>
            &nbsp;&nbsp;&lt;button&gt;Button 1&lt;/button&gt;<br/>
            &nbsp;&nbsp;&lt;button&gt;Button 2&lt;/button&gt;<br/>
            &nbsp;&nbsp;&lt;button&gt;Button 3&lt;/button&gt;<br/>
            &lt;/div&gt;<br/>
            <br/>
            const container = document.getElementById('container');<br/>
            <br/>
            container.addEventListener('click', function(event) {<br/>
            &nbsp;&nbsp;// event.target: The actual button that was clicked<br/>
            &nbsp;&nbsp;console.log('Target:', event.target.tagName, event.target.textContent);<br/>
            <br/>
            &nbsp;&nbsp;// event.currentTarget: The container (element with listener)<br/>
            &nbsp;&nbsp;console.log('Current target:', event.currentTarget.id);<br/>
            <br/>
            &nbsp;&nbsp;// this: Also the container (in regular function)<br/>
            &nbsp;&nbsp;console.log('This:', this.id);<br/>
            });<br/>
            <br/>
            // Clicking Button 2 logs:<br/>
            // Target: BUTTON Button 2<br/>
            // Current target: container<br/>
            // This: container
        </pre>
    </div>
    
    <hr/>
    
    <b>e. Event Delegation Pattern</b>
    
    <div style="background-color: #e8f5e8; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>What is Event Delegation?</b><br/>
        ‚Ä¢ Attach single event listener to parent element<br/>
        ‚Ä¢ Handle events from multiple child elements<br/>
        ‚Ä¢ Uses event bubbling to catch events<br/>
        ‚Ä¢ Works with dynamically added elements<br/>
        ‚Ä¢ More efficient than multiple listeners<br/><br/>
        
        <b>Benefits:</b><br/>
        ‚úì Fewer event listeners (better performance)<br/>
        ‚úì Works with dynamic content<br/>
        ‚úì Less memory usage<br/>
        ‚úì Cleaner code structure
    </div>
    
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Event Delegation Example:</b><br/>
        <pre>
            // ‚ùå Bad: Add listener to each button<br/>
            const buttons = document.querySelectorAll('.delete-btn');<br/>
            buttons.forEach(button => {<br/>
            &nbsp;&nbsp;button.addEventListener('click', handleDelete);<br/>
            });<br/>
            // Problem: New buttons added later won't have listeners<br/>
            <br/>
            // ‚úÖ Good: Event delegation<br/>
            const list = document.getElementById('item-list');<br/>
            <br/>
            list.addEventListener('click', function(event) {<br/>
            &nbsp;&nbsp;// Check if clicked element is a delete button<br/>
            &nbsp;&nbsp;if (event.target.classList.contains('delete-btn')) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;const listItem = event.target.closest('li');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;deleteItem(listItem);<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;// Check if clicked element is an edit button<br/>
            &nbsp;&nbsp;if (event.target.classList.contains('edit-btn')) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;const listItem = event.target.closest('li');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;editItem(listItem);<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;// Check if clicked on list item (for selection)<br/>
            &nbsp;&nbsp;if (event.target.matches('li.item')) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;selectItem(event.target);<br/>
            &nbsp;&nbsp;}<br/>
            });<br/>
            <br/>
            // Helper methods for delegation<br/>
            function handleDelegation(event, selector, callback) {<br/>
            &nbsp;&nbsp;let element = event.target;<br/>
            <br/>
            &nbsp;&nbsp;// Traverse up DOM tree to find matching element<br/>
            &nbsp;&nbsp;while (element && element !== event.currentTarget) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;if (element.matches(selector)) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;callback(element, event);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;element = element.parentElement;<br/>
            &nbsp;&nbsp;}<br/>
            }<br/>
            <br/>
            // Usage<br/>
            list.addEventListener('click', (event) => {<br/>
            &nbsp;&nbsp;handleDelegation(event, '.delete-btn', (btn) => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;btn.closest('li').remove();<br/>
            &nbsp;&nbsp;});<br/>
            });
        </pre>
    </div>
    
    <hr/>
    
    <b>f. Custom Events</b>
    
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Creating and Dispatching Custom Events:</b><br/>
        <pre>
            // Create custom event<br/>
            const event = new Event('customEvent');<br/>
            <br/>
            // With custom data<br/>
            const eventWithData = new CustomEvent('userLoggedIn', {<br/>
            &nbsp;&nbsp;detail: {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;userId: 123,<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;username: 'john_doe',<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;timestamp: Date.now()<br/>
            &nbsp;&nbsp;},<br/>
            &nbsp;&nbsp;bubbles: true, // Event bubbles up<br/>
            &nbsp;&nbsp;cancelable: true // Can be cancelled with preventDefault()<br/>
            });<br/>
            <br/>
            // Listen for custom event<br/>
            document.addEventListener('userLoggedIn', function(event) {<br/>
            &nbsp;&nbsp;console.log('User logged in:', event.detail);<br/>
            &nbsp;&nbsp;console.log('User ID:', event.detail.userId);<br/>
            });<br/>
            <br/>
            // Dispatch event<br/>
            document.dispatchEvent(eventWithData);<br/>
            <br/>
            // Dispatch from specific element<br/>
            const button = document.querySelector('button');<br/>
            button.dispatchEvent(new Event('buttonActivated'));<br/>
            <br/>
            // Real-world example: Form validation<br/>
            const form = document.querySelector('form');<br/>
            <br/>
            // Custom event when form is valid<br/>
            form.addEventListener('input', function() {<br/>
            &nbsp;&nbsp;if (form.checkValidity()) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;form.dispatchEvent(new CustomEvent('formValid', {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;detail: { formData: new FormData(form) }<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}));<br/>
            &nbsp;&nbsp;}<br/>
            });<br/>
            <br/>
            // Listen for form validation<br/>
            form.addEventListener('formValid', function(event) {<br/>
            &nbsp;&nbsp;enableSubmitButton();<br/>
            &nbsp;&nbsp;updatePreview(event.detail.formData);<br/>
            });
        </pre>
    </div>
    
