    <hr/>
    
    <b>b. State Management with Classes</b>
    
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Using Classes for Application State:</b><br/>
        <pre>
            // State management system using classes<br/>
            class StateManager {<br/>
            &nbsp;&nbsp;constructor(element) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.element = element;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.states = new Set();<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;addState(state) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.states.add(state);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.element.classList.add(\`state-\${state}\`);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.emit('state:added', { state });<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;removeState(state) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.states.delete(state);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.element.classList.remove(\`state-\${state}\`);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.emit('state:removed', { state });<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;toggleState(state) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;if (this.hasState(state)) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.removeState(state);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;} else {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.addState(state);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;hasState(state) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;return this.states.has(state);<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;getStates() {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;return Array.from(this.states);<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;clearStates() {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.states.forEach(state => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;this.element.classList.remove(\`state-\${state}\`);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;});<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.states.clear();<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.emit('states:cleared');<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;emit(eventName, data) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;this.element.dispatchEvent(new CustomEvent(eventName, {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;detail: data,<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;bubbles: true<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}));<br/>
            &nbsp;&nbsp;}<br/>
            }<br/>
            <br/>
            // Usage<br/>
            const button = document.querySelector('.my-button');<br/>
            const stateManager = new StateManager(button);<br/>
            <br/>
            // Add states<br/>
            stateManager.addState('loading');<br/>
            stateManager.addState('disabled');<br/>
            <br/>
            // Check state<br/>
            if (stateManager.hasState('loading')) {<br/>
            &nbsp;&nbsp;console.log('Button is loading');<br/>
            }<br/>
            <br/>
            // Listen for state changes<br/>
            button.addEventListener('state:added', (event) => {<br/>
            &nbsp;&nbsp;console.log('State added:', event.detail.state);<br/>
            });
        </pre>
    </div>
    
    <div style="background-color: #f0f8ff; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Common State Patterns:</b><br/>
        
        <b>1. Loading States:</b><br/>
        <pre>
            .is-loading { opacity: 0.7; pointer-events: none; }<br/>
            .is-loading::after { /* spinner */ }<br/>
            element.classList.add('is-loading');
        </pre><br/>
        
        <b>2. Error States:</b><br/>
        <pre>
            .has-error { border-color: #dc3545; }<br/>
            .has-error + .error-message { display: block; }<br/>
            element.classList.add('has-error');
        </pre><br/>
        
        <b>3. Success States:</b><br/>
        <pre>
            .is-success { border-color: #28a745; }<br/>
            .is-success + .success-icon { display: block; }<br/>
            element.classList.add('is-success');
        </pre><br/>
        
        <b>4. Active/Selected States:</b><br/>
        <pre>
            .is-active { background: #007bff; color: white; }<br/>
            .is-selected { box-shadow: 0 0 0 2px #007bff; }<br/>
            element.classList.toggle('is-active');
        </pre><br/>
        
        <b>5. Visibility States:</b><br/>
        <pre>
            .is-visible { opacity: 1; transform: translateY(0); }<br/>
            .is-hidden { display: none; }<br/>
            element.classList.remove('is-hidden');<br/>
            element.classList.add('is-visible');
        </pre>
    </div>
    
    <hr/>
    
    <b>c. CSS Transitions & JavaScript Coordination</b>
    
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Smooth Transitions with Classes:</b><br/>
        <pre>
            // CSS for transitions<br/>
            .modal {<br/>
            &nbsp;&nbsp;opacity: 0;<br/>
            &nbsp;&nbsp;transform: translateY(-20px);<br/>
            &nbsp;&nbsp;transition: opacity 0.3s ease, transform 0.3s ease;<br/>
            &nbsp;&nbsp;pointer-events: none;<br/>
            }<br/>
            <br/>
            .modal.visible {<br/>
            &nbsp;&nbsp;opacity: 1;<br/>
            &nbsp;&nbsp;transform: translateY(0);<br/>
            &nbsp;&nbsp;pointer-events: all;<br/>
            }<br/>
            <br/>
            // JavaScript for showing modal<br/>
            function showModal() {<br/>
            &nbsp;&nbsp;const modal = document.querySelector('.modal');<br/>
            &nbsp;&nbsp;<br/>
            &nbsp;&nbsp;// Force browser to recognize initial state<br/>
            &nbsp;&nbsp;modal.classList.remove('visible');<br/>
            &nbsp;&nbsp;modal.offsetHeight; // Trigger reflow<br/>
            <br/>
            &nbsp;&nbsp;// Add visible class for transition<br/>
            &nbsp;&nbsp;modal.classList.add('visible');<br/>
            }<br/>
            <br/>
            // Listening for transition end<br/>
            modal.addEventListener('transitionend', (event) => {<br/>
            &nbsp;&nbsp;if (event.propertyName === 'opacity') {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;console.log('Modal transition complete');<br/>
            &nbsp;&nbsp;}<br/>
            });<br/>
            <br/>
            // Multiple step animation<br/>
            function animateElement(element) {<br/>
            &nbsp;&nbsp;element.classList.add('step-1');<br/>
            <br/>
            &nbsp;&nbsp;setTimeout(() => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;element.classList.remove('step-1');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;element.classList.add('step-2');<br/>
            &nbsp;&nbsp;}, 300);<br/>
            <br/>
            &nbsp;&nbsp;setTimeout(() => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;element.classList.remove('step-2');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;element.classList.add('step-3');<br/>
            &nbsp;&nbsp;}, 600);<br/>
            }<br/>
            <br/>
            // CSS for steps<br/>
            .step-1 { transform: translateX(0); background: red; }<br/>
            .step-2 { transform: translateX(100px); background: blue; }<br/>
            .step-3 { transform: translateX(200px); background: green; }<br/>
            .animated { transition: all 0.3s ease; }
        </pre>
    </div>
    
    <div style="background-color: #fff0f0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Animation Performance Tips:</b><br/>
        
        <b>1. Use transform and opacity for animations:</b><br/>
        <pre>
            /* ‚úÖ Good for performance */<br/>
            .moving {<br/>
            &nbsp;&nbsp;transform: translateX(100px);<br/>
            &nbsp;&nbsp;opacity: 0.5;<br/>
            &nbsp;&nbsp;transition: transform 0.3s ease, opacity 0.3s ease;<br/>
            }<br/>
            <br/>
            /* ‚ùå Bad for performance */<br/>
            .moving-slow {<br/>
            &nbsp;&nbsp;left: 100px; /* Causes layout reflow */<br/>
            &nbsp;&nbsp;transition: left 0.3s ease;<br/>
            }
        </pre><br/>
        
        <b>2. Use will-change for complex animations:</b><br/>
        <pre>
            .complex-animation {<br/>
            &nbsp;&nbsp;will-change: transform, opacity;<br/>
            }<br/>
            /* Use sparingly - remove when animation ends */
        </pre><br/>
        
        <b>3. Batch style changes:</b><br/>
        <pre>
            // ‚ùå Bad: Multiple reflows<br/>
            element.style.width = '100px';<br/>
            element.style.height = '100px';<br/>
            element.style.backgroundColor = 'red';<br/>
            <br/>
            // ‚úÖ Good: Single reflow<br/>
            element.classList.add('resized', 'colored');<br/>
            <br/>
            // CSS<br/>
            .resized { width: 100px; height: 100px; }<br/>
            .colored { background-color: red; }
        </pre>
    </div>
    
    <hr/>
    
    <b>d. Accessibility Considerations</b>
    
    <div style="background-color: #e8f5e8; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Making Dynamic Content Accessible:</b><br/>
        1. <b>Screen Readers:</b> Use aria-* attributes with classes<br/>
        2. <b>Keyboard Navigation:</b> Maintain focus management<br/>
        3. <b>Reduced Motion:</b> Respect user preferences<br/>
        4. <b>Color Contrast:</b> Ensure readability in all states<br/>
        5. <b>Focus Indicators:</b> Don't hide focus outlines<br/>
    </div>
    
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <b>Accessible Dynamic Styling:</b><br/>
        <pre>
            // Toggle with ARIA attributes<br/>
            const menuButton = document.querySelector('.menu-toggle');<br/>
            const menu = document.querySelector('.menu');<br/>
            <br/>
            menuButton.addEventListener('click', () => {<br/>
            &nbsp;&nbsp;const isExpanded = menu.classList.toggle('expanded');<br/>
            &nbsp;&nbsp;<br/>
            &nbsp;&nbsp;// Update ARIA attributes<br/>
            &nbsp;&nbsp;menuButton.setAttribute('aria-expanded', isExpanded);<br/>
            &nbsp;&nbsp;menu.setAttribute('aria-hidden', !isExpanded);<br/>
            <br/>
            &nbsp;&nbsp;// Manage focus<br/>
            &nbsp;&nbsp;if (isExpanded) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;menu.querySelector('a').focus();<br/>
            &nbsp;&nbsp;} else {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;menuButton.focus();<br/>
            &nbsp;&nbsp;}<br/>
            });<br/>
            <br/>
            // Reduced motion support<br/>
            const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)');<br/>
            <br/>
            if (prefersReducedMotion.matches) {<br/>
            &nbsp;&nbsp;// Disable or simplify animations<br/>
            &nbsp;&nbsp;document.documentElement.classList.add('reduced-motion');<br/>
            }<br/>
            <br/>
            // CSS for reduced motion<br/>
            @media (prefers-reduced-motion: reduce) {<br/>
            &nbsp;&nbsp;* {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;animation-duration: 0.01ms !important;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;animation-iteration-count: 1 !important;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;transition-duration: 0.01ms !important;<br/>
            &nbsp;&nbsp;}<br/>
            }<br/>
            <br/>
            // High contrast mode<br/>
            const prefersHighContrast = window.matchMedia('(prefers-contrast: high)');<br/>
            <br/>
            if (prefersHighContrast.matches) {<br/>
            &nbsp;&nbsp;document.documentElement.classList.add('high-contrast');<br/>
            }<br/>
            <br/>
            // CSS for high contrast<br/>
            .high-contrast .button {<br/>
            &nbsp;&nbsp;border: 2px solid currentColor;<br/>
            }<br/>
            .high-contrast .button.active {<br/>
            &nbsp;&nbsp;border-width: 4px;<br/>
            }
        </pre>
    </div>
    
