            // Open lightbox with image<br/>
            function openLightbox(imageId, event = null) {<br/>
            &nbsp;&nbsp;if (event) event.preventDefault();<br/>
            <br/>
            &nbsp;&nbsp;const image = imageData.find(img => img.id === imageId);<br/>
            &nbsp;&nbsp;if (!image) return;<br/>
            <br/>
            &nbsp;&nbsp;const lightbox = document.querySelector('.lightbox');<br/>
            &nbsp;&nbsp;const lightboxImage = document.querySelector('.lightbox-image');<br/>
            &nbsp;&nbsp;const lightboxCaption = document.querySelector('.lightbox-caption');<br/>
            <br/>
            &nbsp;&nbsp;// Set image source and alt text<br/>
            &nbsp;&nbsp;lightboxImage.src = image.fullSize;<br/>
            &nbsp;&nbsp;lightboxImage.alt = image.description;<br/>
            <br/>
            &nbsp;&nbsp;// Set caption<br/>
            &nbsp;&nbsp;lightboxCaption.innerHTML = ' < br / >
                &
                nbsp; & nbsp; & nbsp; & nbsp; & lt;h3 & gt;\$ {
                    image.title
                } & lt;
                /h3&gt;<br/ >
                &
                nbsp; & nbsp; & nbsp; & nbsp; & lt;p & gt;\$ {
                    image.description
                } & lt;
                /p&gt;<br/ >
                &
                nbsp; & nbsp; & nbsp; & nbsp; & lt;div class = "lightbox-meta" & gt; < br / >
                &
                nbsp; & nbsp; & nbsp; & nbsp; & nbsp; & nbsp; & lt;span & gt;Category: \$ {
                    image.category
                } & lt;
                /span&gt;<br/ >
                &
                nbsp; & nbsp; & nbsp; & nbsp; & nbsp; & nbsp; & lt;span & gt;Date: \$ {
                    image.date
                } & lt;
                /span&gt;<br/ >
                &
                nbsp; & nbsp; & nbsp; & nbsp; & nbsp; & nbsp; & lt;span & gt;Photographer: \$ {
                    image.photographer
                } & lt;
                /span&gt;<br/ >
                &
                nbsp; & nbsp; & nbsp; & nbsp; & lt;
                /div&gt;<br/ >
                &
                nbsp; & nbsp;
                ';<br/>
            <br/>
            &nbsp;&nbsp;// Update current index<br/>
            &nbsp;&nbsp;const currentImages = getCurrentImages();<br/>
            &nbsp;&nbsp;Gallery.currentIndex = currentImages.findIndex(img => img.id === imageId);<br/>
            <br/>
            &nbsp;&nbsp;// Show lightbox with animation<br/>
            &nbsp;&nbsp;lightbox.setAttribute('aria-hidden', 'false');<br/>
            &nbsp;&nbsp;document.body.classList.add('lightbox-open');<br/>
            &nbsp;&nbsp;lightbox.classList.add('active');<br/>
            <br/>
            &nbsp;&nbsp;// Set focus for accessibility<br/>
            &nbsp;&nbsp;setTimeout(() => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;document.querySelector('.lightbox-close').focus();<br/>
            &nbsp;&nbsp;}, 100);<br/>
            <br/>
            &nbsp;&nbsp;// Prevent background scrolling<br/>
            &nbsp;&nbsp;document.body.style.overflow = 'hidden';<br/>
            <br/>
            &nbsp;&nbsp;Gallery.isLightboxOpen = true;<br/>
            }<br/>
            <br/>
            // Close lightbox<br/>
            function closeLightbox() {<br/>
            &nbsp;&nbsp;const lightbox = document.querySelector('.lightbox');<br/>
            <br/>
            &nbsp;&nbsp;lightbox.classList.remove('active');<br/>
            &nbsp;&nbsp;document.body.classList.remove('lightbox-open');<br/>
            <br/>
            &nbsp;&nbsp;setTimeout(() => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;lightbox.setAttribute('aria-hidden', 'true');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;document.body.style.overflow = '';<br/>
            &nbsp;&nbsp;}, 300);<br/>
            <br/>
            &nbsp;&nbsp;// Return focus to the element that opened the lightbox<br/>
            &nbsp;&nbsp;if (Gallery.lastFocusedElement) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;Gallery.lastFocusedElement.focus();<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;Gallery.isLightboxOpen = false;<br/>
            }<br/>
            <br/>
            // Navigate lightbox images<br/>
            function navigateLightbox(direction) {<br/>
            &nbsp;&nbsp;const currentImages = getCurrentImages();<br/>
            <br/>
            &nbsp;&nbsp;if (direction === 'next') {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;Gallery.currentIndex = (Gallery.currentIndex + 1) % currentImages.length;<br/>
            &nbsp;&nbsp;} else if (direction === 'prev') {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;Gallery.currentIndex = (Gallery.currentIndex - 1 + currentImages.length) % currentImages.length;<br/>
            &nbsp;&nbsp;}<br/>
            <br/>
            &nbsp;&nbsp;const nextImage = currentImages[Gallery.currentIndex];<br/>
            &nbsp;&nbsp;openLightbox(nextImage.id);<br/>
            }
        </pre>
    </div>
    
    <b>Step 5: Event Listeners & Keyboard Navigation</b>
    
    <div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
        <pre>
            // Setup all event listeners<br/>
            function setupEventListeners() {<br/>
            &nbsp;&nbsp;// Event delegation for image clicks<br/>
            &nbsp;&nbsp;document.querySelector('.image-grid').addEventListener('click', (event) => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;const viewButton = event.target.closest('.view-button');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;if (viewButton) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;const card = viewButton.closest('.image-card');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;const imageId = parseInt(card.dataset.id);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Gallery.lastFocusedElement = viewButton;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;openLightbox(imageId, event);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            &nbsp;&nbsp;});<br/>
            <br/>
            &nbsp;&nbsp;// Filter buttons<br/>
            &nbsp;&nbsp;document.querySelector('.filter-buttons').addEventListener('click', (event) => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;const button = event.target.closest('.filter-button');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;if (button) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;filterImages(button.dataset.filter);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            &nbsp;&nbsp;});<br/>
            <br/>
            &nbsp;&nbsp;// Search input with debounce<br/>
            &nbsp;&nbsp;const searchInput = document.querySelector('.search-input');<br/>
            &nbsp;&nbsp;let searchTimeout;<br/>
            &nbsp;&nbsp;searchInput.addEventListener('input', (event) => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;clearTimeout(searchTimeout);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;searchTimeout = setTimeout(() => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;searchImages(event.target.value);<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}, 300);<br/>
            &nbsp;&nbsp;});<br/>
            <br/>
            &nbsp;&nbsp;// Lightbox controls<br/>
            &nbsp;&nbsp;document.querySelector('.lightbox-close').addEventListener('click', closeLightbox);<br/>
            &nbsp;&nbsp;document.querySelector('.lightbox-prev').addEventListener('click', () => navigateLightbox('prev'));<br/>
            &nbsp;&nbsp;document.querySelector('.lightbox-next').addEventListener('click', () => navigateLightbox('next'));<br/>
            <br/>
            &nbsp;&nbsp;// Close lightbox on backdrop click<br/>
            &nbsp;&nbsp;document.querySelector('.lightbox').addEventListener('click', (event) => {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;if (event.target.classList.contains('lightbox')) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;closeLightbox();<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;}<br/>
            &nbsp;&nbsp;});<br/>
            <br/>
            &nbsp;&nbsp;// Keyboard navigation<br/>
            &nbsp;&nbsp;document.addEventListener('keydown', handleKeyboard);<br/>
            <br/>
            &nbsp;&nbsp;// Handle browser back/forward<br/>
            &nbsp;&nbsp;window.addEventListener('popstate', handlePopState);<br/>
            }<br/>
            <br/>
            // Keyboard navigation handler<br/>
            function handleKeyboard(event) {<br/>
            &nbsp;&nbsp;if (!Gallery.isLightboxOpen) return;<br/>
            <br/>
            &nbsp;&nbsp;switch (event.key) {<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;case 'Escape':<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;closeLightbox();<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;case 'ArrowLeft':<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;navigateLightbox('prev');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;case 'ArrowRight':<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;navigateLightbox('next');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;case ' ': // Space bar<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;event.preventDefault(); // Prevent page scroll<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;navigateLightbox('next');<br/>
            &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;break;<br/>
            &nbsp;&nbsp;}<br/>
            }
        </pre>
    </div>
    
    <h3>Advanced Features to Implement</h3>
    
