<h3>Implementing Charts with Pure JavaScript</h3>

<b>Chart Utility Class (chart-utils.js):</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
class ChartRenderer {
constructor() {
    this.colors = [
        '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0',
        '#9966FF', '#FF9F40', '#C9CBCF', '#8AC926',
        '#1982C4', '#6A4C93'
    ];
}

// 1. Category Spending Chart (Pie Chart)
renderCategoryChart(containerId, transactions) {
    const container = document.getElementById(containerId);
    if (!container) return;

    // Calculate spending by category
    const categoryData = this.calculateCategoryData(transactions);
    
    // Create SVG element
    const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
    svg.setAttribute('width', '100%');
    svg.setAttribute('height', '100%');
    svg.setAttribute('viewBox', '0 0 400 300');
    
    // Clear container
    container.innerHTML = '';
    container.appendChild(svg);
    
    // Create pie chart
    this.createPieChart(svg, categoryData);
    
    // Add legend
    this.createLegend(svg, categoryData);
}

calculateCategoryData(transactions) {
    const categoryTotals = {};
    
    // Filter only expense transactions
    const expenses = transactions.filter(t => t.type === 'expense');
    
    expenses.forEach(transaction => {
        const category = transaction.category;
        if (!categoryTotals[category]) {
            categoryTotals[category] = 0;
        }
        categoryTotals[category] += transaction.amount;
    });
    
    // Convert to array format
    const data = [];
    let total = 0;
    
    Object.entries(categoryTotals).forEach(([category, amount], index) => {
        data.push({
            category: category,
            amount: amount,
            color: this.colors[index % this.colors.length],
            percentage: 0 // Will calculate later
        });
        total += amount;
    });
    
    // Calculate percentages
    data.forEach(item => {
        item.percentage = total > 0 ? (item.amount / total * 100).toFixed(1) : 0;
    });
    
    // Sort by amount (largest first)
    return data.sort((a, b) => b.amount - a.amount);
}

createPieChart(svg, data) {
    const centerX = 150;
    const centerY = 120;
    const radius = 100;
    
    let startAngle = 0;
    
    data.forEach((item, index) => {
        const percentage = parseFloat(item.percentage);
        const angle = (percentage / 100) * 360;
        const endAngle = startAngle + angle;
        
        // Convert angles to radians
        const startRad = (startAngle - 90) * Math.PI / 180;
        const endRad = (endAngle - 90) * Math.PI / 180;
        
        // Calculate arc coordinates
        const x1 = centerX + radius * Math.cos(startRad);
        const y1 = centerY + radius * Math.sin(startRad);
        const x2 = centerX + radius * Math.cos(endRad);
        const y2 = centerY + radius * Math.sin(endRad);
        
        // Large arc flag
        const largeArcFlag = angle > 180 ? 1 : 0;
        
        // Create path for pie slice
        const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
        const d = [
            'M', centerX, centerY,
            'L', x1, y1,
            'A', radius, radius, 0, largeArcFlag, 1, x2, y2,
            'Z'
        ].join(' ');
        
        path.setAttribute('d', d);
        path.setAttribute('fill', item.color);
        path.setAttribute('stroke', '#fff');
        path.setAttribute('stroke-width', '2');
        path.setAttribute('data-category', item.category);
        path.setAttribute('data-percentage', item.percentage);
        path.setAttribute('data-amount', item.amount.toFixed(2));
        
        // Add hover effects
        path.style.cursor = 'pointer';
        path.style.transition = 'transform 0.3s ease';
        
        path.addEventListener('mouseenter', (e) => {
            e.target.style.transform = 'scale(1.05)';
            this.showTooltip(e, item);
        });
        
        path.addEventListener('mouseleave', (e) => {
            e.target.style.transform = 'scale(1)';
            this.hideTooltip();
        });
        
        svg.appendChild(path);
        startAngle = endAngle;
    });
}

createLegend(svg, data) {
    const legendX = 280;
    const legendY = 50;
    const itemHeight = 25;
    
    data.forEach((item, index) => {
        const y = legendY + (index * itemHeight);
        
        // Color box
        const rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
        rect.setAttribute('x', legendX);
        rect.setAttribute('y', y);
        rect.setAttribute('width', 15);
        rect.setAttribute('height', 15);
        rect.setAttribute('fill', item.color);
        rect.setAttribute('rx', '3');
        svg.appendChild(rect);
        
        // Category name
        const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
        text.setAttribute('x', legendX + 20);
        text.setAttribute('y', y + 12);
        text.setAttribute('font-size', '12');
        text.setAttribute('fill', '#333');
        text.textContent = '
                $ {
                    item.category.charAt(0).toUpperCase() + item.category.slice(1)
                }
                ';
        svg.appendChild(text);
        
        // Percentage
        const percText = document.createElementNS('http://www.w3.org/2000/svg', 'text');
        percText.setAttribute('x', legendX + 150);
        percText.setAttribute('y', y + 12);
        percText.setAttribute('font-size', '12');
        percText.setAttribute('fill', '#666');
        percText.setAttribute('text-anchor', 'end');
        percText.textContent = '
                $ {
                    item.percentage
                } % ';
        svg.appendChild(percText);
    });
}

showTooltip(event, data) {
    const tooltip = document.getElementById('chart-tooltip') || this.createTooltip();
    
    tooltip.innerHTML = \`
        <strong>\${data.category.charAt(0).toUpperCase() + data.category.slice(1)}</strong><br>
        Amount: $\${data.amount.toFixed(2)}<br>
        Percentage: \${data.percentage}%
    \`;
    
    tooltip.style.display = 'block';
    tooltip.style.left = (event.pageX + 10) + 'px';
    tooltip.style.top = (event.pageY + 10) + 'px';
}

hideTooltip() {
    const tooltip = document.getElementById('chart-tooltip');
    if (tooltip) {
        tooltip.style.display = 'none';
    }
}

createTooltip() {
    const tooltip = document.createElement('div');
    tooltip.id = 'chart-tooltip';
    tooltip.style.position = 'absolute';
    tooltip.style.background = 'rgba(0, 0, 0, 0.8)';
    tooltip.style.color = 'white';
    tooltip.style.padding = '10px';
    tooltip.style.borderRadius = '5px';
    tooltip.style.fontSize = '12px';
    tooltip.style.pointerEvents = 'none';
    tooltip.style.display = 'none';
    tooltip.style.zIndex = '1000';
    document.body.appendChild(tooltip);
    return tooltip;
}

// 2. Monthly Overview Chart (Bar Chart)
renderMonthlyChart(containerId, transactions) {
    const container = document.getElementById(containerId);
    if (!container) return;

    // Calculate monthly data
    const monthlyData = this.calculateMonthlyData(transactions);
    
    // Create SVG
    const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
    svg.setAttribute('width', '100%');
    svg.setAttribute('height', '100%');
    svg.setAttribute('viewBox', '0 0 400 300');
    
    container.innerHTML = '';
    container.appendChild(svg);
    
    // Create bar chart
    this.createBarChart(svg, monthlyData);
}

calculateMonthlyData(transactions) {
    const months = [];
    const currentDate = new Date();
    
    // Get last 6 months
    for (let i = 5; i >= 0; i--) {
        const date = new Date(currentDate.getFullYear(), currentDate.getMonth() - i, 1);
        const monthName = date.toLocaleString('default', { month: 'short' });
        const year = date.getFullYear();
        const key = \`\${monthName} \${year}\`;
        
        months.push({
            name: key,
            income: 0,
            expenses: 0,
            savings: 0
        });
    }
    
    // Process transactions
    transactions.forEach(transaction => {
        const date = new Date(transaction.date);
        const monthName = date.toLocaleString('default', { month: 'short' });
        const year = date.getFullYear();
        const key = \`\${monthName} \${year}\`;
        
        const monthData = months.find(m => m.name === key);
        if (monthData) {
            if (transaction.type === 'income') {
                monthData.income += transaction.amount;
            } else if (transaction.type === 'expense') {
                monthData.expenses += transaction.amount;
            }
            monthData.savings = monthData.income - monthData.expenses;
        }
    });
    
    return months;
}

createBarChart(svg, data) {
    const margin = { top: 40, right: 30, bottom: 50, left: 60 };
    const width = 400 - margin.left - margin.right;
    const height = 300 - margin.top - margin.bottom;
    
    const g = document.createElementNS('http://www.w3.org/2000/svg', 'g');
    g.setAttribute('transform', \`translate(\${margin.left}, \${margin.top})\`);
    svg.appendChild(g);
    
    // Find max value for scaling
    const maxValue = Math.max(...data.map(d => Math.max(d.income, d.expenses, d.savings)));
    const yScale = height / (maxValue || 1);
    
    const barWidth = (width / data.length) * 0.6;
    const barSpacing = (width / data.length) * 0.4;
    
    // Draw bars
    data.forEach((month, index) => {
        const x = index * (barWidth + barSpacing);
        
        // Income bar
        if (month.income > 0) {
            const barHeight = month.income * yScale;
            const incomeBar = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
            incomeBar.setAttribute('x', x);
            incomeBar.setAttribute('y', height - barHeight);
            incomeBar.setAttribute('width', barWidth / 3);
            incomeBar.setAttribute('height', barHeight);
            incomeBar.setAttribute('fill', '#27ae60');
            incomeBar.setAttribute('data-value', month.income);
            incomeBar.setAttribute('data-type', 'income');
            g.appendChild(incomeBar);
        }
        
        // Expenses bar
        if (month.expenses > 0) {
            const barHeight = month.expenses * yScale;
            const expenseBar = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
            expenseBar.setAttribute('x', x + barWidth / 3);
            expenseBar.setAttribute('y', height - barHeight);
            expenseBar.setAttribute('width', barWidth / 3);
            expenseBar.setAttribute('height', barHeight);
            expenseBar.setAttribute('fill', '#e74c3c');
            expenseBar.setAttribute('data-value', month.expenses);
            expenseBar.setAttribute('data-type', 'expense');
            g.appendChild(expenseBar);
        }
        
        // Savings bar
        if (month.savings > 0) {
            const barHeight = month.savings * yScale;
            const savingsBar = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
            savingsBar.setAttribute('x', x + (barWidth / 3) * 2);
            savingsBar.setAttribute('y', height - barHeight);
            savingsBar.setAttribute('width', barWidth / 3);
            savingsBar.setAttribute('height', barHeight);
            savingsBar.setAttribute('fill', '#3498db');
            savingsBar.setAttribute('data-value', month.savings);
            savingsBar.setAttribute('data-type', 'savings');
            g.appendChild(savingsBar);
        }
        
        // Month label
        const label = document.createElementNS('http://www.w3.org/2000/svg', 'text');
        label.setAttribute('x', x + barWidth / 2);
        label.setAttribute('y', height + 20);
        label.setAttribute('text-anchor', 'middle');
        label.setAttribute('font-size', '10');
        label.setAttribute('fill', '#666');
        label.textContent = month.name;
        g.appendChild(label);
    });
    
    // Y-axis labels
    for (let i = 0; i <= 5; i++) {
        const value = (maxValue / 5) * i;
        const y = height - (value * yScale);
        
        const line = document.createElementNS('http://www.w3.org/2000/svg', 'line');
        line.setAttribute('x1', 0);
        line.setAttribute('y1', y);
        line.setAttribute('x2', width);
        line.setAttribute('y2', y);
        line.setAttribute('stroke', '#eee');
        line.setAttribute('stroke-width', '1');
        g.insertBefore(line, g.firstChild);
        
        const label = document.createElementNS('http://www.w3.org/2000/svg', 'text');
        label.setAttribute('x', -10);
        label.setAttribute('y', y + 3);
        label.setAttribute('text-anchor', 'end');
        label.setAttribute('font-size', '10');
        label.setAttribute('fill', '#666');
        label.textContent = \`$\${value.toFixed(0)}\`;
        g.appendChild(label);
    }
    
    // Add legend
    const legendData = [
        { label: 'Income', color: '#27ae60' },
        { label: 'Expenses', color: '#e74c3c' },
        { label: 'Savings', color: '#3498db' }
    ];
    
    legendData.forEach((item, index) => {
        const legendX = width - 100;
        const legendY = -20 + (index * 20);
        
        const rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
        rect.setAttribute('x', legendX);
        rect.setAttribute('y', legendY);
        rect.setAttribute('width', 10);
        rect.setAttribute('height', 10);
        rect.setAttribute('fill', item.color);
        rect.setAttribute('rx', '2');
        g.appendChild(rect);
        
        const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
        text.setAttribute('x', legendX + 15);
        text.setAttribute('y', legendY + 9);
        text.setAttribute('font-size', '10');
        text.setAttribute('fill', '#333');
        text.textContent = item.label;
        g.appendChild(text);
    });
}

// 3. Budget Progress Chart (Donut Chart)
renderBudgetChart(containerId, spent, budget) {
    const container = document.getElementById(containerId);
    if (!container) return;

    const percentage = budget > 0 ? (spent / budget * 100) : 0;
    
    // Create SVG
    const svg = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
    svg.setAttribute('width', '100%');
    svg.setAttribute('height', '100%');
    svg.setAttribute('viewBox', '0 0 200 200');
    
    container.innerHTML = '';
    container.appendChild(svg);
    
    // Draw background circle
    const bgCircle = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
    bgCircle.setAttribute('cx', '100');
    bgCircle.setAttribute('cy', '100');
    bgCircle.setAttribute('r', '80');
    bgCircle.setAttribute('fill', '#f0f0f0');
    bgCircle.setAttribute('stroke', '#ddd');
    bgCircle.setAttribute('stroke-width', '10');
    svg.appendChild(bgCircle);
    
    // Draw progress arc
    const endAngle = (percentage / 100) * 360;
    const startRad = (0 - 90) * Math.PI / 180;
    const endRad = (endAngle - 90) * Math.PI / 180;
    
    const x1 = 100 + 80 * Math.cos(startRad);
    const y1 = 100 + 80 * Math.sin(startRad);
    const x2 = 100 + 80 * Math.cos(endRad);
    const y2 = 100 + 80 * Math.sin(endRad);
    
    const largeArcFlag = endAngle > 180 ? 1 : 0;
    
    const path = document.createElementNS('http://www.w3.org/2000/svg', 'path');
    const d = [
        'M', 100, 100,
        'L', x1, y1,
        'A', 80, 80, 0, largeArcFlag, 1, x2, y2,
        'Z'
    ].join(' ');
    
    path.setAttribute('d', d);
    path.setAttribute('fill', percentage > 90 ? '#e74c3c' : 
                        percentage > 75 ? '#f39c12' : '#27ae60');
    svg.appendChild(path);
    
    // Add percentage text
    const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
    text.setAttribute('x', '100');
    text.setAttribute('y', '100');
    text.setAttribute('text-anchor', 'middle');
    text.setAttribute('dominant-baseline', 'middle');
    text.setAttribute('font-size', '24');
    text.setAttribute('font-weight', 'bold');
    text.setAttribute('fill', percentage > 90 ? '#e74c3c' : 
                        percentage > 75 ? '#f39c12' : '#27ae60');
    text.textContent = \`\${percentage.toFixed(1)}%\`;
    svg.appendChild(text);
    
    // Add label
    const label = document.createElementNS('http://www.w3.org/2000/svg', 'text');
    label.setAttribute('x', '100');
    label.setAttribute('y', '130');
    label.setAttribute('text-anchor', 'middle');
    label.setAttribute('font-size', '12');
    label.setAttribute('fill', '#666');
    label.textContent = 'Budget Used';
    svg.appendChild(label);
}
}
