<h3>Integrating Charts into Dashboard</h3>

<b>Update FinanceDashboard Class:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
// Add to FinanceDashboard constructor
this.chartRenderer = new ChartRenderer();

// Add to initializeDashboard method
this.setupCharts();

// Add new methods to FinanceDashboard
setupCharts() {
// Initial chart rendering
this.updateCharts();

// Update charts when transactions change
window.addEventListener('resize', () => {
    setTimeout(() => this.updateCharts(), 100);
});
}

updateCharts() {
// Category spending chart
this.chartRenderer.renderCategoryChart('category-chart', this.transactions);

// Monthly overview chart
this.chartRenderer.renderMonthlyChart('monthly-chart', this.transactions);

// Budget progress chart
const monthlyExpenses = this.getCurrentMonthExpenses();
this.chartRenderer.renderBudgetChart('budget-progress', monthlyExpenses, this.monthlyBudget);

// Update budget progress bar
const progress = document.getElementById('budget-progress');
const percentage = this.monthlyBudget > 0 ? 
    Math.min(100, (monthlyExpenses / this.monthlyBudget * 100)) : 0;
progress.style.width = \`\${percentage}%\`;

// Change color based on percentage
if (percentage > 90) {
    progress.style.background = '#e74c3c';
} else if (percentage > 75) {
    progress.style.background = '#f39c12';
} else {
    progress.style.background = '#27ae60';
}
}

getCurrentMonthExpenses() {
const now = new Date();
const currentMonth = now.getMonth();
const currentYear = now.getFullYear();

return this.transactions
    .filter(t => t.type === 'expense')
    .reduce((total, t) => {
        const date = new Date(t.date);
        if (date.getMonth() === currentMonth && date.getFullYear() === currentYear) {
            return total + t.amount;
        }
        return total;
    }, 0);
}

// Update the updateDashboard method
updateDashboard() {
// Calculate totals
const totals = this.calculateTotals();

// Update display elements
document.getElementById('total-income').textContent = \`$\${totals.income.toFixed(2)}\`;
document.getElementById('total-expenses').textContent = \`$\${totals.expenses.toFixed(2)}\`;
document.getElementById('total-savings').textContent = \`$\${totals.savings.toFixed(2)}\`;
document.getElementById('monthly-budget').textContent = \`$\${this.monthlyBudget.toFixed(2)}\`;

// Update charts
this.updateCharts();

// Update transactions table
this.updateTransactionsTable();

// Update savings goal
this.updateSavingsGoal();
}

calculateTotals() {
const totals = {
    income: 0,
    expenses: 0,
    savings: 0
};

this.transactions.forEach(transaction => {
    if (transaction.type === 'income') {
        totals.income += transaction.amount;
    } else if (transaction.type === 'expense') {
        totals.expenses += transaction.amount;
    }
});

totals.savings = totals.income - totals.expenses;
return totals;
}
