<h3>Implementing Robust Data Persistence</h3>

<b>Enhanced localStorage Implementation:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
// Add to FinanceDashboard class
class FinanceDashboard {
constructor() {
    // ... existing code ...
    
    this.STORAGE_KEYS = {
        TRANSACTIONS: 'finance_dashboard_transactions',
        BUDGET: 'finance_dashboard_budget',
        SAVINGS_GOAL: 'finance_dashboard_savings_goal',
        SETTINGS: 'finance_dashboard_settings',
        CATEGORIES: 'finance_dashboard_categories'
    };
    
    this.migrationVersion = 1;
    this.checkAndMigrateData();
}

// Improved data loading with versioning
loadFromLocalStorage() {
    try {
        // Load transactions
        const transactionsData = localStorage.getItem(this.STORAGE_KEYS.TRANSACTIONS);
        if (transactionsData) {
            const parsed = JSON.parse(transactionsData);
            this.transactions = this.validateTransactions(parsed) || [];
        }
        
        // Load budget
        const budgetData = localStorage.getItem(this.STORAGE_KEYS.BUDGET);
        if (budgetData) {
            this.monthlyBudget = parseFloat(budgetData) || 3000;
        }
        
        // Load savings goal
        const savingsData = localStorage.getItem(this.STORAGE_KEYS.SAVINGS_GOAL);
        if (savingsData) {
            const parsed = JSON.parse(savingsData);
            this.savingsGoal = parsed.amount || 10000;
            this.currentSavings = parsed.current || 0;
        }
        
        // Load settings
        const settingsData = localStorage.getItem(this.STORAGE_KEYS.SETTINGS);
        if (settingsData) {
            this.settings = JSON.parse(settingsData);
        } else {
            this.settings = this.getDefaultSettings();
        }
        
        // Load custom categories
        const categoriesData = localStorage.getItem(this.STORAGE_KEYS.CATEGORIES);
        if (categoriesData) {
            const customCats = JSON.parse(categoriesData);
            this.categories = { ...this.categories, ...customCats };
        }
        
        console.log('Data loaded successfully from localStorage');
    } catch (error) {
        console.error('Error loading from localStorage:', error);
        this.resetToDefaults();
    }
}

// Improved data saving with compression and backup
saveToLocalStorage() {
    try {
        // Create backup before saving
        this.createBackup();
        
        // Save transactions (with compression for large datasets)
        const transactionsToSave = this.compressTransactions(this.transactions);
        localStorage.setItem(this.STORAGE_KEYS.TRANSACTIONS, 
            JSON.stringify(transactionsToSave));
        
        // Save budget
        localStorage.setItem(this.STORAGE_KEYS.BUDGET, 
            this.monthlyBudget.toString());
        
        // Save savings goal
        const savingsData = {
            amount: this.savingsGoal,
            current: this.currentSavings,
            lastUpdated: new Date().toISOString()
        };
        localStorage.setItem(this.STORAGE_KEYS.SAVINGS_GOAL, 
            JSON.stringify(savingsData));
        
        // Save settings
        localStorage.setItem(this.STORAGE_KEYS.SETTINGS, 
            JSON.stringify(this.settings));
        
        // Save custom categories
        const customCategories = {};
        Object.keys(this.categories).forEach(key => {
            if (!this.getDefaultCategories().hasOwnProperty(key)) {
                customCategories[key] = this.categories[key];
            }
        });
        
        if (Object.keys(customCategories).length > 0) {
            localStorage.setItem(this.STORAGE_KEYS.CATEGORIES, 
                JSON.stringify(customCategories));
        }
        
        // Update last sync timestamp
        localStorage.setItem('last_sync', new Date().toISOString());
        
        console.log('Data saved successfully to localStorage');
        this.showNotification('Data saved successfully', 'success');
        
    } catch (error) {
        console.error('Error saving to localStorage:', error);
        this.showNotification('Error saving data', 'error');
        
        // Check storage quota
        if (error.name === 'QuotaExceededError') {
            this.handleStorageQuotaExceeded();
        }
    }
}

// Transaction compression for large datasets
compressTransactions(transactions) {
    return transactions.map(t => ({
        i: t.id,
        t: t.type,
        a: t.amount,
        d: t.description,
        c: t.category,
        dt: t.date,
        ts: t.timestamp || Date.now()
    }));
}

decompressTransactions(compressed) {
    return compressed.map(t => ({
        id: t.i,
        type: t.t,
        amount: t.a,
        description: t.d,
        category: t.c,
        date: t.dt,
        timestamp: t.ts
    }));
}

// Data validation
validateTransactions(transactions) {
    if (!Array.isArray(transactions)) return null;
    
    const validTransactions = [];
    const seenIds = new Set();
    
    transactions.forEach(t => {
        // Check if transaction has required fields
        if (!t.id || !t.type || !t.amount || !t.description || !t.category || !t.date) {
            console.warn('Invalid transaction skipped:', t);
            return;
        }
        
        // Check for duplicate IDs
        if (seenIds.has(t.id)) {
            console.warn('Duplicate transaction ID skipped:', t.id);
            return;
        }
        
        // Validate data types
        if (typeof t.amount !== 'number' || t.amount <= 0) {
            console.warn('Invalid amount skipped:', t.amount);
            return;
        }
        
        if (!['income', 'expense'].includes(t.type)) {
            console.warn('Invalid transaction type skipped:', t.type);
            return;
        }
        
        // Validate date format
        if (!this.isValidDate(t.date)) {
            console.warn('Invalid date skipped:', t.date);
            return;
        }
        
        seenIds.add(t.id);
        validTransactions.push(t);
    });
    
    return validTransactions;
}

isValidDate(dateString) {
    const date = new Date(dateString);
    return date instanceof Date && !isNaN(date);
}

// Backup system
createBackup() {
    const backupKey = 'finance_dashboard_backup_' + 
        new Date().toISOString().split('T')[0];
    
    const backupData = {
        transactions: this.transactions,
        budget: this.monthlyBudget,
        savingsGoal: this.savingsGoal,
        currentSavings: this.currentSavings,
        settings: this.settings,
        timestamp: new Date().toISOString(),
        version: this.migrationVersion
    };
    
    // Keep only last 7 backups
    const backups = this.getBackupList();
    if (backups.length >= 7) {
        localStorage.removeItem(backups[0]);
    }
    
    localStorage.setItem(backupKey, JSON.stringify(backupData));
}

getBackupList() {
    const backups = [];
    for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        if (key.startsWith('finance_dashboard_backup_')) {
            backups.push(key);
        }
    }
    return backups.sort();
}

restoreFromBackup(backupKey) {
    try {
        const backupData = JSON.parse(localStorage.getItem(backupKey));
        if (!backupData) throw new Error('Backup not found');
        
        this.transactions = backupData.transactions || [];
        this.monthlyBudget = backupData.budget || 3000;
        this.savingsGoal = backupData.savingsGoal || 10000;
        this.currentSavings = backupData.currentSavings || 0;
        this.settings = backupData.settings || this.getDefaultSettings();
        
        this.saveToLocalStorage();
        this.updateDashboard();
        this.showNotification('Backup restored successfully', 'success');
        
    } catch (error) {
        console.error('Error restoring backup:', error);
        this.showNotification('Error restoring backup', 'error');
    }
}

// Data migration for future updates
checkAndMigrateData() {
    const dataVersion = localStorage.getItem('data_version') || 0;
    
    if (dataVersion < this.migrationVersion) {
        console.log('Migrating data from version', dataVersion, 'to', this.migrationVersion);
        this.migrateData(dataVersion, this.migrationVersion);
        localStorage.setItem('data_version', this.migrationVersion.toString());
    }
}

migrateData(fromVersion, toVersion) {
    // Migration logic for different versions
    if (fromVersion === 0 && toVersion === 1) {
        // Convert old transaction format to new format
        const oldTransactions = localStorage.getItem('transactions');
        if (oldTransactions) {
            const parsed = JSON.parse(oldTransactions);
            if (Array.isArray(parsed)) {
                const migrated = parsed.map(t => ({
                    ...t,
                    timestamp: t.timestamp || Date.now()
                }));
                localStorage.setItem(this.STORAGE_KEYS.TRANSACTIONS, 
                    JSON.stringify(migrated));
                localStorage.removeItem('transactions');
            }
        }
    }
}

// Handle storage quota exceeded
handleStorageQuotaExceeded() {
    // Clear old backups
    const backups = this.getBackupList();
    const backupsToRemove = Math.floor(backups.length / 2);
    
    for (let i = 0; i < backupsToRemove; i++) {
        localStorage.removeItem(backups[i]);
    }
    
    // Try saving again
    setTimeout(() => this.saveToLocalStorage(), 100);
}

// Default settings
getDefaultSettings() {
    return {
        currency: 'USD',
        dateFormat: 'MM/DD/YYYY',
        theme: 'light',
        notifications: true,
        autoSave: true,
        backupFrequency: 'daily',
        defaultView: 'dashboard',
        chartAnimation: true
    };
}

getDefaultCategories() {
    return {
        food: { name: 'Food', color: '#FF6384', icon: 'fas fa-utensils' },
        transportation: { name: 'Transportation', color: '#36A2EB', icon: 'fas fa-car' },
        entertainment: { name: 'Entertainment', color: '#FFCE56', icon: 'fas fa-film' },
        shopping: { name: 'Shopping', color: '#4BC0C0', icon: 'fas fa-shopping-bag' },
        bills: { name: 'Bills', color: '#9966FF', icon: 'fas fa-file-invoice' },
        salary: { name: 'Salary', color: '#FF9F40', icon: 'fas fa-money-check-alt' },
        freelance: { name: 'Freelance', color: '#C9CBCF', icon: 'fas fa-laptop-code' }
    };
}

resetToDefaults() {
    this.transactions = [];
    this.monthlyBudget = 3000;
    this.savingsGoal = 10000;
    this.currentSavings = 0;
    this.settings = this.getDefaultSettings();
    this.categories = this.getDefaultCategories();
    
    this.showNotification('Reset to default settings', 'info');
    this.updateDashboard();
}
}
</pre>
