<h3>Advanced Search & Filter System</h3>

<b>Implementing Comprehensive Search:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
// Add to FinanceDashboard class
class FinanceDashboard {
// ... existing code ...

setupSearchAndFilter() {
    // Search input
    const searchInput = document.querySelector('.search-bar input');
    searchInput.addEventListener('input', (e) => {
        this.filterTransactions(e.target.value);
    });
    
    // Advanced filter button
    const filterBtn = document.getElementById('advanced-filter-btn') || 
                        this.createAdvancedFilterButton();
    filterBtn.addEventListener('click', () => {
        this.openAdvancedFilterModal();
    });
    
    // Quick filters
    this.setupQuickFilters();
}

createAdvancedFilterButton() {
    const filterBtn = document.createElement('button');
    filterBtn.id = 'advanced-filter-btn';
    filterBtn.className = 'btn-filter';
    filterBtn.innerHTML = '<i class="fas fa-filter"></i> Advanced Filter';
    
    const searchBar = document.querySelector('.search-bar');
    searchBar.parentNode.insertBefore(filterBtn, searchBar.nextSibling);
    
    return filterBtn;
}

filterTransactions(searchTerm) {
    const filtered = this.transactions.filter(transaction => {
        // Search in description
        if (transaction.description.toLowerCase().includes(searchTerm.toLowerCase())) {
            return true;
        }
        
        // Search in category
        if (this.categories[transaction.category]?.name.toLowerCase().includes(searchTerm.toLowerCase())) {
            return true;
        }
        
        // Search in amount
        if (transaction.amount.toString().includes(searchTerm)) {
            return true;
        }
        
        // Search in date
        if (transaction.date.includes(searchTerm)) {
            return true;
        }
        
        return false;
    });
    
    this.displayFilteredTransactions(filtered);
}

setupQuickFilters() {
    const quickFilters = [
        { id: 'filter-today', label: 'Today', filter: this.filterToday.bind(this) },
        { id: 'filter-week', label: 'This Week', filter: this.filterThisWeek.bind(this) },
        { id: 'filter-month', label: 'This Month', filter: this.filterThisMonth.bind(this) },
        { id: 'filter-income', label: 'Income Only', filter: this.filterIncome.bind(this) },
        { id: 'filter-expenses', label: 'Expenses Only', filter: this.filterExpenses.bind(this) },
        { id: 'filter-large', label: 'Large (> $100)', filter: this.filterLarge.bind(this) }
    ];
    
    const filterContainer = document.createElement('div');
    filterContainer.className = 'quick-filters';
    
    quickFilters.forEach(filter => {
        const btn = document.createElement('button');
        btn.className = 'quick-filter-btn';
        btn.textContent = filter.label;
        btn.addEventListener('click', () => {
            filter.filter();
            this.highlightActiveFilter(btn);
        });
        filterContainer.appendChild(btn);
    });
    
    const transactionsSection = document.querySelector('.transactions');
    transactionsSection.insertBefore(filterContainer, 
        transactionsSection.querySelector('.transactions-table'));
}

filterToday() {
    const today = new Date().toISOString().split('T')[0];
    const filtered = this.transactions.filter(t => t.date === today);
    this.displayFilteredTransactions(filtered);
}

filterThisWeek() {
    const now = new Date();
    const startOfWeek = new Date(now.setDate(now.getDate() - now.getDay()));
    const filtered = this.transactions.filter(t => {
        const date = new Date(t.date);
        return date >= startOfWeek;
    });
    this.displayFilteredTransactions(filtered);
}

filterThisMonth() {
    const now = new Date();
    const currentMonth = now.getMonth();
    const currentYear = now.getFullYear();
    
    const filtered = this.transactions.filter(t => {
        const date = new Date(t.date);
        return date.getMonth() === currentMonth && 
                date.getFullYear() === currentYear;
    });
    this.displayFilteredTransactions(filtered);
}

filterIncome() {
    const filtered = this.transactions.filter(t => t.type === 'income');
    this.displayFilteredTransactions(filtered);
}

filterExpenses() {
    const filtered = this.transactions.filter(t => t.type === 'expense');
    this.displayFilteredTransactions(filtered);
}

filterLarge() {
    const filtered = this.transactions.filter(t => t.amount > 100);
    this.displayFilteredTransactions(filtered);
}

highlightActiveFilter(activeBtn) {
    document.querySelectorAll('.quick-filter-btn').forEach(btn => {
        btn.classList.remove('active');
    });
    activeBtn.classList.add('active');
}

displayFilteredTransactions(filtered) {
    const tbody = document.getElementById('transactions-body');
    tbody.innerHTML = '';
    
    if (filtered.length === 0) {
        tbody.innerHTML = \`
            <tr>
                <td colspan="5" class="no-results">
                    <i class="fas fa-search"></i>
                    <p>No transactions found</p>
                </td>
            </tr>
        \`;
        return;
    }
    
    filtered.forEach(transaction => {
        const row = this.createTransactionRow(transaction);
        tbody.appendChild(row);
    });
}

openAdvancedFilterModal() {
    // Create and show advanced filter modal
    const modal = document.getElementById('advanced-filter-modal') || 
                    this.createAdvancedFilterModal();
    modal.style.display = 'flex';
}

createAdvancedFilterModal() {
    const modal = document.createElement('div');
    modal.id = 'advanced-filter-modal';
    modal.className = 'modal';
    
    modal.innerHTML = \`
        <div class="modal-content">
            <div class="modal-header">
                <h3>Advanced Filter</h3>
                <button class="close-modal">&times;</button>
            </div>
            <div class="modal-body">
                <form id="advanced-filter-form">
                    <div class="filter-section">
                        <h4>Date Range</h4>
                        <div class="form-group">
                            <label for="filter-start-date">From</label>
                            <input type="date" id="filter-start-date">
                        </div>
                        <div class="form-group">
                            <label for="filter-end-date">To</label>
                            <input type="date" id="filter-end-date">
                        </div>
                    </div>
                    
                    <div class="filter-section">
                        <h4>Amount Range</h4>
                        <div class="form-group">
                            <label for="filter-min-amount">Minimum Amount</label>
                            <input type="number" id="filter-min-amount" step="0.01" min="0">
                        </div>
                        <div class="form-group">
                            <label for="filter-max-amount">Maximum Amount</label>
                            <input type="number" id="filter-max-amount" step="0.01" min="0">
                        </div>
                    </div>
                    
                    <div class="filter-section">
                        <h4>Categories</h4>
                        <div class="category-checkboxes">
                            \${Object.entries(this.categories).map(([key, cat]) => \`
                                <label class="checkbox-label">
                                    <input type="checkbox" name="categories" value="\${key}" checked>
                                    <span class="checkmark"></span>
                                    <i class="\${cat.icon}"></i>
                                    \${cat.name}
                                </label>
                            \`).join('')}
                        </div>
                    </div>
                    
                    <div class="filter-section">
                        <h4>Transaction Type</h4>
                        <label class="radio-label">
                            <input type="radio" name="type" value="all" checked>
                            <span class="radiomark"></span>
                            All Transactions
                        </label>
                        <label class="radio-label">
                            <input type="radio" name="type" value="income">
                            <span class="radiomark"></span>
                            Income Only
                        </label>
                        <label class="radio-label">
                            <input type="radio" name="type" value="expense">
                            <span class="radiomark"></span>
                            Expenses Only
                        </label>
                    </div>
                    
                    <div class="filter-actions">
                        <button type="button" class="btn-secondary" id="reset-filters">Reset</button>
                        <button type="submit" class="btn-primary">Apply Filters</button>
                    </div>
                </form>
            </div>
        </div>
    \`;
    
    document.body.appendChild(modal);
    
    // Add event listeners
    modal.querySelector('.close-modal').addEventListener('click', () => {
        modal.style.display = 'none';
    });
    
    modal.addEventListener('click', (e) => {
        if (e.target === modal) {
            modal.style.display = 'none';
        }
    });
    
    document.getElementById('advanced-filter-form').addEventListener('submit', (e) => {
        e.preventDefault();
        this.applyAdvancedFilters();
        modal.style.display = 'none';
    });
    
    document.getElementById('reset-filters').addEventListener('click', () => {
        this.resetFilters();
        modal.style.display = 'none';
    });
    
    return modal;
}

applyAdvancedFilters() {
    const form = document.getElementById('advanced-filter-form');
    const formData = new FormData(form);
    
    const filters = {
        startDate: formData.get('filter-start-date'),
        endDate: formData.get('filter-end-date'),
        minAmount: parseFloat(formData.get('filter-min-amount')) || null,
        maxAmount: parseFloat(formData.get('filter-max-amount')) || null,
        categories: formData.getAll('categories'),
        type: formData.get('type')
    };
    
    const filtered = this.transactions.filter(transaction => {
        // Date filter
        if (filters.startDate && transaction.date < filters.startDate) return false;
        if (filters.endDate && transaction.date > filters.endDate) return false;
        
        // Amount filter
        if (filters.minAmount !== null && transaction.amount < filters.minAmount) return false;
        if (filters.maxAmount !== null && transaction.amount > filters.maxAmount) return false;
        
        // Category filter
        if (filters.categories.length > 0 && !filters.categories.includes(transaction.category)) {
            return false;
        }
        
        // Type filter
        if (filters.type !== 'all' && transaction.type !== filters.type) return false;
        
        return true;
    });
    
    this.displayFilteredTransactions(filtered);
    this.showNotification(\`Found \${filtered.length} transactions\`, 'info');
}

resetFilters() {
    this.displayFilteredTransactions(this.transactions);
    document.querySelector('.search-bar input').value = '';
    this.showNotification('Filters reset', 'info');
}
}
