<h3>Import/Export Functionality</h3>

<b>Data Import/Export Implementation:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
// Add to FinanceDashboard class
class FinanceDashboard {
// ... existing code ...

setupImportExport() {
    // Create import/export buttons if they don't exist
    if (!document.getElementById('export-btn')) {
        this.createImportExportButtons();
    }
}

createImportExportButtons() {
    const actionsContainer = document.querySelector('.user-info') || 
                            document.querySelector('.top-bar');
    
    const exportBtn = document.createElement('button');
    exportBtn.id = 'export-btn';
    exportBtn.className = 'btn-action';
    exportBtn.innerHTML = '<i class="fas fa-download"></i> Export';
    exportBtn.addEventListener('click', () => this.exportData());
    
    const importBtn = document.createElement('button');
    importBtn.id = 'import-btn';
    importBtn.className = 'btn-action';
    importBtn.innerHTML = '<i class="fas fa-upload"></i> Import';
    importBtn.addEventListener('click', () => this.importData());
    
    // Create file input for import (hidden)
    const fileInput = document.createElement('input');
    fileInput.type = 'file';
    fileInput.id = 'import-file';
    fileInput.accept = '.json,.csv';
    fileInput.style.display = 'none';
    fileInput.addEventListener('change', (e) => this.handleFileImport(e));
    
    // Trigger file input when import button is clicked
    importBtn.addEventListener('click', () => fileInput.click());
    
    actionsContainer.appendChild(exportBtn);
    actionsContainer.appendChild(importBtn);
    document.body.appendChild(fileInput);
}

exportData(format = 'json') {
    const exportData = {
        transactions: this.transactions,
        budget: this.monthlyBudget,
        savingsGoal: this.savingsGoal,
        currentSavings: this.currentSavings,
        settings: this.settings,
        categories: this.categories,
        exportDate: new Date().toISOString(),
        version: this.migrationVersion
    };
    
    let data, filename, mimeType;
    
    if (format === 'json') {
        data = JSON.stringify(exportData, null, 2);
        filename = \`finance-data-\${new Date().toISOString().split('T')[0]}.json\`;
        mimeType = 'application/json';
    } else if (format === 'csv') {
        data = this.convertToCSV(exportData);
        filename = \`finance-data-\${new Date().toISOString().split('T')[0]}.csv\`;
        mimeType = 'text/csv';
    }
    
    // Create download link
    const blob = new Blob([data], { type: mimeType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
    
    this.showNotification(\`Data exported as \${format.toUpperCase()}\`, 'success');
}

convertToCSV(data) {
    // Convert transactions to CSV
    const headers = ['Date', 'Type', 'Category', 'Description', 'Amount'];
    const rows = data.transactions.map(t => [
        t.date,
        t.type,
        this.categories[t.category]?.name || t.category,
        \`"\${t.description.replace(/"/g, '""')}"\`,
        t.amount.toFixed(2)
    ]);
    
    return [headers.join(','), ...rows.map(row => row.join(','))].join(' ');
}

handleFileImport(event) {
    const file = event.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    
    reader.onload = (e) => {
        try {
            const content = e.target.result;
            let importedData;
            
            if (file.name.endsWith('.json')) {
                importedData = JSON.parse(content);
            } else if (file.name.endsWith('.csv')) {
                importedData = this.parseCSV(content);
            } else {
                throw new Error('Unsupported file format');
            }
            
            this.confirmImport(importedData);
        } catch (error) {
            this.showNotification('Error importing file: ' + error.message, 'error');
        }
    };
    
    reader.onerror = () => {
        this.showNotification('Error reading file', 'error');
    };
    
    reader.readAsText(file);
    
    // Reset file input
    event.target.value = '';
}

parseCSV(csvText) {
    const lines = csvText.split(' ');
    const headers = lines[0].split(',').map(h => h.trim());
    
    const transactions = [];
    for (let i = 1; i < lines.length; i++) {
        if (!lines[i].trim()) continue;
        
        const values = this.parseCSVLine(lines[i]);
        if (values.length !== headers.length) continue;
        
        const transaction = {};
        headers.forEach((header, index) => {
            transaction[header.toLowerCase()] = values[index];
        });
        
        // Convert to proper format
        transactions.push({
            id: Date.now() + i,
            date: transaction.date,
            type: transaction.type.toLowerCase(),
            category: this.findCategoryByName(transaction.category),
            description: transaction.description.replace(/^"|"$/g, ''),
            amount: parseFloat(transaction.amount),
            timestamp: Date.now()
        });
    }
    
    return { transactions };
}

parseCSVLine(line) {
    const values = [];
    let current = '';
    let inQuotes = false;
    
    for (let i = 0; i < line.length; i++) {
        const char = line[i];
        const nextChar = line[i + 1];
        
        if (char === '"') {
            if (inQuotes && nextChar === '"') {
                current += '"';
                i++; // Skip next quote
            } else {
                inQuotes = !inQuotes;
            }
        } else if (char === ',' && !inQuotes) {
            values.push(current);
            current = '';
        } else {
            current += char;
        }
    }
    
    values.push(current);
    return values;
}

findCategoryByName(categoryName) {
    const found = Object.entries(this.categories).find(
        ([key, cat]) => cat.name === categoryName
    );
    return found ? found[0] : 'other';
}

confirmImport(importedData) {
    const modal = document.createElement('div');
    modal.className = 'modal';
    modal.innerHTML = \`
        <div class="modal-content">
            <div class="modal-header">
                <h3>Confirm Import</h3>
            </div>
            <div class="modal-body">
                <p>Import \${importedData.transactions?.length || 0} transactions?</p>
                <div class="import-options">
                    <label class="radio-label">
                        <input type="radio" name="import-mode" value="merge" checked>
                        <span class="radiomark"></span>
                        Merge with existing data
                    </label>
                    <label class="radio-label">
                        <input type="radio" name="import-mode" value="replace">
                        <span class="radiomark"></span>
                        Replace existing data
                    </label>
                </div>
            </div>
            <div class="modal-footer">
                <button class="btn-secondary" id="cancel-import">Cancel</button>
                <button class="btn-primary" id="confirm-import">Import</button>
            </div>
        </div>
    \`;
    
    document.body.appendChild(modal);
    modal.style.display = 'flex';
    
    modal.querySelector('#cancel-import').addEventListener('click', () => {
        document.body.removeChild(modal);
    });
    
    modal.querySelector('#confirm-import').addEventListener('click', () => {
        const mode = modal.querySelector('input[name="import-mode"]:checked').value;
        this.processImport(importedData, mode);
        document.body.removeChild(modal);
    });
    
    modal.addEventListener('click', (e) => {
        if (e.target === modal) {
            document.body.removeChild(modal);
        }
    });
}

processImport(importedData, mode) {
    try {
        if (mode === 'replace') {
            this.transactions = importedData.transactions || [];
        } else if (mode === 'merge') {
            const existingIds = new Set(this.transactions.map(t => t.id));
            const newTransactions = (importedData.transactions || []).filter(
                t => !existingIds.has(t.id)
            );
            this.transactions.push(...newTransactions);
        }
        
        if (importedData.budget) this.monthlyBudget = importedData.budget;
        if (importedData.savingsGoal) this.savingsGoal = importedData.savingsGoal;
        if (importedData.currentSavings) this.currentSavings = importedData.currentSavings;
        if (importedData.settings) this.settings = { ...this.settings, ...importedData.settings };
        if (importedData.categories) this.categories = { ...this.categories, ...importedData.categories };
        
        this.saveToLocalStorage();
        this.updateDashboard();
        this.showNotification('Data imported successfully', 'success');
        
    } catch (error) {
        this.showNotification('Error importing data: ' + error.message, 'error');
    }
}
}
