<h3>Performance Optimization</h3>

<b>Optimization Techniques Implementation:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
class PerformanceOptimizer {
    constructor(dashboard) {
        this.dashboard = dashboard;
        this.debounceTimers = {};
        this.cache = new Map();
        this.optimizeDashboard();
    }

    optimizeDashboard() {
        this.optimizeEventListeners();
        this.optimizeRendering();
        this.optimizeMemoryUsage();
        this.setupPerformanceMonitoring();
    }

    // 1. Event Listener Optimization
    optimizeEventListeners() {
        // Debounce search input
        const searchInput = document.querySelector('.search-bar input');
        this.debounce(searchInput, 'input', 300, (e) => {
            this.dashboard.filterTransactions(e.target.value);
        });

        // Throttle window resize for charts
        this.throttle(window, 'resize', 200, () => {
            this.dashboard.updateCharts();
        });

        // Use event delegation for transaction actions
        const transactionsBody = document.getElementById('transactions-body');
        transactionsBody.addEventListener('click', (e) => {
            const target = e.target;
            
            if (target.classList.contains('delete-btn')) {
                const id = parseInt(target.dataset.id);
                this.dashboard.deleteTransaction(id);
            }
            
            if (target.classList.contains('edit-btn')) {
                const id = parseInt(target.dataset.id);
                this.dashboard.editTransaction(id);
            }
            
            if (target.classList.contains('checkbox-complete')) {
                const id = parseInt(target.dataset.id);
                this.dashboard.toggleTransactionComplete(id);
            }
        });
    }

    debounce(element, event, delay, callback) {
        let timer;
        element.addEventListener(event, (e) => {
            clearTimeout(timer);
            timer = setTimeout(() => callback(e), delay);
        });
    }

    throttle(element, event, limit, callback) {
        let inThrottle;
        element.addEventListener(event, (e) => {
            if (!inThrottle) {
                callback(e);
                inThrottle = true;
                setTimeout(() => inThrottle = false, limit);
            }
        });
    }

    // 2. Rendering Optimization
    optimizeRendering() {
        // Virtual scrolling for large transaction lists
        this.setupVirtualScrolling();
        
        // Lazy loading for charts
        this.setupLazyLoading();
        
        // Cache DOM queries
        this.cacheDOMElements();
    }

    setupVirtualScrolling() {
        const container = document.querySelector('.transactions-table');
        const tbody = document.getElementById('transactions-body');
        
        let visibleRows = 50;
        let startIndex = 0;
        
        const renderVisibleRows = () => {
            const transactions = this.dashboard.transactions;
            const endIndex = Math.min(startIndex + visibleRows, transactions.length);
            
            // Clear and render only visible rows
            tbody.innerHTML = '';
            
            for (let i = startIndex; i < endIndex; i++) {
                const row = this.dashboard.createTransactionRow(transactions[i]);
                tbody.appendChild(row);
            }
            
            // Update scroll indicator
            this.updateScrollIndicator(startIndex, transactions.length);
        };
        
        container.addEventListener('scroll', () => {
            const scrollTop = container.scrollTop;
            const rowHeight = 50; // Approximate row height
            const newStartIndex = Math.floor(scrollTop / rowHeight);
            
            if (newStartIndex !== startIndex) {
                startIndex = newStartIndex;
                renderVisibleRows();
            }
        });
        
        // Initial render
        renderVisibleRows();
    }

    updateScrollIndicator(startIndex, totalRows) {
        const indicator = document.getElementById('scroll-indicator') || 
                         this.createScrollIndicator();
        
        const percent = (startIndex / totalRows * 100).toFixed(1);
        indicator.textContent = \`Showing rows \${startIndex + 1}-\${Math.min(startIndex + 50, totalRows)} of \${totalRows}\`;
    }

    createScrollIndicator() {
        const indicator = document.createElement('div');
        indicator.id = 'scroll-indicator';
        indicator.style.cssText = \`
            position: sticky;
            bottom: 0;
            background: rgba(0, 0, 0, 0.8);
            color: white;
            padding: 5px 10px;
            font-size: 12px;
            text-align: center;
            z-index: 100;
        \`;
        
        document.querySelector('.transactions').appendChild(indicator);
        return indicator;
    }

    setupLazyLoading() {
        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    const chartId = entry.target.id;
                    if (!this.cache.has(chartId)) {
                        this.loadChart(chartId);
                        this.cache.set(chartId, true);
                    }
                }
            });
        }, { threshold: 0.1 });
        
        // Observe chart containers
        document.querySelectorAll('.chart').forEach(chart => {
            observer.observe(chart);
        });
    }

    loadChart(chartId) {
        // Simulate chart loading with skeleton
        const chartElement = document.getElementById(chartId);
        if (chartElement) {
            chartElement.innerHTML = 
                <div class="chart-skeleton">
                    <div class="skeleton-line"></div>
                    <div class="skeleton-line"></div>
                    <div class="skeleton-line"></div>
                </div>
            ;
            
            // Simulate network delay
            setTimeout(() => {
                this.dashboard.updateCharts();
            }, 300);
        }
    }

    cacheDOMElements() {
        this.cachedElements = {
            totalIncome: document.getElementById('total-income'),
            totalExpenses: document.getElementById('total-expenses'),
            totalSavings: document.getElementById('total-savings'),
            monthlyBudget: document.getElementById('monthly-budget'),
            budgetProgress: document.getElementById('budget-progress'),
            transactionsBody: document.getElementById('transactions-body')
        };
    }

    // 3. Memory Optimization
    optimizeMemoryUsage() {
        // Clean up old data
        this.setupMemoryCleanup();
        
        // Optimize chart memory
        this.optimizeChartMemory();
        
        // Prevent memory leaks
        this.preventMemoryLeaks();
    }

    setupMemoryCleanup() {
        // Clean old transactions (keep last 2 years)
        const twoYearsAgo = new Date();
        twoYearsAgo.setFullYear(twoYearsAgo.getFullYear() - 2);
        
        const oldTransactions = this.dashboard.transactions.filter(t => {
            const date = new Date(t.date);
            return date < twoYearsAgo;
        });
        
        if (oldTransactions.length > 0) {
            console.log(\`Cleaning up \${oldTransactions.length} old transactions\`);
            this.dashboard.transactions = this.dashboard.transactions.filter(t => {
                const date = new Date(t.date);
                return date >= twoYearsAgo;
            });
            this.dashboard.saveToLocalStorage();
        }
        
        // Clean cache periodically
        setInterval(() => {
            this.cache.clear();
        }, 5 * 60 * 1000); // Every 5 minutes
    }

    optimizeChartMemory() {
        // Destroy and recreate charts instead of updating
        const chartMethods = {
            'category-chart': () => this.dashboard.chartRenderer.renderCategoryChart,
            'monthly-chart': () => this.dashboard.chartRenderer.renderMonthlyChart,
            'budget-progress': () => this.dashboard.chartRenderer.renderBudgetChart
        };
        
        // Store original methods
        Object.keys(chartMethods).forEach(key => {
            const original = chartMethods[key]();
            chartMethods[key] = {
                original,
                lastData: null,
                callCount: 0
            };
        });
    }

    preventMemoryLeaks() {
        // Remove event listeners on component unmount
        window.addEventListener('beforeunload', () => {
            this.cleanup();
        });
    }

    cleanup() {
        // Clear all timers
        Object.values(this.debounceTimers).forEach(timer => {
            clearTimeout(timer);
        });
        
        // Clear cache
        this.cache.clear();
        
        // Remove observers
        if (this.intersectionObserver) {
            this.intersectionObserver.disconnect();
        }
    }

    // 4. Performance Monitoring
    setupPerformanceMonitoring() {
        // Monitor FPS
        this.monitorFPS();
        
        // Monitor memory usage
        this.monitorMemory();
        
        // Log performance metrics
        this.logPerformanceMetrics();
    }

    monitorFPS() {
        let frameCount = 0;
        let lastTime = performance.now();
        let fps = 60;
        
        const checkFPS = () => {
            frameCount++;
            const currentTime = performance.now();
            
            if (currentTime - lastTime >= 1000) {
                fps = frameCount;
                frameCount = 0;
                lastTime = currentTime;
                
                if (fps < 30) {
                    console.warn(\`Low FPS: \${fps}\`);
                    this.throttleAnimations();
                }
            }
            
            requestAnimationFrame(checkFPS);
        };
        
        checkFPS();
    }

    throttleAnimations() {
        // Reduce animation quality when FPS is low
        document.documentElement.style.setProperty('--animation-duration', '0s');
        document.querySelectorAll('.chart').forEach(chart => {
            chart.style.willChange = 'auto';
        });
    }

    monitorMemory() {
        if ('memory' in performance) {
            setInterval(() => {
                const usedJSHeapSize = performance.memory.usedJSHeapSize;
                const totalJSHeapSize = performance.memory.totalJSHeapSize;
                const percent = (usedJSHeapSize / totalJSHeapSize * 100).toFixed(1);
                
                if (percent > 80) {
                    console.warn(\`High memory usage: \${percent}%\`);
                    this.triggerGarbageCollection();
                }
            }, 30000); // Check every 30 seconds
        }
    }

    triggerGarbageCollection() {
        if (window.gc) {
            window.gc();
        } else {
            // Force garbage collection by creating and releasing memory
            const largeArray = new Array(1000000).fill(0);
            largeArray.length = 0;
        }
    }

    logPerformanceMetrics() {
        // Log page load performance
        window.addEventListener('load', () => {
            setTimeout(() => {
                const timing = performance.timing;
                const loadTime = timing.loadEventEnd - timing.navigationStart;
                const domReadyTime = timing.domContentLoadedEventEnd - timing.navigationStart;
                
                console.log(\`Page load time: \${loadTime}ms\`);
                console.log(\`DOM ready time: \${domReadyTime}ms\`);
                
                // Log to analytics if available
                if (typeof ga !== 'undefined') {
                    ga('send', 'timing', 'Page Load', 'load', loadTime);
                    ga('send', 'timing', 'DOM Ready', 'ready', domReadyTime);
                }
            }, 0);
        });
    }
}
</pre>
