<h3>Accessibility Implementation</h3>

<b>Accessibility Enhancements:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
class AccessibilityEnhancer {
    constructor(dashboard) {
        this.dashboard = dashboard;
        this.setupAccessibility();
    }

    setupAccessibility() {
        this.enhanceKeyboardNavigation();
        this.addScreenReaderSupport();
        this.improveColorContrast();
        this.addAriaAttributes();
        this.setupFocusManagement();
    }

    // 1. Keyboard Navigation
    enhanceKeyboardNavigation() {
        // Make all interactive elements keyboard accessible
        document.querySelectorAll('button, input, select, [tabindex]').forEach(element => {
            if (!element.hasAttribute('tabindex')) {
                element.setAttribute('tabindex', '0');
            }
        });

        // Add keyboard shortcuts
        this.addKeyboardShortcuts();

        // Ensure proper focus order
        this.ensureFocusOrder();
    }

    addKeyboardShortcuts() {
        document.addEventListener('keydown', (e) => {
            // Skip if user is typing in an input
            if (e.target.tagName === 'INPUT' || e.target.tagName === 'TEXTAREA') {
                return;
            }

            // Ctrl/Cmd + N: New transaction
            if ((e.ctrlKey || e.metaKey) && e.key === 'n') {
                e.preventDefault();
                this.dashboard.openTransactionModal();
            }

            // Ctrl/Cmd + F: Focus search
            if ((e.ctrlKey || e.metaKey) && e.key === 'f') {
                e.preventDefault();
                document.querySelector('.search-bar input').focus();
            }

            // Ctrl/Cmd + S: Save data
            if ((e.ctrlKey || e.metaKey) && e.key === 's') {
                e.preventDefault();
                this.dashboard.saveToLocalStorage();
            }

            // Escape: Close modals
            if (e.key === 'Escape') {
                this.closeAllModals();
            }

            // Arrow keys for navigation
            if (e.key === 'ArrowDown' || e.key === 'ArrowUp') {
                this.navigateTransactions(e.key);
            }
        });
    }

    closeAllModals() {
        document.querySelectorAll('.modal').forEach(modal => {
            modal.style.display = 'none';
        });
    }

    navigateTransactions(key) {
        const rows = document.querySelectorAll('#transactions-body tr');
        const currentRow = document.activeElement.closest('tr');
        
        if (!currentRow) {
            rows[0]?.focus();
            return;
        }

        const currentIndex = Array.from(rows).indexOf(currentRow);
        let nextIndex;

        if (key === 'ArrowDown') {
            nextIndex = Math.min(currentIndex + 1, rows.length - 1);
        } else if (key === 'ArrowUp') {
            nextIndex = Math.max(currentIndex - 1, 0);
        }

        rows[nextIndex]?.focus();
    }

    ensureFocusOrder() {
        // Set tabindex in logical order
        const focusOrder = [
            '.sidebar .logo',
            '.nav-menu a',
            '.search-bar input',
            '.user-info button',
            '.stats-grid .stat-card',
            '.chart-container',
            '#add-transaction',
            '#transactions-body tr'
        ];

        focusOrder.forEach((selector, index) => {
            document.querySelectorAll(selector).forEach(element => {
                element.setAttribute('tabindex', (index + 1).toString());
            });
        });
    }

    // 2. Screen Reader Support
    addScreenReaderSupport() {
        // Add aria-live regions for dynamic content
        this.addAriaLiveRegions();

        // Add screen reader text for icons
        this.addScreenReaderText();

        // Announce changes to screen readers
        this.setupChangeAnnouncements();
    }

    addAriaLiveRegions() {
        const liveRegion = document.createElement('div');
        liveRegion.id = 'aria-live-region';
        liveRegion.setAttribute('aria-live', 'polite');
        liveRegion.setAttribute('aria-atomic', 'true');
        liveRegion.style.cssText = \`
            position: absolute;
            width: 1px;
            height: 1px;
            padding: 0;
            margin: -1px;
            overflow: hidden;
            clip: rect(0, 0, 0, 0);
            white-space: nowrap;
            border: 0;
        \`;
        document.body.appendChild(liveRegion);
    }

    announceToScreenReader(message) {
        const liveRegion = document.getElementById('aria-live-region');
        if (liveRegion) {
            liveRegion.textContent = message;
            // Clear after announcement
            setTimeout(() => {
                liveRegion.textContent = '';
            }, 1000);
        }
    }

    addScreenReaderText() {
        // Add text for icon-only buttons
        document.querySelectorAll('[class*="fa-"]').forEach(icon => {
            const parent = icon.parentElement;
            if (parent.tagName === 'BUTTON' && !parent.textContent.trim()) {
                const label = this.getIconLabel(icon.classList);
                if (label) {
                    const srText = document.createElement('span');
                    srText.className = 'sr-only';
                    srText.textContent = label;
                    parent.appendChild(srText);
                }
            }
        });
    }

    getIconLabel(classList) {
        const iconMap = {
            'fa-home': 'Dashboard',
            'fa-wallet': 'Expenses',
            'fa-money-bill-wave': 'Income',
            'fa-bullseye': 'Goals',
            'fa-chart-bar': 'Reports',
            'fa-cog': 'Settings',
            'fa-search': 'Search',
            'fa-bell': 'Notifications',
            'fa-plus': 'Add',
            'fa-edit': 'Edit',
            'fa-trash': 'Delete',
            'fa-download': 'Export',
            'fa-upload': 'Import',
            'fa-filter': 'Filter'
        };

        for (const iconClass of classList) {
            if (iconMap[iconClass]) {
                return iconMap[iconClass];
            }
        }
        return null;
    }

    setupChangeAnnouncements() {
        // Announce transaction additions
        const originalAddTransaction = this.dashboard.addTransaction;
        this.dashboard.addTransaction = function(transaction) {
            const result = originalAddTransaction.call(this, transaction);
            if (result) {
                this.accessibilityEnhancer.announceToScreenReader(
                    \`Transaction added: \${transaction.description} for $\${transaction.amount}\`
                );
            }
            return result;
        };

        // Announce data saves
        const originalSave = this.dashboard.saveToLocalStorage;
        this.dashboard.saveToLocalStorage = function() {
            originalSave.call(this);
            this.accessibilityEnhancer.announceToScreenReader('Data saved successfully');
        };
    }

    // 3. Color Contrast Improvements
    improveColorContrast() {
        // Check and fix contrast ratios
        this.fixContrastIssues();

        // Add high contrast mode
        this.addHighContrastMode();

        // Ensure proper color usage
        this.ensureColorMeaning();
    }

    fixContrastIssues() {
        // Check text elements for sufficient contrast
        document.querySelectorAll('*').forEach(element => {
            const style = window.getComputedStyle(element);
            const bgColor = style.backgroundColor;
            const textColor = style.color;
            
            // Simple contrast check (in real app, use proper contrast ratio calculation)
            if (this.isLowContrast(bgColor, textColor)) {
                element.style.color = this.getContrastColor(bgColor);
            }
        });
    }

    isLowContrast(bgColor, textColor) {
        // Simplified check - in production, use proper contrast ratio algorithm
        return bgColor === textColor || 
               (bgColor.includes('255,255,255') && textColor.includes('255,255,255')) ||
               (bgColor.includes('0,0,0') && textColor.includes('0,0,0'));
    }

    getContrastColor(bgColor) {
        // Return black or white based on background brightness
        if (bgColor.includes('255,255,255') || bgColor.includes('light')) {
            return '#000000';
        }
        return '#ffffff';
    }

    addHighContrastMode() {
        const highContrastToggle = document.createElement('button');
        highContrastToggle.id = 'high-contrast-toggle';
        highContrastToggle.className = 'accessibility-btn';
        highContrastToggle.innerHTML = '<i class="fas fa-adjust"></i><span class="sr-only">High Contrast</span>';
        highContrastToggle.addEventListener('click', () => this.toggleHighContrast());
        
        document.querySelector('.top-bar').appendChild(highContrastToggle);
    }

    toggleHighContrast() {
        document.body.classList.toggle('high-contrast');
        const isHighContrast = document.body.classList.contains('high-contrast');
        this.announceToScreenReader(isHighContrast ? 
            'High contrast mode enabled' : 'High contrast mode disabled');
    }

    ensureColorMeaning() {
        // Don't rely solely on color for meaning
        document.querySelectorAll('.trend.positive, .trend.negative').forEach(element => {
            if (!element.textContent.includes('‚Üë') && !element.textContent.includes('‚Üì')) {
                const isPositive = element.classList.contains('positive');
                const symbol = isPositive ? '‚Üë' : '‚Üì';
                element.textContent = symbol + ' ' + element.textContent;
            }
        });
    }

    // 4. ARIA Attributes
    addAriaAttributes() {
        // Add roles and labels
        this.addRoles();
        this.addLabels();
        this.addDescriptions();
    }

    addRoles() {
        // Main landmarks
        document.querySelector('nav.sidebar').setAttribute('role', 'navigation');
        document.querySelector('main.main-content').setAttribute('role', 'main');
        document.querySelector('header.top-bar').setAttribute('role', 'banner');
        
        // Charts
        document.querySelectorAll('.chart').forEach(chart => {
            chart.setAttribute('role', 'img');
            chart.setAttribute('aria-label', 'Data visualization chart');
        });
        
        // Tables
        document.querySelectorAll('table').forEach(table => {
            table.setAttribute('role', 'grid');
        });
    }

    addLabels() {
        // Form labels
        document.querySelectorAll('input, select, textarea').forEach(input => {
            if (!input.id) {
                input.id = \`input-\${Math.random().toString(36).substr(2, 9)}\`;
            }
            
            if (!document.querySelector(\`label[for="\${input.id}"]\`)) {
                const label = input.previousElementSibling;
                if (label && label.tagName === 'LABEL') {
                    label.setAttribute('for', input.id);
                }
            }
        });
    }

    addDescriptions() {
        // Add descriptions for complex elements
        const statsGrid = document.querySelector('.stats-grid');
        statsGrid.setAttribute('aria-label', 'Financial statistics overview');
        
        const transactionsTable = document.querySelector('.transactions-table');
        transactionsTable.setAttribute('aria-label', 'Recent transactions list');
        
        // Add instructions for screen reader users
        const instructions = document.createElement('div');
        instructions.id = 'screen-reader-instructions';
        instructions.className = 'sr-only';
        instructions.innerHTML = \`
            <p>Use arrow keys to navigate through transactions.</p>
            <p>Press Control+N to add a new transaction.</p>
            <p>Press Control+F to focus the search box.</p>
            <p>Press Escape to close any open dialogs.</p>
        \`;
        document.querySelector('main').prepend(instructions);
    }

    // 5. Focus Management
    setupFocusManagement() {
        // Manage focus for modals
        this.setupModalFocus();

        // Return focus after actions
        this.setupFocusReturn();

        // Skip to content link
        this.addSkipLink();
    }

    setupModalFocus() {
        document.querySelectorAll('.modal').forEach(modal => {
            const focusableElements = modal.querySelectorAll(
                'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
            );
            
            const firstElement = focusableElements[0];
            const lastElement = focusableElements[focusableElements.length - 1];
            
            modal.addEventListener('keydown', (e) => {
                if (e.key === 'Tab') {
                    if (e.shiftKey) {
                        // Shift + Tab
                        if (document.activeElement === firstElement) {
                            e.preventDefault();
                            lastElement.focus();
                        }
                    } else {
                        // Tab
                        if (document.activeElement === lastElement) {
                            e.preventDefault();
                            firstElement.focus();
                        }
                    }
                }
            });
        });
    }

    setupFocusReturn() {
        // Store last focused element before opening modal
        let lastFocusedElement;
        
        const originalOpenModal = this.dashboard.openTransactionModal;
        this.dashboard.openTransactionModal = function() {
            lastFocusedElement = document.activeElement;
            originalOpenModal.call(this);
            
            // Focus first element in modal
            const modal = document.getElementById('transaction-modal');
            const firstInput = modal.querySelector('input, select, textarea, button');
            setTimeout(() => firstInput?.focus(), 100);
        };
        
        const originalCloseModal = this.dashboard.closeTransactionModal;
        this.dashboard.closeTransactionModal = function() {
            originalCloseModal.call(this);
            // Return focus to last focused element
            setTimeout(() => lastFocusedElement?.focus(), 100);
        };
    }

    addSkipLink() {
        const skipLink = document.createElement('a');
        skipLink.href = '#main-content';
        skipLink.className = 'skip-link';
        skipLink.textContent = 'Skip to main content';
        
        document.body.insertBefore(skipLink, document.body.firstChild);
        
        // Add id to main content
        document.querySelector('main.main-content').id = 'main-content';
    }
}
