<h3>Error Handling & User Feedback</h3>

<b>Comprehensive Error Handling:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
class ErrorHandler {
    constructor(dashboard) {
        this.dashboard = dashboard;
        this.setupErrorHandling();
        this.setupUserFeedback();
    }

    setupErrorHandling() {
        // Global error handler
        window.addEventListener('error', this.handleGlobalError.bind(this));
        
        // Unhandled promise rejections
        window.addEventListener('unhandledrejection', this.handlePromiseRejection.bind(this));
        
        // Network errors
        this.setupNetworkErrorHandling();
        
        // localStorage errors
        this.setupStorageErrorHandling();
    }

    handleGlobalError(event) {
        console.error('Global error:', event.error);
        this.logError({
            type: 'global',
            message: event.message,
            filename: event.filename,
            lineno: event.lineno,
            colno: event.colno,
            error: event.error?.stack
        });
        
        this.showUserFriendlyError('An unexpected error occurred. Please try refreshing the page.');
    }

    handlePromiseRejection(event) {
        console.error('Unhandled promise rejection:', event.reason);
        this.logError({
            type: 'promise',
            reason: event.reason?.message || event.reason,
            stack: event.reason?.stack
        });
        
        this.showUserFriendlyError('An operation failed. Please try again.');
    }

    setupNetworkErrorHandling() {
        // Listen for online/offline status
        window.addEventListener('online', () => {
            this.dashboard.showNotification('You are back online', 'success');
        });
        
        window.addEventListener('offline', () => {
            this.dashboard.showNotification('You are offline. Working in offline mode.', 'warning');
        });
        
        // Intercept fetch errors
        const originalFetch = window.fetch;
        window.fetch = function(...args) {
            return originalFetch.apply(this, args)
                .catch(error => {
                    console.error('Fetch error:', error);
                    throw error;
                });
        };
    }

    setupStorageErrorHandling() {
        // Test localStorage availability
        this.testLocalStorage();
        
        // Handle storage events (changes from other tabs)
        window.addEventListener('storage', (event) => {
            if (event.key === this.dashboard.STORAGE_KEYS.TRANSACTIONS) {
                this.dashboard.showNotification(
                    'Data was updated in another tab. Refreshing...',
                    'info'
                );
                setTimeout(() => location.reload(), 1000);
            }
        });
    }

    testLocalStorage() {
        try {
            localStorage.setItem('test', 'test');
            localStorage.removeItem('test');
        } catch (error) {
            console.error('localStorage not available:', error);
            this.showUserFriendlyError(
                'localStorage is not available. Some features may not work properly.'
            );
        }
    }

    logError(errorData) {
        const errors = JSON.parse(localStorage.getItem('dashboard_errors') || '[]');
        errors.push({
            timestamp: new Date().toISOString(),
            ...errorData
        });
        
        // Keep only last 100 errors
        if (errors.length > 100) {
            errors.splice(0, errors.length - 100);
        }
        
        try {
            localStorage.setItem('dashboard_errors', JSON.stringify(errors));
        } catch (error) {
            console.error('Could not log error:', error);
        }
    }

    setupUserFeedback() {
        // Loading states
        this.setupLoadingStates();
        
        // Success/error messages
        this.setupMessageSystem();
        
        // Confirmation dialogs
        this.setupConfirmations();
    }

    setupLoadingStates() {
        // Add loading indicator for async operations
        const originalUpdateDashboard = this.dashboard.updateDashboard;
        this.dashboard.updateDashboard = async function() {
            this.showLoading();
            try {
                await originalUpdateDashboard.call(this);
            } finally {
                this.hideLoading();
            }
        };
    }

    setupMessageSystem() {
        // Extend notification system with more options
        const originalShowNotification = this.dashboard.showNotification;
        this.dashboard.showNotification = function(message, type = 'info', duration = 5000) {
            const notification = originalShowNotification.call(this, message, type);
            
            if (duration > 0) {
                setTimeout(() => {
                    notification.style.opacity = '0';
                    setTimeout(() => {
                        if (notification.parentNode) {
                            notification.parentNode.removeChild(notification);
                        }
                    }, 300);
                }, duration);
            }
            
            return notification;
        };
    }

    setupConfirmations() {
        // Confirm destructive actions
        const originalDeleteTransaction = this.dashboard.deleteTransaction;
        this.dashboard.deleteTransaction = function(id) {
            if (this.settings?.confirmDeletions !== false) {
                this.showConfirmationDialog(
                    'Delete Transaction',
                    'Are you sure you want to delete this transaction?',
                    () => originalDeleteTransaction.call(this, id)
                );
            } else {
                originalDeleteTransaction.call(this, id);
            }
        };
    }

    showConfirmationDialog(title, message, onConfirm) {
        const modal = document.createElement('div');
        modal.className = 'modal confirmation-modal';
        modal.innerHTML = \`
            <div class="modal-content">
                <div class="modal-header">
                    <h3>\${title}</h3>
                </div>
                <div class="modal-body">
                    <p>\${message}</p>
                </div>
                <div class="modal-footer">
                    <button class="btn-secondary" id="cancel-btn">Cancel</button>
                    <button class="btn-danger" id="confirm-btn">Confirm</button>
                </div>
            </div>
        \`;
        
        document.body.appendChild(modal);
        modal.style.display = 'flex';
        
        modal.querySelector('#cancel-btn').addEventListener('click', () => {
            document.body.removeChild(modal);
        });
        
        modal.querySelector('#confirm-btn').addEventListener('click', () => {
            onConfirm();
            document.body.removeChild(modal);
        });
        
        modal.addEventListener('click', (e) => {
            if (e.target === modal) {
                document.body.removeChild(modal);
            }
        });
        
        // Focus first button
        setTimeout(() => modal.querySelector('#cancel-btn').focus(), 100);
    }

    showUserFriendlyError(message) {
        const errorModal = document.createElement('div');
        errorModal.className = 'modal error-modal';
        errorModal.innerHTML = \`
            <div class="modal-content">
                <div class="modal-header error">
                    <h3><i class="fas fa-exclamation-triangle"></i> Error</h3>
                </div>
                <div class="modal-body">
                    <p>\${message}</p>
                    <div class="error-actions">
                        <button id="reload-btn" class="btn-primary">
                            <i class="fas fa-redo"></i> Reload Page
                        </button>
                        <button id="report-btn" class="btn-secondary">
                            <i class="fas fa-bug"></i> Report Issue
                        </button>
                    </div>
                </div>
            </div>
        \`;
        
        document.body.appendChild(errorModal);
        errorModal.style.display = 'flex';
        
        errorModal.querySelector('#reload-btn').addEventListener('click', () => {
            location.reload();
        });
        
        errorModal.querySelector('#report-btn').addEventListener('click', () => {
            this.reportIssue(message);
            document.body.removeChild(errorModal);
        });
        
        errorModal.addEventListener('click', (e) => {
            if (e.target === errorModal) {
                document.body.removeChild(errorModal);
            }
        });
    }

    reportIssue(message) {
        const errors = JSON.parse(localStorage.getItem('dashboard_errors') || '[]');
        const recentErrors = errors.slice(-5); // Last 5 errors
        
        const issueData = {
            userMessage: message,
            errors: recentErrors,
            userAgent: navigator.userAgent,
            timestamp: new Date().toISOString(),
            url: location.href,
            dataSize: this.dashboard.transactions.length
        };
        
        // In a real app, send this to your error tracking service
        console.log('Issue report:', issueData);
        
        this.dashboard.showNotification(
            'Issue reported. Thank you for your feedback!',
            'success'
        );
        
        // For demo purposes, show the report
        alert('Issue report (check console for details):\n' + 
              JSON.stringify(issueData, null, 2));
    }
}
</pre>
</div>

<h3>Deployment Preparation</h3>

<b>Build Process & Deployment:</b>
<div style="background-color: #e0e0e0; padding: 15px; margin: 10px 0; border-radius: 5px;">
<pre>
class DeploymentPreparer {
    constructor(dashboard) {
        this.dashboard = dashboard;
        this.prepareForDeployment();
    }

    prepareForDeployment() {
        this.minifyAssets();
        this.addMetaTags();
        this.createServiceWorker();
        this.generateManifest();
        this.setupAnalytics();
        this.createDocumentation();
    }

    minifyAssets() {
        // In a real build process, you would use tools like:
        // - Terser for JavaScript minification
        // - CSSNano for CSS minification
        // - HTMLMinifier for HTML minification
        
        console.log('Assets would be minified in production build');
        
        // Add cache busting for production
        this.addCacheBusting();
    }

    addCacheBusting() {
        // Add version query string to asset URLs
        const version = '1.0.0';
        const assets = document.querySelectorAll('link[rel="stylesheet"], script[src]');
        
        assets.forEach(asset => {
            const url = new URL(asset.href || asset.src, location.href);
            url.searchParams.set('v', version);
            
            if (asset.tagName === 'LINK') {
                asset.href = url.toString();
            } else {
                asset.src = url.toString();
            }
        });
    }

    addMetaTags() {
        const head = document.head;
        
        // Viewport (already should be there)
        if (!document.querySelector('meta[name="viewport"]')) {
            const viewport = document.createElement('meta');
            viewport.name = 'viewport';
            viewport.content = 'width=device-width, initial-scale=1.0, maximum-scale=1.0, user-scalable=no';
            head.appendChild(viewport);
        }
        
        // Charset
        if (!document.querySelector('meta[charset]')) {
            const charset = document.createElement('meta');
            charset.charset = 'UTF-8';
            head.appendChild(charset);
        }
        
        // Theme color
        const themeColor = document.createElement('meta');
        themeColor.name = 'theme-color';
        themeColor.content = '#4A90E2';
        head.appendChild(themeColor);
        
        // Description
        const description = document.createElement('meta');
        description.name = 'description';
        description.content = 'Personal Finance Dashboard - Track your income, expenses, and savings goals';
        head.appendChild(description);
        
        // Open Graph tags for social sharing
        const ogTags = [
            { property: 'og:title', content: 'Finance Dashboard' },
            { property: 'og:description', content: 'Track your personal finances' },
            { property: 'og:type', content: 'website' },
            { property: 'og:image', content: '/assets/og-image.png' },
            { property: 'og:url', content: location.href }
        ];
        
        ogTags.forEach(tag => {
            const meta = document.createElement('meta');
            meta.setAttribute('property', tag.property);
            meta.content = tag.content;
            head.appendChild(meta);
        });
    }

    createServiceWorker() {
        // Register service worker for offline functionality
        if ('serviceWorker' in navigator) {
            window.addEventListener('load', () => {
                navigator.serviceWorker.register('/sw.js')
                    .then(registration => {
                        console.log('ServiceWorker registration successful:', registration);
                    })
                    .catch(error => {
                        console.log('ServiceWorker registration failed:', error);
                    });
            });
        }
        
        // Create service worker file content
        const swContent = \`
// Service Worker for Finance Dashboard
const CACHE_NAME = 'finance-dashboard-v1';
const urlsToCache = [
    '/',
    '/index.html',
    '/dashboard.css',
    '/dashboard.js',
    '/chart-utils.js',
    'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css',
    'https://fonts.googleapis.com/css2?family=Segoe+UI:wght@400;500;600&display=swap'
];

self.addEventListener('install', event => {
    event.waitUntil(
        caches.open(CACHE_NAME)
            .then(cache => {
                return cache.addAll(urlsToCache);
            })
    );
});

self.addEventListener('fetch', event => {
    event.respondWith(
        caches.match(event.request)
            .then(response => {
                if (response) {
                    return response;
                }
                return fetch(event.request);
            })
    );
});

self.addEventListener('activate', event => {
    const cacheWhitelist = [CACHE_NAME];
    event.waitUntil(
        caches.keys().then(cacheNames => {
            return Promise.all(
                cacheNames.map(cacheName => {
                    if (cacheWhitelist.indexOf(cacheName) === -1) {
                        return caches.delete(cacheName);
                    }
                })
            );
        })
    );
});
\`;
        
        // In a real app, you would write this to a file
        console.log('Service Worker would be created:', swContent);
    }

    generateManifest() {
        const manifest = {
            "name": "Finance Dashboard",
            "short_name": "FinanceDash",
            "description": "Personal finance tracking application",
            "start_url": "/",
            "display": "standalone",
            "background_color": "#ffffff",
            "theme_color": "#4A90E2",
            "icons": [
                {
                    "src": "/icons/icon-72x72.png",
                    "sizes": "72x72",
                    "type": "image/png"
                },
                {
                    "src": "/icons/icon-96x96.png",
                    "sizes": "96x96",
                    "type": "image/png"
                },
                {
                    "src": "/icons/icon-128x128.png",
                    "sizes": "128x128",
                    "type": "image/png"
                },
                {
                    "src": "/icons/icon-144x144.png",
                    "sizes": "144x144",
                    "type": "image/png"
                },
                {
                    "src": "/icons/icon-152x152.png",
                    "sizes": "152x152",
                    "type": "image/png"
                },
                {
                    "src": "/icons/icon-192x192.png",
                    "sizes": "192x192",
                    "type": "image/png"
                },
                {
                    "src": "/icons/icon-384x384.png",
                    "sizes": "384x384",
                    "type": "image/png"
                },
                {
                    "src": "/icons/icon-512x512.png",
                    "sizes": "512x512",
                    "type": "image/png"
                }
            ]
        };
        
        // Create manifest file
        const manifestLink = document.createElement('link');
        manifestLink.rel = 'manifest';
        manifestLink.href = '/manifest.json';
        document.head.appendChild(manifestLink);
        
        console.log('Manifest would be created:', JSON.stringify(manifest, null, 2));
    }

    setupAnalytics() {
        // Add Google Analytics (example)
        const analyticsScript = document.createElement('script');
        analyticsScript.async = true;
        analyticsScript.src = 'https://www.googletagmanager.com/gtag/js?id=UA-XXXXX-Y';
        document.head.appendChild(analyticsScript);
        
        const configScript = document.createElement('script');
        configScript.textContent = \`
            window.dataLayer = window.dataLayer || [];
            function gtag(){dataLayer.push(arguments);}
            gtag('js', new Date());
            gtag('config', 'UA-XXXXX-Y');
        \`;
        document.head.appendChild(configScript);
        
        // Custom event tracking for dashboard
        this.setupCustomAnalytics();
    }

    setupCustomAnalytics() {
        // Track dashboard usage
        const trackEvent = (category, action, label) => {
            if (typeof gtag !== 'undefined') {
                gtag('event', action, {
                    'event_category': category,
                    'event_label': label
                });
            }
        };
        
        // Track transactions
        const originalAddTransaction = this.dashboard.addTransaction;
        this.dashboard.addTransaction = function(transaction) {
            const result = originalAddTransaction.call(this, transaction);
            if (result) {
                trackEvent('transactions', 'add', transaction.type);
            }
            return result;
        };
        
        // Track exports
        const originalExportData = this.dashboard.exportData;
        this.dashboard.exportData = function(format) {
            originalExportData.call(this, format);
            trackEvent('data', 'export', format);
        };
        
        // Track page views
        trackEvent('navigation', 'pageview', 'dashboard');
    }

    createDocumentation() {
        // Create help modal
        const helpModal = document.createElement('div');
        helpModal.id = 'help-modal';
        helpModal.className = 'modal';
        helpModal.innerHTML = \`
            <div class="modal-content">
                <div class="modal-header">
                    <h3>Finance Dashboard Help</h3>
                    <button class="close-modal">&times;</button>
                </div>
                <div class="modal-body">
                    <div class="help-section">
                        <h4>Getting Started</h4>
                        <p>Add your first transaction by clicking the "Add Transaction" button.</p>
                        <p>Set your monthly budget in the settings menu.</p>
                    </div>
                    
                    <div class="help-section">
                        <h4>Keyboard Shortcuts</h4>
                        <ul>
                            <li><kbd>Ctrl/Cmd + N</kbd>: New Transaction</li>
                            <li><kbd>Ctrl/Cmd + F</kbd>: Focus Search</li>
                            <li><kbd>Ctrl/Cmd + S</kbd>: Save Data</li>
                            <li><kbd>Escape</kbd>: Close Modals</li>
                        </ul>
                    </div>
                    
                    <div class="help-section">
                        <h4>Tips & Tricks</h4>
                        <ul>
                            <li>Use categories to organize your spending</li>
                            <li>Set savings goals to track progress</li>
                            <li>Export data regularly for backup</li>
                            <li>Use filters to analyze specific time periods</li>
                        </ul>
                    </div>
                </div>
                <div class="modal-footer">
                    <button class="btn-primary" id="close-help">Got it!</button>
                </div>
            </div>
        \`;
        
        document.body.appendChild(helpModal);
        
        // Add help button to interface
        const helpButton = document.createElement('button');
        helpButton.id = 'help-button';
        helpButton.className = 'accessibility-btn';
        helpButton.innerHTML = '<i class="fas fa-question-circle"></i><span class="sr-only">Help</span>';
        helpButton.addEventListener('click', () => {
            helpModal.style.display = 'flex';
        });
        
        document.querySelector('.top-bar').appendChild(helpButton);
        
        // Close help modal
        helpModal.querySelector('.close-modal').addEventListener('click', () => {
            helpModal.style.display = 'none';
        });
        
        helpModal.querySelector('#close-help').addEventListener('click', () => {
            helpModal.style.display = 'none';
        });
        
        helpModal.addEventListener('click', (e) => {
            if (e.target === helpModal) {
                helpModal.style.display = 'none';
            }
        });
        
        // Create README file content
        const readmeContent = \`
